css:
@-moz-document domain("arstechnica.com") {
#masthead,.international-banner,#page-footer,#article-footer-wrap,#article-sidebar,#archive-head,
article.standalone .post-meta ul.share-buttons,aside.sidebar.story-sidebar
{
	display:none;
}
#container {
	padding-top: 0 !important;
    margin: 0 auto;
    width: 35rem !important;
}
#content, #container {
	width: 35rem;
}
article.standalone {
	border: none;
}


/* Schrift unf Farbe fuer alles */
html,body,article,div,h1,h2,h3,h4,h5,p,ul,
ol,dl,span,em,a,form,#article,b,
ul,ol,dl,pre,li,dd,dt,table,tr,td,th,#content
{
    font-family: 'Source Sans Pro',Arial,sans-serif !important;
    color: rgba(240, 240, 240, 1) !important;
    background-color: rgba(20, 20, 20, 1) !important;
    
    line-height: 1.4;
}
/* haessliche weisse borders um bilder weg */
.TexticonThumbPicture,
.TexticonUrlPicture {
    border: none !important;
}

/* etwas bessere lesbarkeit */
html, body, form {
	font-size: 22px;
}
h1 {
    font-size: 1.6rem !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
    font-weight: 800;
    line-height: 1.2;
}
h2 {
    font-size: 1.4rem !important;
    font-weight: 700;
        margin-top: .7rem;
    margin-bottom: .1rem;
    line-height: 1.2;
}
h3 {
    font-size: 1.2rem !important;
    font-weight: 600;
    line-height: 1.2 !important;
        margin-top: .7rem;
    margin-bottom: .1rem;
}
h4 {
    font-size: 1.1rem !important;
        margin-top: .7rem;
    margin-bottom: .1rem;
}
h5 {
    font-size: 1rem !important;
        margin-top: .7rem;
    margin-bottom: .1rem;
}
p, a,ul,li,dl,dd,dt,ol,div,pre,blockquote,.article-content.clearfix {
    font-size: 1rem !important;
    font-weight: 300 !important;
    margin-top: .1rem;
    margin-bottom: .7rem;
    line-height: 1.3;
}
a:active,
a:hover,
a:link,
a:visited {
    color: rgba(240,240,240,1);
    text-decoration: underline;
}
figure.image.right {
	float:none;
    height: 15rem;
    overflow: hidden;
}
}
