css:
@namespace url(http://www.w3.org/1999/xhtml);
  
@-moz-document regexp('http://www\\.lesnumeriques.com/')  {
  
  /* FIL D'ACTUALITÉ */
  
  .card.mod-card-news:nth-child(n){
    border: 0;
    width: 49.5%;
    height: 70px;
  }
  
  .cards-wrapper > a:hover {
	  box-shadow:0 0 3px #a1a1a1;
  }
  
  .card.mod-card-news:nth-child(n) .card-transparent {
    /*display: none;*/
    transform: none;
    bottom: 0;
    top:-1px;
    left: 0;
    height: 100%;
    -webkit-transform: none;
    -moz-transform: none;
    -ms-transform: none;
    -o-transform: none;
    background-color: rgba(255, 255, 255, 0);
    opacity: 1;
    filter: none;
    backface-visibility: hidden;
    width: 100%;
    z-index: 0;
    
  }
  
  .card.mod-card-news:nth-child(odd){
    margin-right: 0.5%;
    margin-bottom: 15px;
   
  }
  
  .card.mod-card-news:nth-child(even){
    margin-left: 0.5%;
    margin-top: 15px;
  }
  
  .card.mod-card-news img {
    width: initial;
    margin-top: 2.5px;
    margin-left: 2.5px;
    height: 65px;
  }
 
  .card .card-content {
    padding: 0 35px 0 120px;
  }
  
  
  /* FLÈCHE DÉFILEMENT DES ACTUALITÉS */
  
  .block-more .mod-arrow {
    display: inline-block;
    margin-left: 15px;
    margin-right: 15px;
    pointer-events: none;
    color: #a1a1a1;
  }
  
  .block-more .mod-arrow-top.mod-arrow-show{
    pointer-events:inherit;
    color: #73b914;
  }
  
  .block-more .mod-arrow-bottom.mod-arrow-show{
    pointer-events:inherit;
    color: #73b914;
  }
  
  
  /* CAROUSEL*/
  
  .carousel {
    height: 300px;
  }
  
  /*
  .carousel-wrapper {
    height: 100%;
  }
  
  .carousel-item:nth-child(n){
    top:0;
  }
  
  .carousel-item-img{
    height: 100%;
    width: 100%;
  }
  
  .carousel-item:nth-child(5n+1) {
    height: 100%;
    width: 34.6%;
  }
  
  .carousel-item:nth-child(5n), .carousel-item:nth-child(5n+2), .carousel-item:nth-child(5n-1), .carousel-item:nth-child(5n-2) {
    height: 50%;
    top:50%;
  }
  */
}

@-moz-document regexp('http://www\\.lesnumeriques.com/.*')  {
  
  #pub-banner {
    height: 93px;
    overflow: hidden;
  }
  
}
