css:
@namespace url(http://www.w3.org/1999/xhtml);
  
@-moz-document regexp('http://www\\.lesnumeriques.com/actualites'), regexp('http://www\\.lesnumeriques.com/actualites/.*')  {
  
    
  /* BARRE DE NAVIGATION*/
  
  .list-header {
    padding: 10px;
  }
  
  .list-header nav {
    padding-bottom: 10px;
  }
  
  .generic-social-share {
    top: 4px;
  }
  
  .breadcrumbs-v4 {
    margin-bottom: 0px;
  }
  
  .list-filters {
    padding: 0 10px 10px;
  }
  
  .list-filters-content {
    padding: 5px;
  }
  
  .list-filters a {
    padding: 3px 5px;
    font-size: 14px;
  }
  
  /* LISTE DES ACTUALITÉs */
  
  .list-content {
    margin-top: 10px;
  }
  
  .list-content > li {
    padding: 5px 20px;
    margin-bottom: 6px;
  }
  
  .list-content .text {
    font-weight: normal;
  }
  
  /* NEWS */
 
  .list-content .news {
    height: 90px;
    min-height: 97px;
  }
  
  .list-content .news h2 {
    font-size: 19px;
  }
  
  .list-content .news .subtitle {
    font-size: 14px;
    margin: 3px 0 3px 0px;
  }
  
  .list-content .item-date {
    float: right;
    margin-top: -20px;
  }
  
  .list-content .news .text {
    font-size: 14px;
  }
  
  .list-content .news .image {
    height: 95px;
    margin-right: 10px;
    width: 170px;
  }
  
  /* TESTS */
  
  .list-content .test {
    min-height: 97px;
    display: flex;
  }
  
  .list-content .test .image {
    order: 1;
    height: 95px;
    margin-right: 10px;
    min-width: 170px;
    max-width: 170px;
  }
  
  .list-content .test img {
    display: block;
    height: 100%;
    margin-left: auto;
    margin-right: auto;
  }

  .list-content .test a{
    order: 2;
  }
  
  .list-content .test h2{
    width: 100%;
    font-size: 19px;
    padding: 0;
  }
  
  .list-content .test .subtitle{
    padding: 0;
  }
  
  .list-content .news .item-date {
    float: right;
    margin-top: -20px;
  }
  
  .list-content .test .notes{
    order: 3;
    min-width: 180px;
    max-width: 180px;
    float:right;
    margin-left: auto;
    padding-left: 5px;
  }
  
  .list-content .test .our-stars{
    padding-bottom: 6px;
    margin-bottom: 6px;
  }
  
  .list-content .test .users-note{
    border-bottom: none;
    margin-bottom: 0px;
    padding-bottom: 0px;
  }
  
  .list-content .test a.see-more, .list-content .test .social-v4, .prices-v4{
    display: none;
  }
  
  /* ARTICLES  */
  
  .list-content .article .stretch {
    height: 130px;
  }
  
  .list-content .article .subtype {
    font-weight: 100;
    margin-top: 1%;
    font-size: 15px;
  }
  
  .list-content .article h2 {
    font-size: 19px;
    line-height: 25px;
  }
  
  .list-content .article h3 {
    font-size: 15px;
  }
  
  .list-content .article .subtype, .list-content .article h2, .list-content .article h3 {
    opacity: .8;
  }
  
  .list-content .article .item-date {
  margin-right: 10px;
  }
  
  .list-content .article .text {
    font-size: 14px;
  }
  
}

@-moz-document regexp('http://www\\.lesnumeriques.com/.*')  {
  
  #pub-banner {
    height: 93px;
    overflow: hidden;
  }
  
}
