@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sheltermanager.com") {

body {
    background: #000000 url('https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcQBCM1cRH7YXdnA3LHHvaNcKlrFGJacLYM3nXuGkUmMCMUBMkes') !important;
    background-color: #000000 !important;
    color: #888888 !important;
    border-color: #808080 !important;
    background-repeat: repeat-y;
    background-attachment: fixed !important;
    background-position: center !important;
}
div,
h3,
.morelink,
.tabmenu li a {
    background-color: transparent !important;
    color: #ffffff !important;
    border-color: #ffffff !important;
}
.asm-menu-list {
    list-style-type: none;
    padding: 5px;
    background-color: #090469 !important;
    margin-left: 0px;
}
.asm-shelterview-animal {
    display: inline-block;
    text-align: center;
    border: 1px solid #ccc;
    margin-top: 3px;
    background-color: transparent !important;
    padding: 2px;
}
.ui-widget-content {
    border: 1px solid #000000;
    background: rgba(17, 17, 17, 0) 50% top repeat-x;
    color: #d9d9d9;
}
.ui-state-highlight,
.ui-widget-content .ui-state-highlight,
.ui-widget-header .ui-state-highlight {
    border: 1px solid #5175C5;
    background: #5175C5 50% 50% repeat;
    color: #ffffff;
}
.asm-thumbnail {
    vertical-align: middle;
    border: 1px solid #000;
    background-color: #ffffff;
    padding: 6px;
    margin: 5px;
    max-width: 100px;
    max-height: 100px;
    border-radius: 3px;
    box-shadow: 3px 3px 6px #32cd32;
}
.asm-thumbnail-male {
    background-color: #CA6AB1;
    box-shadow: 3px 3px 6px #CA6AB1;
}
.asm-thumbnail-female {
    background-color: #348AB1;
    box-shadow: 3px 3px 6px #348AB1;
}
.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default {
    border: 1px solid #007EFF;
    background: #007EFF 50% 50% repeat;
    font-weight: bold;
    color: #ffffff;
}
.asm-quicklinks a {
    display: inline-block;
    white-space: nowrap;
    border-radius: 3px;
    box-shadow: 0 1px 3px #DDD;
    border: 1px solid #555;
    padding: 5px;
    margin: 1px;
    font-weight: bold;
}
div,
h3,
.morelink,
.tabmenu li a {
    background-color: transparent !important;
    color: #00FF14 !important;
    border-color: #ffffff !important;
}
.ui-widget-content a {
    color: #FFFFFF;
    font-weight: bold;
}
.asm-menu-category {
    padding: 4px;
    margin: 5px;
    font-weight: bold;
    border-bottom: 1px solid #00DCFF;
}
.ui-widget-overlay {
    background: #6262ff;
} 
}/* end */
