css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
 * Dark Twitter (2016) theme
 * by Brennced
 * https://brennced.github.io/ | https://userstyles.org/users/331381
 */

@-moz-document domain("twitter.com") {
  body,
  .stream-container.conversations-enabled,
  .dropdown-menu,
  .modal-content,
  .global-nav .search-input,
  .WhoToFollow,
  .ProfilePage {
    background: #292f33 !important;
    color: #f5f8fa !important;
  }
  .fullname,
  .tweet-text,
  .ProfileHeaderCard-locationText, .ProfileHeaderCard-joinDateText, .ProfileHeaderCard-vineProfileText, .ProfileHeaderCard-birthdateText,
  .ProfileCard a, .ProfileCard a:hover, .ProfileCard a:focus, .ProfileCard a:active, .ProfileCard .pretty-link, .ProfileCard .pretty-link:hover, .ProfileCard .pretty-link:focus, .ProfileCard .pretty-link:active,
  .ProfileNav-item.is-active .ProfileNav-value, .ProfileNav-item.is-active:hover .ProfileNav-value,
  .ProfileHeading-content,
  .ProfileHeading-toggleItem.is-active, .ProfileHeading-toggleItem.is-active:hover, .ProfileHeading-toggleItem.is-active:focus,
  .QuoteTweet-fullname {
    color: #f5f8fa !important;
  }
  .tweet,
  .stream-item,
  .modal-header,
  .ProfileHeading-content,
  .new-tweets-bar,
  .PermalinkOverlay-spinnerContainer,
  .Gallery-content {
    background: #292f33 !important;
  }
  
  .DashboardProfileCard-content,
  .Trends,
  .DashboardProfileCard,
  .module .flex-module,
  .DMActivity-header,
  .content-header .header-inner, .content-no-header .no-header-inner,
  .module .list-link {
    background: #292f33 !important;
    color: #f5f8fa !important;
    border-color: #1e2225 !important;
  }
  .DashboardProfileCard-bg,
  .tweet,
  .stream-container,
  .stream,
  .stream-item,
  .original-tweet-container,
  .TwitterCard-container,
  .global-nav .search-input,
  .stream-end,
  .ProfileCanopy-navBar,
  .ProfileHeading-content,
  .WhoToFollow,
  .dropdown-divider,
  .content-header,
  .stream-item-activity-notification,
  .new-tweets-bar,
  .QuoteTweet,
  .stream-end-item, .stream-end, .stream-loading, .stream-placeholder {
    border-color: #1e2225 !important;
  }
  
  .TwitterCard-container--clickable:hover {
    background: #32393e !important;
  }
  .dropdown-caret,
  .dropdown-caret * {
    border-color: #292f33 !important;
  }
  .modal-content,
  .DMActivity-header,
  .dm_dialog-dialog {
    border: 1px solid #1e2225;
  }
  .global-nav-inner,
  .stream-end-inner,
  .ProfileCard,
  .ProfileCanopy-navBar,
  .ProfileNav-item--userActions,
  .btn:not(.tweet-btn),
  .ThreadedConversation {
    background: #32393e !important;
    color: #f5f8fa !important;
  }
  .typeahead strong {
    color: #eee;
  }
  input, textarea, div[contenteditable], .t1-select {
    background: transparent !important;
  }
}
