css:
@-moz-document domain("spiegel.de") {
iframe, #header, .column-small, .social-bar.clearfix, div[id^='content_ad'], #footer_ad, #subchannel_teaser, .top-anchor.link-gradient-button-box, #footer, #article-overscroll-area-outer, div[id^='adform-adbox-'], #footer_ad, .module-box.function-box, .spVeeseo, .module-box, #spSponsoredBy {
    display: none !important;
}

#wrapper {
    position: static;
    max-width: 25rem;
    min-width: 200px;
    margin: 0 auto;
}

img, .js-module-box-image.box-position {
    max-width: 25rem;
}

#wrapper-shadow, #wrapper-content, .column-both, .grid-article .column-wide, #content-main, .grid-article, .grid_blog, .grid-channel, .grid-homepage, .grid-search, .grid-seite2, .grid-topic, .grid-article .column-both, .grid-channel .column-both, .grid-homepage .column-both, .grid-search .column-both, .grid-topic .column-both {
    float: none;
    width: 100%;
    display: block;
    max-width: 100%;
    position: static;
    padding: 0;
    margin: 0;
}

#wrapper-content {
    padding-bottom: 800rem;
    border: none;
    margin: 0;
}
/* Schrift unf Farbe fuer alles */

html, body, article, div, h1, h2, h3, h4, h5, p, ul, ol, dl, span, em, a, form, #article, b, ul, ol, dl, pre, li, dd, dt, table, tr, td, th, #content, strong {
    font-family: 'Source Sans Pro', Arial, sans-serif !important;
    color: rgba(240, 240, 240, 1) !important;
    background-color: rgba(20, 20, 20, 1) !important;
    background-image: none;
    line-height: 1.4 !important;
}

.author-info dl {
    overflow: visible;
}
/* etwas bessere lesbarkeit */

html, body, form {
    font-size: 20px;
}

h1 {
    font-size: 1.6rem !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
    font-weight: 700;
    line-height: 1.2;
}

h2 {
    font-size: 1.4rem !important;
    font-weight: 600;
    margin-top: .7rem;
    margin-bottom: .1rem;
    line-height: 1.2;
}

h3 {
    font-size: 1.2rem !important;
    font-weight: 500;
    line-height: 1.2 !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
}

h4 {
    font-size: 1.1rem !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
}

h5 {
    font-size: 1rem !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
}

p, a, ul, li, dl, dd, dt, ol, div, pre, blockquote, .article-content.clearfix {
    font-size: 1rem !important;
    font-weight: 300 !important;
    margin-top: .1rem;
    margin-bottom: .7rem;
    line-height: 1.5 !important;
}

strong {
    font-weight: 500;
}

a:active, a:hover, a:link, a:visited {
    color: rgba(240, 240, 240, 1);
    text-decoration: underline;
}
}
