css:
@-moz-document url-prefix("http://www.eldarya.fr/"), regexp("http://(fa1|fa2)\\.eldarya\\.fr/.*") {


/*bank*/

#bank > p {
    margin: 15px 0 20px 65px;
    color: #34386f
}

/*classement*/
.ranking-winning-guard {
    color: #ffffff;
    font-weight: bold;
    text-shadow: 0px 0px 2px #feac11, 0px 0px 3px #feac11 !important;
}

#ranking-top-players .ranking-top-players-number {
    box-shadow: rgb(51, 55, 110) 0px 0px 2px;
    background-color: rgb(51, 55, 110);
    border-width: 1px;
    border-style: solid;
    border-color: rgb(34, 147, 251);
}

#ranking-requirements div.on {
    color: #ffffff;
    font-weight: bold;
    text-shadow: 0px 0px 2px #feac11, 0px 0px 3px #feac11 !important;
}

/*__________GENERAL__________*/

#container {
  position: relative;
  font-family: "Banda Regular", sans-serif;
  }
  #container .page-main-container {
    background-color: rgba(255, 255, 255, 0.60);;
    border-radius: 8px;
    padding: 20px;
    min-height: 400px;
    position: relative;
    box-shadow: inset 1px 1px 3px 1px rgba(47, 62, 113, 0);
                    }
    #container .page-main-container:after {
      content: '';
      border-radius: 8px;
      background-position: 0 0, 100% 0, 0 0, 0 100%;
      background-repeat: no-repeat;
      background-size: 4px 0%, 4px 0%, 0% 4px, 0% 4px;
      box-shadow: 1px 2px 3px 0 rgba(47, 62, 113, 0);
}

#main-menu ul {
    text-align: center;
    white-space: nowrap;
    background-color: rgba(92, 157, 248, 0);
    box-shadow: inset 1px 1px 3px 1px  rgba(92, 157, 248, 0);
}

#main-menu ul:after {
      background-size: 4px 0%, 4px 0%, 0% 4px, 0% 4px;
      box-shadow: 1px 2px 3px 0 rgba(47, 62, 113, 0);
}

/*bulle info*/
.tooltip-content {
    position: fixed;
    font-family: "Banda", serif;
    color: white;
    background-color: #34386F;
    border: 1px solid #00E4FF;
    padding: 10px;
    border-radius: 6px;
}


.tooltip-content {
    position: fixed;
    font-family: "Banda", serif;
    color: white;
    background-color: #34386F;
    border: 1px solid #00E4FF;
    padding: 10px;
    border-radius: 6px;
    text-shadow: 0px 0px 1px #fff6d3 !important ;
}




.flavr-button.gold, .button.button.button-gold {
    background-color: #F9CE4D;
    border-color: #F9CE4D;
}

.flavr-button.gold, .button.button.button-gold:hover, .flavr-button.gold, .button.button.button-gold.selected {
    background-color: white !important;
    color: #F9CE4D !important;
}

.flavr-button.purple, .button.button.button-purple:hover, .flavr-button.purple, .button.button.button-purple.selected {
    background-color: white !important;
    color: #102F54 !important;
}


/*__________PAGE ACCUEIL__________*/

#header-login-form input {
    color: #102F54;
    font-family: Banda , sans-serif;
    margin: 0 3px;
}

/*__________PAGE PRINCIPALE__________*/

/*fond*/

body {
    background: #4da8fd url("/*[[fondjour]]*/") fixed top center;
    background-size: cover;
    overflow-y: scroll;
}


/*background night*/
body.night {
    background: #4da8fd url("/*[[fondnuit]]*/") fixed top center;
    background-size: cover;
}



/*top*/

#header #header-top {
    position: absolute;
    top: 0;
