css:
@-moz-document domain("eldarya.fr"), domain("fa1.eldarya.com"), domain("www.eldarya.ru"), domain("eldarya.com"), domain("eldarya.com.br"), domain("eldarya.de"), domain("eldarya.es"), domain("eldarya.fi"), domain("eldarya.hu"), domain("eldarya.it"), domain("eldarya.pl") {
body {
background: url("https://s33.postimg.cc/dbqqtm927/image.png") fixed top center;;
    background-size: cover;
    overflow-y: scroll;
}
#header #header-top {
    position: absolute;
    top: 0;
    background-color: rgb(41, 51, 141);
    height: 39px;
    width: 100%;
}
#container .page-main-container {
    background-color: rgba(223, 236, 255, 0.33);
    border-radius: 10px;
    padding: 20px;
    min-height: 400px;
}
    #index-news .date {
    background-color: #4F3F8C;
}
#index-news {
    background-color: rgb(79, 63, 140);
    border-color: rgba(107, 117, 255, 0.63);
}#news-list .news + .news {
    border-top: 2px solid rgba(79, 63, 140, 0.42);
}
#news-list {
    background: linear-gradient(0deg, rgb(145, 191, 239) 0%, rgb(224, 232, 255) 100%);
}
.index-carousel {
    position: relative;
    height: 231px;
    width: 522px;
    border-radius: 10px;
    border: 2px solid rgb(27, 28, 64);
    background-color: rgba(168, 207, 239, 0.77);
    overflow: hidden;
}
.index-main-container .index-carousel-description {
    color: rgb(0, 0, 0);
}
#player-headerPortrait {
    width: 112px;
    height: 113px;
    top: 13px;
    right: 12px;
}
#player-infos .player-name {
    color: #fff;
    font-weight: 600;
    font-family: "Banda Regular", sans-serif;
    position: relative;
    height: 32px;
    line-height: 32px;
    display: inline-block;
    font-size: 16px;
    margin: 25px 0;
    border: solid 2px #CFE0FF;
    background-color: #343B9C;
}
#index-player {
    position: absolute;
    margin: 0 auto;
    width: 420px;
    height: 600px;
    right: 20px;
    top: 20px;
    border: solid 2px rgba(73, 0, 175, 0.45);
    border-radius: 10px;
    overflow: hidden;
    background-color: #fff;
}
#pet-info-luck-level {
    display: inline-block;
    width: 30px;
    min-height: 30px;
    padding-left: 35px;
    margin-left: 6px;
    background: url("http://www.eldarya.fr/static/img/pet/luck_full.png") 0% 50% no-repeat;
}
#pet-info-food, #pet-info-levels {
    color: rgb(52, 56, 111);
    font-weight: bold;
    background: rgba(225, 228, 241, 0.76);
    border-radius: 10px;
    margin: 15px 0px;
    padding: 15px;
    overflow: hidden;
}
#pet-info-food img {
    border-width: 2px;
    border-style: solid;
    border-color: rgb(100, 57, 144);
    background: rgb(230, 222, 254);
}
.episode-container .text {
    z-index: 100;
    text-align: justify;
    position: absolute;
    font-family: Suplexmentary;
    line-height: 1em;
    font-size: 20px;
    overflow: hidden;
    background: linear-gradient(rgba(126, 115, 181, 0.85098) 0%, rgba(34, 29, 109, 0.85098) 89%, rgba(50, 25, 82, 0.298039) 100%);
}
#right-profile-tab {
    float: left;
    width: 369px;
    background-color: rgba(221, 232, 249, 0.72);
    position: relative;
    min-height: 111px;
    padding: 15px 15px 30px;
    margin: 10px 0px 0px 10px;
    border-radius: 10px;
    overflow: auto;
}
#library-container li {
    background-color: rgba(225, 236, 255, 0.76);
}
.mall-category, .mall-product, .mall-variation {
    width: 106px;
    height: 124px;
    float: left;
    cursor: pointer;
    position: relative;
    box-shadow: black 0px 0px 5px;
    background: rgba(233, 241, 255, 0.91);
    margin: 5px;
    padding: 7px;
    border-radius: 3px;
}
#mall-productDetail-info {
    width: 626px;
    clear: both;
    height: 85px;
    position: relative;
    box-shadow: black 0px 0px 5px;
    text-align: center;
    background: rgba(225, 236, 253, 0.68);
    margin: 7px 5px;
    padding: 7px;
    border-radius: 3px;
}
.guard-gem.guard-4 {
    background-image: url("http://img15.hostingpics.net/pics/752856markerombre02.png");
}

.guard-gem.guard-2 {
    background-image: url("http://img15.hostingpics.net/pics/864579markerobsidienne02.png");
}


.guard-gem.guard-3 {
    background-image: url("http://img15.hostingpics.net/pics/519141markerabsynthe02.png");
}


.guard-gem.guard-1 {
    background-image: url("http://img15.hostingpics.net/pics/133836markeretincelant02.png");
}
#index-ranking {
    display: inline-block;
    height: 344px;
    width: 280px;
    background-color: rgba(48, 52, 148, 0.85);
    border-radius: 10px;
    border: 2px solid #aeb4ff;
}
#ranking-list .ranking-first {
    height: 101px;
    color: rgb(255, 233, 206);
}
#ranking-list {
    background: linear-gradient(0deg, rgba(69, 67, 150, 0.02) 0%, #667dbd 76%, rgba(139, 143, 202, 0.52) 96%);
}
.marketplace-itemsForSale-item {
    height: 105px;
    float: left;
    cursor: pointer;
    position: relative;
    box-shadow: black 0px 0px 5px;
    background: #E4E4F3;
    margin: 7px;
    padding: 7px;
    border-radius: 3px;
}
img.abstract-icon {
    background-color: #E4E4F3;
}
.inventory-item {
    width: 80px;
    height: 80px;
    background: #EBEBFF;
    margin: 5px;
    padding: 2px;
    float: left;
    position: relative;
    border-radius: 3px;
    box-shadow: 0px 0px 5px black;
}
.appearance-category, .appearance-item {
    background: rgba(229, 229, 255, 0.5);
    margin: 5px;
    float: left;
    cursor: pointer;
    position: relative;
    border-radius: 3px;
    box-shadow: 0px 0px 5px black;
}
#selectedCategory {
    border: 1px solid rgba(0, 0, 255, 0.61);
    border-bottom-width: 2px;
    background: #EAEAFF;
    position: absolute;
    border-radius: 4px;
    cursor: pointer;
    width: 100%;
}
body.night {
    background: #4da8fd
url("https://s33.postimg.cc/liiqkvtwf/image.png") fixed top center;
    background-size: cover;
}
}
