css:
@-moz-document domain(gitweb.dmg.zone) {


/*************** GitLab Theme By DMG IT *******************/
/********* Custom Themes and Branding Available ***********/

body {
    background: #404040 !important;
    color: #fff !important;
}

header.navbar-gitlab {
    background-color: #303030 !important;
    box-shadow: 0px 0px 4px 1px #000;
    border-bottom: none !important;
}
header .header-content .title a {
    color: #fff !important;
}
body.ui_charcoal .page-with-sidebar .header-logo {
    background-color: #303030 !important;
    border-color: #303030 !important;
}
body.ui_charcoal .page-with-sidebar .sidebar-wrapper {
    background: #202020 !important;
}
body.ui_charcoal .page-with-sidebar .nav-sidebar li.active a {
    background: #4185ef !important;
}
ul.projects-list, ul.content-list {
    background: #202020 !Important;
    color: #fff !important;
    padding: 10px !important;
}
ul.content-list>li a {
    color: #fff !important;
}
.content-wrapper .container-fluid {
    background: transparent !important;
}
.top-area {
    border-bottom: none !important;
    padding: 10px !important;
}
.nav-links li.active a {
    color: #fff !important;
}
.event-item a {
    color: #fff !important;
}
.event-item .event-title {
    color: #959595 !important;
}
.event-item {
    color: #959595 !important;
}
.gray-content-block {
    background-color: #202020 !important;
    border-bottom: 1px solid #4185ef !important;
    border-top: 1px solid #4185ef !important;
}
.nothing-here-block {
    color: #4185ef !important;
}
a.btn-sm {
    color: #000 !important;
}
.fa {
    color: #000 !important;
}
ul.nav-sidebar .fa {
    box-shadow: inset 0px 0px 34px 8px #fff !important;
    border-radius: 2px;
    padding: 7px;
}
tbody .fa {
    box-shadow: inset 0px 0px 34px 8px #fff !important;
    border-radius: 2px;
    padding: 7px;
    margin-right: 10px;
    width: 30px;
}
.nav-sidebar li a i {
    width: 30px;
}
.cover-block {
    background: #404040 !important;
    border-bottom: 1px solid #4185ef !important;
}
.cover-block .cover-title h1 {
    color: #4185ef !important;
}
h1, h2, h3, h4, h5, h6 {
    color: #fff;
}
.git-empty .light-well {
    color: #4185ef !important;
}
.light-well {
    background-color: #000 !important;
}
.git-empty h5 {
    color: #fff !important;
}
.gray-content-block {
    color: #fff !important;
}
.detail-page-description .title {
    color: #fff !important;
}
.issuable-sidebar a {
    color: #4185ef;
}
.detail-page-header .identifier {
    color: #FFFFFF;
}
.issuable-meta {
    color: #cccccc;
}
.detail-page-header .author {
    color: #4185ef !important;
}
.wiki p {
    color: #BBBBBB;
}
.form-actions {
    background: transparent !important;
    border-top: none !important;
}
.project-stats .nav > li > a {
    color: #fff !important;
}
.project-stats li.missing a {
    color: #4185ef !important;
}
.project-last-commit .commit-row-message {
    color: #4185ef;
}
.project-last-commit .commit_short_id {
    color: #fff !important;
}
.container-fluid .ci-status.ci-skipped {
    color: #fff;
    border-color: #000;
}
.wiki h2, .wiki p {
    color: #fff;
}
.content-block {
    padding: 20px;
    margin-top: 20px;
    margin-bottom: 20px;
    border-bottom: none !important;
}
.cover-block .cover-desc {
    color: #dddddd !important;
}
.project-stats .nav > li > a:hover, .project-stats .nav > li > a:focus {
    background-color: #000000;
    border-color: rgba(0,0,0,0.06);
    color: #4185ef !important;
}
.project-stats li.missing a:hover {
    background-color: #000000;
}
.nav-links {
    border-bottom: 1px solid #4185ef;
}
ul.content-list {
    background: #303030 !Important;
    color: #fff !important;
    padding: 10px !important;
    box-shadow: 0px 0px 10px 1px #000;
    margin-top: 15px;
    border-radius: 3px;
}
.prepend-top-default .commit-box .commit-title {
    color: #4185ef;
}
.prepend-top-default .content-block {
    color: #4185ef;
    background-color: #303030;
}
.light {
    color: #fff;
}
.cgray {
    color: #fff;
}
.file-holder .file-content {
    background: #303030;
}
.file-holder .file-title {
    color: #fff;
    border-bottom: none;
    background-color: #4185ef;
}
.file-holder .file-title a:not(.btn) {
    color: #fff;
}
.file-holder.readme-holder {
    margin-top: 5%;
}
table.table tr td {
    border-color: #4185ef;
    border-bottom: 1px solid #000;
}
.tree-holder .tree-item .tree-item-file-name a {
    color: #fff;
}
.tree-holder .tree_commit .tree-commit-link {
    color: #fff;
}
table.table tr th {
    background-color: #303030;
    border-bottom: 1px solid #4185ef;
}
.tree-holder .tree-table tr:hover td {
    background: #202020;
}
.breadcrumb.repo-breadcrumb a {
    color: #4185ef;
}
.blob-commit-info {
    background: transparent;
}
li.commit .commit-row-title .commit-row-message {
    color: #FFF;
}
li.commit .commit-row-info {
    color: #FFF;
}
li.commit .commit-row-info a {
    color: #4185ef;
}
li.commit .commit-row-title .commit_short_id {
    color: #fff;
}
body.ui_charcoal .page-with-sidebar .sidebar-wrapper .sidebar-user {
    background: #000;
    color: #fff;
}
body.ui_charcoal .page-with-sidebar .collapse-nav a {
    color: #fff;
    background: #202020;
}
.panel admin-filter {
    background: transparent;
}
.merge-request.today, .issue.today {
    background: transparent;
    border-color: #000;
    box-shadow: inset 0px 0px 6px 1px #4185ef, 0px 0px 6px 4px #000 !important;
}
.issue-info {
    color: #000;
}
ul.notes .note .note-body .note-text p {
    color: #fff;
}
.note-header .author_link {
    color: #4185ef;
}
li.commit .commit-row-title .commit-row-message {
    color: #4185ef !important;
}
ul.notes .system-note {
    background: #202020;
}
.issues-list .issue {
    box-shadow: inset 0px 0px 6px 1px #ccc, 0px 0px 6px 4px #000;
}
.issue-info {
    color: #4185ef;
}
.panel-default>.panel-heading {
    color: #fff;
    background-color: #000;
    border-color: #4185ef;
}
.panel-default {
    border: none;
}
.panel.panel-small .panel-heading a {
    color: #fff;
    font-size: 17px;
}
.panel {
    background-color: transparent;
}
.issue.closed {
    background: #202020;
}
.project-visibility-level-holder .radio .option-descr {
    color: #fff !important;
}
.project-visibility-level-holder .radio .option-title {
    color: #fff !important;
}
legend {
    color: #4185ef !important;
}
}
