css:
@-moz-document regexp("http://pantip.com/topic/.*"), regexp("https://pantip.com/topic/.*") {
@font-face {
  font-family: 'Kanit';
  font-style: normal;
  font-weight: 400;
  src: local('Kanit'), local('Kanit-Regular'), url(https://fonts.gstatic.com/s/kanit/v1/XJlHi6uxFW6uak2UaVnLX4DGDUGfDkXyfkzVDelzfFk.woff2) format('woff2');
  unicode-range: U+0E01-0E5B, U+200B-200D, U+25CC;
}

* {
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
}
::selection {
    background: #aaa;
    color: #fff;
}
::-moz-selection {
    background: #27AAE1;
    color: #fff;
}
html {
    min-width: inherit;
}
body {
    font-family: Helvetica, Thonburi, "Leelawadee", Tahoma;
    line-height: 1.75em;
    background: #2b2e33;
    margin: 0 auto;
    min-width: inherit;
}
.samgee .logo-banner {
    border-bottom: 1px solid #444;
}
.samgee .logo-banner.doodle-banner {
    background: none !important;
}
.samgee .expand-submenu {
    background: #2b2e33;
    border-bottom: 1px solid #444;
}
.samgee .menu-one .submenu-room-item .iconwrap {
    background: #2b2e33;
   	border: 1px solid transparent;
    opacity: 0.5;
}
.samgee .menu-one .submenu-room-item a:hover {
    background: rgba(255,255,255,0.1);
}
.samgee .menu-one .submenu-room-item a:hover .iconwrap {
    background: none;
    opacity: 1;
    border: 1px solid transparent;
}
.samgee .menu-one .submenu-room-item .title {
    color: #eee;
}
.samgee .menu-one .submenu-room-item .desc {
    color: #999;
}
.samgee .header {
    width: 100%;
    height: 2.6em;
    background: none;
    position: relative;
    bottom: 0px;
    right: 0px;
    left: 0;
    border: 0 !important;
    z-index: 1;
}
.samgee .container-outer.container-liquid > a {
    display: none !important;
}
.samgee .header .search-wrap {
    display: none !important;
}
.samgee .logo-banner.doodle-banner {
    height: auto;
}
.samgee .logo-banner.doodle-banner .container-outer {
    height: 40px;
    margin-top: 5px;
}
.samgee .expand-submenu .submenu-room {
    left: 0;
}
.samgee .expand-submenu .container-outer.container-liquid {
    width: 100%;
    max-width: 840px;
    font-size: 12px;
}
.samgee .main-post .display-post-story {
    color: #aaa;
}
.samgee .container-outer,
.samgee .container-liquid {
    width: 720px;
    max-width: inherit;
    min-width: inherit;
    padding-top: 0;
    padding-bottom: 0;
}
.samgee .display-post-wrapper.main-post {
    background: transparent;
    border-bottom: 1px solid #444;
    padding-bottom: 10px;
    margin-bottom: 30px;
}
.samgee .display-post-wrapper.main-post .display-post-avatar a img {
    display: none;
}
.samgee .display-post-wrapper.main-post .main-post-inner {
    border: 0;
    box-shadow: none;
}
header.s-down {
    display: none;
    visibility: hidden;
}
.display-post-status-leftside {
    padding: 0 !important;
}
.fixed-header,
.display-post-favourite,
.display-post-tag-wrapper,
.icon-clock,
.comment-box-remark,
.related-post,
.footer,
.display-post-edit,
.avatarlink,
.icon-memberbadge-mini,
.icon-mini-status.icon-status-owner,
.share-widget02,
.icon.icon-new-post{
    display: none !important;
}
.samgee .header li a {
    opacity: 0.6;
}
.samgee .header li a:hover {
    opacity: 1;
    border: 0;
    background: none;
}
.samgee .header .global-navigate li a small {
    position: relative;
    top: -1px;
    font-size: 6px;
}
.samgee .header .global-navigate li,
.samgee .header .my-menu li {
    border-right: 0;
    border-left: 0;
}
.emotion-vote-user {
    opacity: 0;
    height: 0;
    padding: 0;
}
.samgee .emotion-vote-choice {
    border: 0;
}
.emotion-vote-list {
    background: transparent !important;
    border: 0 !important;
    height: 0 !important;
    overflow: hidden !important;  
}
.samgee .display-post-avatar .display-post-avatar-inner {
    margin-left: 0;
}
.samgee .emotion-vote-user,
.samgee .emotion-vote-choice {
    padding: 0;
}
.display-post-story {
    padding-bottom: 20px;
    font-size: 20px !important;
    line-height: 40px !important;
    font-weight: 300;
    font-family: Helvetica, Thonburi, "Leelawadee", Tahoma;
}
.samgee .main-post .display-post-vote .like-score,
.samgee .display-post-vote .like-score.top-score,
.samgee .main-post .display-post-emotion .emotion-vote,
.samgee .altcolor05 .display-post-emotion .emotion-vote,
.samgee .display-post-avatar .display-post-avatar-inner .display-post-name,
.samgee .display-post-avatar .display-post-avatar-inner .display-post-timestamp,
.samgee .display-post-vote .like-score,
.samgee .display-post-emotion .emotion-vote,
.samgee .altcolor05 .display-post-vote .like-score,
.samgee .top-comment .display-post-vote .like-score,
.samgee .top-comment .display-post-emotion .emotion-vote {
    color: #777;
}
.display-post-vote .icon-heart-like {
    -webkit-filter: url("data:image/svg+xml;utf8,&lt;svg xmlns=\'http://www.w3.org/2000/svg\'&gt;&lt;filter id=\'grayscale\'&gt;&lt;feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/&gt;&lt;/filter&gt;&lt;/svg&gt;#grayscale");
    filter: gray;
    -webkit-filter: grayscale(100%);
}
#comments-jsrender {
    padding-bottom: 15px;
}
.samgee .main-post .display-post-emotion,
.samgee .altcolor05 .display-post-emotion {
    border-right: solid 1px #444;
}
.samgee .top-comment .display-post-emotion {
    border-right: solid 1px #304c3d;
}
.samgee .display-post-wrapper.section-comment,
.samgee .display-post-wrapper.best-answer {
    padding-top: 10px;
}
.samgee .display-post-wrapper.section-comment.altcolor05.sub-comment {
    padding-top: 10px;
}
.samgee .display-post-wrapper.section-comment.altcolor05 {
    padding-top: 0px;
}
.samgee .loadmore-bar-paging.sub-loadmore {
    width: 580px;
    margin: 0 0 0 70px;
    border: 0;
}
.samgee .loadmore-bar-paging.sub-loadmore.bottom {
    margin-top: 10px;
}
.samgee .loadmore-bar-paging a,
.samgee .loadmore-bar-paging .loadmore-bar-inner {
    border: 0;
    background: #29353f !important;
    box-shadow: none;
    color: #306a99;
    text-shadow: none;
}
.samgee .loadmore-bar-paging a:hover,
.samgee .loadmore-bar-paging.sub-loadmore a:hover {
    background: #314756 !important;
}
.samgee .loadmore-bar-paging a .focus-txt {
    color: #75abd8 !important;
}
/* Regular Comment */

.samgee .display-post-wrapper.best-answer .display-post-wrapper-inner,
.samgee .display-post-wrapper.section-comment .display-post-wrapper-inner {
    background: #222426;
    padding: 10px;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    width: 580px;
    border-radius: 5px;
}
.samgee .display-post-wrapper.best-answer .display-post-wrapper-inner {
    background: #321c3a;
}
.samgee .display-post-wrapper.section-comment .display-post-story {
    font-size: 16px !important;
    line-height: 28px !important;
    color: #888;
}
/* Comment Owner */

.samgee .display-post-wrapper.section-comment.altcolor05 .display-post-wrapper-inner {
    background: transparent;
    padding: 0px;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    width: 720px;
    margin: 50px 0 30px;
    border-bottom: 1px solid #444;
    padding-bottom: 30px;
    border-radius: 0;
}
.samgee .display-post-wrapper.section-comment.altcolor05 .display-post-story {
    color: #aaa;
    font-size: 20px !important;
    line-height: 40px !important;
}
/* Sub Comment Owner */

.samgee .display-post-wrapper.sub-comment.altcolor05 .display-post-wrapper-inner {
    background: #1b2935;
    padding: 10px !important;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    width: 580px;
    border-bottom: 0;
    margin: 0;
    border-radius: 5px;
    padding-top: 10px !important;
}
.samgee .display-post-wrapper.sub-comment {
    margin-left: 70px;
}
.samgee .display-post-status-leftside {
    border: 0 !important;
}
.samgee .main-post .display-post-status {
    top: 30px;
    right: 0;
}
.samgee .main-post .display-post-status .icon,
.samgee .display-post-status .icon-mini-status {
    float: right;
    filter: url("data:image/svg+xml;utf8,&lt;svg xmlns=\'http://www.w3.org/2000/svg\'&gt;&lt;filter id=\'grayscale\'&gt;&lt;feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/&gt;&lt;/filter&gt;&lt;/svg&gt;#grayscale");
    filter: gray;
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}
.samgee .display-post-status {
    right: 10px;
}
.remark-four-txt,
.unfocus-txt {
    color: #666 !important;
}
.display-post-action .display-post-timestamp {
    font-size: 11px !important;
    position: relative;
    top: 2px;
    color: #555 !important;
}
.display-post-story .youtube-thumbnail .video_id img {
    margin: 0;
}
.display-post-story .youtube-thumbnail {
    margin: 20px 0;
}
.youtube-thumbnail.neo.m, .youtube-thumbnail.neo.m .video_id .video_preview {
    height: 400px;
    width: 560px;
}
.youtube-thumbnail.neo.m .video_id .play_btn {
    top: 170px;
    left: 240px;
}
.youtube-thumbnail.neo.m iframe{
    width: 100% !important;
    height: 100% !important;
}
.display-post-story img {
    max-width: 100%;
    margin: 20px 0;
    box-shadow: 0 3px 15px rgba(0, 0, 0, 0.4);
}
.display-post-story img.img-in-emotion {
    padding: 0;
    box-shadow: none;
}
.float-nav-gotopage {
    float: right;
}
.samgee .float-nav-info {
    height: 30px;
}
.samgee .topic-float-nav .float-nav-info .icon,
.samgee .topic-float-nav a.block .icon-story-mode {
    display: none;
    position: relative;
    top: 0;
    left: 0;
}
.samgee .topic-float-nav a.block {
    padding: 0 20px 0 0;
    color: #666;
}
.samgee .topic-float-nav {
    background: #222;
    opacity: 1;
    box-shadow: none;
    border: 0;
    color: #666;
}
.samgee .topic-float-nav select {
    background: #333;
}
.samgee .pageno-title.pageno-title-counter .title {
    margin: 30px 0 0;
    padding: 0;
    background: none;
    font-family: Kanit, Helvetica, Thonburi, "Leelawadee", Tahoma;
    font-size: 22px;
    color: #aaa;
}
.samgee .pageno-title.pageno-title-counter .title i {
    display: none;
}
.samgee .display-post-wrapper.pageno-title .pageno-title-line {
    display: none;
}
.samgee .comment-box-color {
    margin: 20px 0 0;
}
.samgee .comment-box-color .display-post-story-wrapper {
    border: 0;
}
.samgee .comment-box-color .display-post-wrapper-inner {
    background: none;
    box-shadow: none;
}
.samgee .display-post-wrapper.comment-box-color .surround-box-light.bb-toolbar {
    background: rgba(0,0,0,0.9);
    border: 0;
}
.samgee .comment-box-color .toolbar-icon {
    border: 0;
    background: none;
    opacity: 0.5;
}
.samgee .display-post-wrapper.comment-box-color input,
.samgee .display-post-wrapper.comment-box-color select,
.samgee .display-post-wrapper.comment-box-color textarea {
    background: rgba(0, 0, 0, 0.2);
    border: 0;
    box-shadow: none;
    outline: none;
    font-family: Helvetica, Thonburi, "Leelawadee", Tahoma;
    font-weight: 300;
    padding: 10px;
    font-size: 16px;
}
.samgee .display-post-story-footer {
    margin-top: 15px !important;
}
.spoil-style {
    border: dashed 1px #555;
    padding: 10px 0px 10px 20px;
}
.samgee .display-post-wrapper.comment-box-w-ad {
    width: 100%;
}
.button {
    border: 0;
    background: none !important;
}
p {
    color: #ccc;
    font-size: 20px;
    line-height: 36px;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: Helvetica, Thonburi, "Leelawadee", Tahoma;
}
h1,
h2 {
    color: #fff !important;
}
.samgee .display-post-title {
    font-family: Kanit, Helvetica, Thonburi,"Leelawadee", Tahoma;
    font-size: 32px;
    line-height: 48px;
    padding: 60px 0 20px;
}
h1 {
    font-size: 30px;
    line-height: 42px;
}
h2 {
    font-size: 22px;
    line-height: 26px;
}
hr {
    height: 1px;
    background-color: #d8d8d8;
    border: none;
    width: 100%;
    margin: 0px;
}
a[href] {
    color: #27AAE1;
    text-decoration: none;
}
a[href]:hover {
    color: #4ECAFF;
}
.display-post-story a[href]:hover {
    text-decoration: underline;
}
li {
    line-height: 1.5em;
}
}
