css:
/* i really want this to be global */
@-moz-document regexp("https://.*.purelyhr.com/.*") {
body,#appHeaderNav { background: url(https://cdn.purelyhr.com/img/bg/15.png) no-repeat center center fixed; }
.phr.main-container {
    background-color: inherit !important;
    margin-top:0px;
    padding-top:120px;
    height:110%;
}
.page-heading {right: 17px;}
.block { background-color: rgba(255,255,255,0.3); }
.phr.main-top .phr.brand-purelyhr  { background-color: #1C2B36;}
.phr.main-top  { background-color: #1C2B36;}
.page-heading { 
    background-color: rgba(21, 32, 41, 0.5);
}
.page-title { color: #fff; }
.page-subtitle { color: #ddd; }
.page-heading-action-container {border-left-color:black;}

/* #1C2B36 */
.phr.main-left { background-color: rgba(28, 43, 54, 0.8);}


.btn { color: rgba(40,40,40,0.8); }


.table tbody tr.odd { background-color: rgba(150,150,150,0.3);}
.table tbody tr { background-color: rgba(250,250,250,0.3);  }
.table > tbody > tr > td { border-color: rgba(250,250,250,0.3); }
.table tbody tr.info td { background-color: rgba(0,0,0,0.1); }
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: rgba(0,0,0,0.1);
}

.fc-toolbar {background-color:#3C3C3C;}
.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active{border: 1px solid #404040; background: #585858;}

#jobCodeBreakdown { background-color: rgba(50,50,50,0.1); }

.select2-container--default { background-color: rgba(246,246,247,0.3)}

.legend > div { background-color: inherit !important; padding:23px !important; }
.legend > table { background-color:rgba(0,0,0,0.7); }

::-webkit-scrollbar {
    width: 17px;
}
::-webkit-scrollbar-track {
    background-color: rgba(21, 32, 41, 0.4);
}
::-webkit-scrollbar-track:hover {
    background-color: rgba(21, 32, 41, 0.4);
}
::-webkit-scrollbar-thumb {
    background-color: rgba(21, 32, 41, 0.4);
}
::-webkit-scrollbar-thumb:hover {
    background-color: rgba(21, 32, 41, 0.4);
}

/* For TOM */

#divContentTop,
#divContentBottom
{display: none; }
#menu-bar-outer {background-image:none; background-color:rgba(28, 43, 54, 0.75); }

#divContentMid { width:990px; padding: 8px 5px; background-color: rgba(255,255,255,0.3); background-image:none; }
#divContentPageTop {background-image:none;  background-color: rgba(0,0,0,0.3);  }
#divContentPageTop .title { color:white; text-shadow:none; }
}
