css:
@-moz-document url-prefix("http://prntscr.com"), domain("prnt.sc") {
#head, body, #content, #foot, #foot div, .box, .center,
 .border, .aC button, .aC, td h2, .payment-check-box {
background: url(http://vignette4.wikia.nocookie.net/kallenbearrp/images/5/51/Googletile.png/revision/latest?cb=20160113083017);
background-repeat: repeat;
}

a, p, h1, h2, h3, h4, h5, small, b, tr, .aC, .box, .error, .aC, .cL {
    color: #2cabe2 !important;
    text-shadow:0px 0px 2px black;
}

.twitter-item__text {
    color: #bbb;
    text-shadow:0px 0px 2px black;
}

.purchase {
background:blue !important;
}

.twitter-header__title {
    font-size: 18px;
    font-weight: bold;
    color: #2CABE2;
    opacity: .8;
    text-shadow: 0 1px 0 #fff;
}

.twitter-header__title_small, .twitter__more {
    font-size: 15px;
    color: #2CABE2;
    opacity: .8;
    font-weight: bold;
    text-shadow: 0 1px 0 #fff;
    background: rgba(0,0,0,0.40);
}

.header-auth-popup ul, .header-language-popup ul {
    margin: 0;
    padding: 0;
    background: rgba(0, 0, 0, 0.80);
}

.gallery__title, .loaded-info {
    background: rgba(0, 0, 0, 0.40);
}

.gallery__title textarea {
    color: #ccc;
    text-shadow: 0 0 2px #000;
}

.gallery__date {
    font-size: 12px;
    color: #ccc;
    text-shadow: 0 0 2px #000;
}

.footer {
    background: url(http://vignette2.wikia.nocookie.net/kallenbearrp/images/7/73/Seamless-leather-texture5.jpg/revision/latest?cb=20160113115101);
    background-repeat: repeat ;
}

.footer-logo {
    background: rgba(41, 142, 186, 0.40);
    box-shadow: 0px 0px 20px #298EBA;
}

.footer-copy {
    color: #000;
    font-weight: bold; 
    text-shadow:0px 0px 2px white;
}
}
