  body {
      font-size: 24px;
      min-width: 400px;
      max-width: 700px;
      margin: auto;
      background-color: white;
      color: darkslategrey;
      line-height: 1.25;
      font-family: "Source Serif Pro";
  }

  tt {
      color: black;
      font-family: "Source Code Pro";
      font-weight: 600;

      white-space: nowrap;
  }

  a {
      color: lightseagreen;
      text-decoration: none;
      font-weight: 600;
  }

  img {
      background-color: white;
      padding: 5px;
      zoom: 1.5;
  }

  h1, h2, h3, h4, h5, h6 {
      font-family: "Source Sans Pro";
      text-decoration: none;
  }

  .navigation {
      color: lightslategray;
      font-style: normal;
  }

  .footnote {
      line-height: 1.25;
  }

  tt i {
      font-style: normal;
      color: lightslategray;
      white-space: nowrap;
  }

  em {
      font-weight: 700;
      font-style: normal;
  }

  sup {
      font-size: 1em;
  }

  small {
      font-size: 0.65em;
  }
