css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("eevblog.com") {
	a:link {
		color: #aaa !important;
		outline: 0 !important;
	}
	a:hover, a:visited {
		color: #fff !important;
		outline: 0 !important;
	}
	body {
		background-image: none !important;
		background-color: #111 !important;
	}
	body center, #leaderboard1 {
		display: none !important;
	}
	input,button,select,textarea {
		color: #aaa !important;
		background: #000 none repeat scroll 0% 0% !important;
		border: 1px solid #777 !important;
	}
	.button_submit,.button_reset {
		background-image: none !important;
		background-color: #000 !important;
    color: #aaa !important;
	}
	input:hover,textarea:hover,button:hover,select:hover {
		border: 1px solid #aaa !important;
	}
	.button_submit:hover,.button_reset:hover {
		border: 1px solid #aaa;
		background-image: none !important;
		background-color: #222 !important;
	}
	blockquote.bbc_standard_quote,blockquote.bbc_alternate_quote {
		color: #aaa !important;
		background-image: none !important;
		border-top: none !important;
		border-bottom: none !important;
		border: 1px solid #333 !important;
	}
	blockquote.bbc_standard_quote {
		background-color: #111 !important;
	}
	blockquote.bbc_alternate_quote {
		background-color: #333 !important;
	}
	code.bbc_code {
		background-color: #111 !important;
		color: #aaa !important;
		border-top: none !important;
		border-bottom: none !important;
		border: 1px solid #666 !important;
	}
	.codeheader, .quoteheader {
		background-color: transparent !important;
		color: #aaa !important;
	}
	.titlebg,.titlebg2,tr.titlebg th,tr.titlebg td,tr.titlebg2 td {
		background-image: none !important;
		background-color: #e3e9ef !important;
	}
	.catbg,.catbg2,tr.catbg td,tr.catbg2 td,tr.catbg th,tr.catbg2 th {
		background-image: none !important;
		background-color: #444 !important;
	}
	.openid_login {
		background-image: none !important;
		background-color: #000 !important;
	}
	.description,.description_board,.plainbox {
		border: 1px solid #444 !important;
		background: #111 !important;
		color: #aaa !important;
	}
	#ajax_in_progress {
		background-image: none !important;
	}
	h4.catbg,h4.catbg2,h3.catbg,h3.catbg2,.table_list tbody.header td.catbg {
		background-image: none !important;
	}
	h4.titlebg,h3.titlebg {
		background-image: none !important;
    background-color: #333 !important;
    color: #aaa !important;
	}
	div.cat_bar {
		background-image: none !important;
		background-color: #444 !important;
	}
	div.title_bar {
		background-image: none !important;
		background-color: #333 !important;
	}
	.roundframe {
		border-left: none !important;
		border-right: none !important;
	}
	div.roundframe div.cat_bar {
		background-image: none !important;
		background-color: #444 !important;
	}
	div.roundframe div.cat_bar h3.catbg {
		background-image: none !important;
	}
	div.title_barIC {
		background-image: none !important;
		background-color: #333 !important;
	}
	div.title_barIC h4.titlebg {
		background-image: none !important;
	}
	.table_grid tr.catbg,.table_grid tr.titlebg {
		border-bottom: none !important;
	}
	tr.catbg th.first_th {
		background-image: none !important;
		background-color: #444 !important;
	}
	tr.catbg th.last_th {
		background-image: none !important;
		background-color: #444 !important;
	}
	tr.whos_viewing {
		background-color: #333 !important;
	}
	tr.titlebg th.first_th {
		background-image: none !important;
		background-color: #e3e9ef !important;
	}
	tr.titlebg th.last_th {
		background-image: none !important;
		background-color: #e3e9ef !important;
	}
	table.table_grid td {
		border-bottom: none !important;
		border-right: none !important;
	}
	.windowbg span.topslice {
		background-image: none !important;
	}
	.windowbg span.topslice span {
		background-image: none !important;
	}
	.windowbg span.botslice {
		background-image: none !important;
	}
	.windowbg span.botslice span {
		background-image: none !important;
	}
	.windowbg2 span.topslice {
		background-image: none !important;
	}
	.windowbg2 span.topslice span {
		background-image: none !important;
	}
	.windowbg2 span.botslice {
		background-image: none !important;
	}
	.windowbg2 span.botslice span {
		background-image: none !important;
	}
	.approvebg span.topslice {
		background-image: none !important;
	}
	.approvebg span.topslice span {
		background-image: none !important;
	}
	.approvebg span.botslice {
		background-image: none !important;
	}
	.approvebg span.botslice span {
		background-image: none !important;
	}
	.roundframe {
		background-color: #111 !important;
	}
	span.upperframe {
		background-image: none !important;
	}
	span.upperframe span {
		background-image: none !important;
	}
	span.lowerframe {
		background-image: none !important;
	}
	span.lowerframe span {
		background-image: none !important;
	}
	.dropmenu li ul {
		background-image: none !important;
    background-color: #111 !important;
	}
  .dropmenu li li:first-child {
    border-top: 1px solid #999 !important;
  }
	.dropmenu li a.active {
		background-image: none !important;
	}
	.dropmenu li a.active span.firstlevel {
		background-image: none !important;
	}
	.dropmenu li a.firstlevel:hover,.dropmenu li:hover a.firstlevel {
		background-image: none !important;
	}
	.dropmenu li a.firstlevel:hover span.firstlevel,.dropmenu li:hover a.firstlevel span.firstlevel {
		background-image: none !important;
	}
	.dropmenu li li a:hover,.dropmenu li li:hover>a {
		background-color: #333 !important;
	}
	.buttonlist ul li a {
		color: #aaa !important;
		background-image: none !important;
		background-color: #444 !important;
		padding: 3px 0 3px 8px !important;
    border: 1px solid #aaa !important;
	}
	.buttonlist ul li:hover, .buttonlist ul li {
		background-color: transparent !important;
	}
	.buttonlist ul li a:hover {
		background-image: none !important;
		background-color: #333 !important;
	}
	.buttonlist ul li a span {
		background-image: none !important;
    background-color: #444 !important;
	}
	.buttonlist ul li a:hover span, .buttonlist ul li a.button_strip_reply:hover {
		color: #aaa !important;
		background-image: none !important;
		background-color: #333 !important;
	}
	.buttonlist ul li a.active {
		background-image: none !important;
		background-color: #444 !important;
	}
	.buttonlist ul li a.active span {
		background-color: #444 !important;
		background-image: none !important;
	}
	.buttonlist ul li a.active, .buttonlist ul li a.active span {
		background-color: #444 !important;
	}
	.buttonlist ul li a.active:hover, .buttonlist ul li a.active:hover span  {
		color: #aaa !important;
		background-color: #333 !important;
	}
	#header {
		background-image: none !important;
		background-color: #111 !important;
    color: #aaa !important;
	}
	#header div.frame {
		background-image: none !important;
	}
	#content_section {
		background-image: none !important;
		background-color: #111 !important;
	}
	#content_section div.frame {
		background-image: none !important;
	}
	#footer_section {
		background-image: none !important;
	}
	#footer_section div.frame {
		background-image: none !important;
	}
	ul.quickbuttons li.quote_button {
		background-image: none !important;
	}
	ul.quickbuttons li.remove_button {
		background-image: none !important;
	}
	ul.quickbuttons li.modify_button {
		background-image: none !important;
	}
	ul.quickbuttons li.approve_button {
		background-image: none !important;
	}
	ul.quickbuttons li.restore_button {
		background-image: none !important;
	}
	ul.quickbuttons li.split_button {
		background-image: none !important;
	}
	ul.quickbuttons li.reply_button {
		background-image: none !important;
	}
	ul.quickbuttons li.reply_all_button {
		background-image: none !important;
	}
	ul.quickbuttons li.notify_button {
		background-image: none !important;
	}
	.topic_table td.stickybg2 {
		background-image: none !important;
	}
	.topic_table td.lockedbg2 {
		background-image: none !important;
	}
	.topic_table td.locked_sticky2 {
		background-image: none !important;
	}
	.profile_pie {
		background-image: none !important;
	}
	.statsbar div.bar {
		background-image: none !important;
	}
	.statsbar div.bar div {
		background-image: none !important;
	}
	.arp_header {
    color: #aaa !important;
	}
	.buttons_tools h2 {
		background-image: none !important;
	}
	.buttons_tools h2:hover {
		background-image: none !important;
	}
	.buttons_tools a {
		background-image: none !important;
	}
	.navigate_section ul {
		border-top: 1px none !important;
	}
	#upshrinkHeaderIC p.last {
		border-top: none !important;
	}
	table tr:nth-child(even) td.arp_rest, table tr:nth-child(even) td.arp_left, table.table_list tr:nth-child(even) td, table.table_grid tr:nth-child(even) td, .windowbg {
		background-color: #000 !important;
		color: #aaa !important;
		border: none !important;
	}
	table tr:nth-child(odd) td.arp_rest, table tr:nth-child(odd) td.arp_left, table.table_list tr:nth-child(odd) td, table.table_grid tr:nth-child(odd) td, .windowbg2 {
		background-color: #222 !important;
		color: #aaa !important;
		border: none !important;
	}
}
