css:
@-moz-document domain("arstechnica.com") {
body,
html {
    background: #101010;
}
article.standalone,
.article-content,
article.in-column a h1,
article.in-column a,
.main-feature-alt h1,
.heading,
div.brief-box ol li a,
table.post .post-author,
table.post .post-author .post-author-tribus,
table.post .post-author .post-author-registered,
table.post .post-author .post-author-postcount,
table.post .post-content,
#preview-content,
#latest-stories ul li .title,
#latest-stories .lower-dek,
#footer a,
h2,
h2.title,
table.post .post-author .post-author-name a,
table.post .post-author .post-author-name a:visited,
#forum-links a,
#anonymous-links ul li a,
#author-footer .author-social-footer strong,
#recommendations-footer .heading-column h3,
article.single .post-lowerdek,
article.single .post-title,
.post-tease .post-meta h2 a,
p,
#content .subheading,
.jumpbox .jumptext {
    color: #777777;
}
#content,
li.feature-fixed {
    background: #202020;
}
.right.for-rule {
    border-left: 1px solid #404040;
}
article.standalone {
    border-right: 1px solid #404040;
}
h2.standalone-deck,
article.standalone .post-meta,
.latest-stories li,
article.in-column,
figcaption,
.post-links,
.thin-divide-bottom,
article.standalone .comments-bar,
.main-feature-alt {
    border-bottom: 1px solid #404040;
}
.latest-stories {
    border-bottom: 5px solid #404040;
}
.thick-divide-top {
    border-top: 10px solid #404040;
}
.thick-divide-bottom {
    border-bottom: 10px solid #404040;
}
#radar {
    background-color: #101010;
    border: 1px solid #404040;
    background: -webkit-linear-gradient(top, rgb(244, 61, 0) 0%, rgba(0, 0, 0, 0) 1%);
}
#stories-main h1.cutout {
    color: #777777;
    background-image: linear-gradient(rgba(32, 32, 32, 0.70), rgb(32, 32, 32));
}
.main-feature-alt p.excerpt,
p.excerpt,
#recommendations .lower-deck {
    color: #505050;
}
div.brief-box {
    border: 1px solid #ff4f00;
    background: #202020;
}
article .comments-hotness,
article.in-column .comments-hotness.radar {
    background: rgba(0, 0, 0, 0);
}
.row {
    background: #101010;
}
.post-tease .post-meta {
    background-color: transparent;
    background-image: linear-gradient(rgb(16, 16, 16) 0px, rgb(16, 16, 16) 65%);
}
.post-tease::after {
    border-color: rgb(255, 78, 0) rgb(255, 78, 0) rgb(255, 78, 0);
}
main {
    background-color: #101010;
}
#author-footer {
    border-top: 10px solid rgb(244, 61, 0);
}
#masthead {
    background-color: #303030;
}
table.post .post-date-actions-inner {
    border-bottom: 1px dotted #777777;
}
table.post.odd {
    background-color: #101010;
}
table.post.even {
    background-color: #151515;
}
table.post.odd .post-author {
    background: #101010;
}
table.post.even .post-author {
    background: #151515;
}
#content {
    border-left: 1px solid #777777;
    border-right: 1px solid #777777;
}
table.post {
    border-bottom: 1px solid #777777;
}
.pm-panel-message .quotecontent,
table.post .post-content .quotecontent,
table.post .post-content .ip-ubbcode-quote-content,
#preview-content .quotecontent,
#preview-content .ip-ubbcode-quote-content {
    border-bottom: 1px solid #777777;
    border-top: 1px solid #777777;
}
.actionbar {
    background: #202020;
}
#stories-main h1.cutout:hover {
    color: rgb(255, 78, 0);
}
}
