css:
@-moz-document domain(devforum.ro) {
  html{	
    color: #fff;
    background-color: #222;
  }
  .map:not(code), .map-colapsed:not(code), .topic-map:not(code), .topic-map-expanded:not(code), .winget-button, .avatars, .limks, .topic-body:not(code){
    background-color: #222;
    color: #fff !important;
    border-color: #333 !important;
  }
  strong code, em code, i code, b code, u code, p code {
    color: #333;
    background: #f8f8f8;
  }
  code:not(.hljs), .cooked del, .d-editor-preview del {
    background-color: #333 !important;
    color: #80B0C9 !important;
  }
  .title{
    color: #80B0C9 !important;
    border-color: #333 !important;
  }
  .quote, nav.button {
    background-color: #222 !important;
  }
  aside.quote blockquote{
    color: #fff;
    background-color: #222;
    border-color: #333;
  }
  blockquote{
    background-color: #262626;
    border-color: #333;
  }
  .d-header {
    background-color: #181818;
    border-color: #333;
  }
  .contents .title {
    background-color: rbga(255,255,255,0);
  }
  .quote .title{
    background-color: #222 !important;
  }
  button.widget-button {
    background-color: #222 !important;
    border-color: #333 !important;
  }
  img, .topic-avatar, tr.topic-list-item, list>tbody>tr:first-of-type, #topic-closing-info, section.links {
    border-color: #333 !important;
  }
  .extra-info-wrapper .topic-link {
    color: #3CBEFF;
  }
  a.mention, a.mention-group {
    background-color: #333;
    color: #3CBEFF;
  }
  .nav-pills>li:not(.active) a{
    color: #80B0C9 !important;
  }
  .fancy-title{
    color: #80B0C9 !important;
  }
  .widget-button:hover {
    color: #fff !important;
  }
  .modal-body {
    color: #333;
  }
  aside.onebox .onebox-body {
    color: #999 !important;
  }
  aside.onebox header a[href] {
    color: #999 !important;
  }
  .checkbox-label {
    color: #000;
  }
  .moderator .topic-body {
    background-color: #666;
  }
  #topic-closing-info h3 {
    background-color: #666;
  }
  .cookie-monster {
    background: #666;
  }
  .form-horizontal .controls label, .form-horizontal .control-label {
    color: #333 !important;
  }
  #reply-control .reply-area .ac-wrap {
    color: #333;
  }
  button.widget-button {
    color: #80B0C9;
  }
  
  a {
    color: #3CBEFF;
  }
  button {
    color: #ffffff;
  }
  a[data-auto-route="true"], a.full-name, .fa-heart{
    color: #80B0C9 !important;
  }
  a.title:not(.visited) {
    color: #3CBEFF !important;
    text-shadow: 0px 0px 30px #fff;
  }
  a code {
    font-weight: 900;
    /* background-color: #444 !important; */
    color: #91DAFF !important;
  }
  a.hashtag {
    color: #08c;
  }
  a.hashtag:visited {
    color: #80B0C9;
  }
  a.hashtag:hover {
    color: #3CBEFF;
  }
  .topic-list.categories .category h3 a[href] {
    color: #08c;
  }
  .topic-list.categories .category h3 a[href]:hover {
    color: #3CBEFF;
  }
  .topic-list.categories .category h3 a[href]:active {
    color: #666;
  }
  .category-list tbody .category h3 a[href] {
    color: #3CBEFF;
  }
  .fa.fa-circle-o, .poll-container ul li span {
    color: #fff;
  }
  #reply-control .contents .d-editor-preview blockquote {
    color: #fff;
  }
}
