/* STYLE BY ADALEIGHFAITH || TUMBLR & INSTAGRAM */

@-moz-document domain("adf.ly") {

#CashSlideDiv, #interstitual table tbody tr:not([height*="1%"]), #sitebar #bottom, #sitebar #bottomAd, #skip_ad_button, #top span:first-child, div[style*="display: inline-block; text-align: center; padding-top: 20px; height: 20px; width: 60%; color: #ffffff; letter-spacing: 2px"] {
display: none !important;
}

#interstitual #top {
background-image: none;
position: relative !important;
}

#interstitual table tbody tr td {
position: absolute;
top: 30%;
}

#interstitual {
background: rgb(8,46,73)!important;
}

#please_wait {
margin-left: -155px;
top: 301px !important;
}

#skip_button {
background-image: url("http://i.imgur.com/Mvll0m0.png");
height: 41px !important;
right: auto !important;
top: 301px !important;
width: 155px !important;
}

#top span {
left: 50%;
margin-left: -77.5px;
position: absolute !important;
width: 155px !important;
}

}
