css:
@-moz-document domain("www.bol.com") {

.constrain--light {
    background-color: #222222;
}
body, .service-bar__constrained:before, .service-bar-below {
    color: #c3c1bc;
    background-color: #222222;
}
.product_name {
    color: #999;
}
#top_bar ul li a:hover {
    color: #F06D23;
}
#top_bar ul li a {
    color: #c3c1bc;
}
#top_bar .top-bar__bdr {
    border-bottom: 1px solid #F06D23;
}
.shopping-cart--overview {
    background-color: #31312f;
}
.marketing-entries {
    background-color: #F06D23;
}
.cp .h3 {
    color: #7f9db9;
}
#sidebar .single-select-item {
    color: #7f9db9;
}
.header-fixed .main-nav__item.is-open>.main-nav__link {
    border-color: #f06d23;
}
.sub-nav {
    background: #31312F;
}
.menu-list a {
    color: #7f9db9;
}
.sub-nav__title {
    border-bottom: 1px solid #f06d23;
}
.is-open .category-nav-btn {
    background: #7BAEDE;
}
.header-fixed .main-nav__link {
    color: #c3c1bc;
    border-color: #31312f;
}
.header-fixed .main-nav__item {
    background-color: #31312f;
}
open .main-nav {
    background-color: #7f9db9;
    border-color: #f06d23;
}
.single_link li a {
    color: #62615f;
}
.breadcrumbs__link {
    color: #f06d23;
}
.seo_block {
    color: #d6e0f5;
}
#doormat a {
    color: #62615f;
}
.jumpstation .ingangen a {
    color: #f06d23;
}
.navigation_slot a {
    color: #969696;
}
.dc-cat-entry .content_title {
    background: rgba(49, 49, 47, 0.57);
    color: #d6e0f5;
}
.nav-list__link {
    color: #969696;
}
.table-zakelijk tr:first-child th {
    background: none;
    background-color: #222222;
    color: #fff7e0;
}
.table-zakelijk tr:first-child:hover th {
    background: none;
    background-color: #7f9db9;
}
.table-zakelijk tr:nth-child(even) {
    background-color: #333;
}
.table-zakelijk tr:nth-child(odd) {
    background-color: #222222;
}
.table-zakelijk th,
.table-zakelijk td {
    color: #fff7e0;
}
#zakelijk_cadeaukaarten #nav .main-nav2 a.selected,
#zakelijk_cadeaukaarten #nav .main-nav2 a:hover {
    color: #f06d23;
    border-bottom: 2px solid #f06d23;
}
.dc-intro  {
    background-color: #222222;
}
.usp ul li {
    color: #222222;
}
.order_item {
    background: none;
}
.address_part {
    background: none;
}
.bgbox_white {
    background: none;
}
.product-details__title {
    color: #f06d23;
}
.box--checkout {
    background-color: #31312f;
}
.checkout_form .option {
    border: 1px solid #62615f;
    background-color: #31312F;
}
.checkout_form fieldset.sendform {
    background: none;
}
.checkout_form .payment_option fieldset.sendform {
    background: none;
}
.step-indicator {
    background-color: #31312f;
}
.step-indicator__steps.is-active:before,
.step-indicator__steps.is-processed:before {
    right: 90%;
    border-color: #36c #36c #36c #31312f;
}
.step-indicator__steps.is-active:after,
.step-indicator__steps.is-processed:after {
    left: 90%;
    border-color: #31312f #31312f #31312f #36c;
}
.private_wishlist {
    background: #31312f;
}
.shared_wishlist {
    background: #31312f;
}
.shared_wishlist .outside_tr {
    background: none;
}
.shared_wishlist .outside_tl {
    background: none;
}
.info_message  {
    background: #333;
}
.js_overlay_box,
.js_overlay_box_split {
    background: #31312f;
}
.js_overlay_bg {
    background: rgb(0, 0, 0);
}
.bol_forms h2 {
    background: #31312f;
    color: #f06d23;
}
.product-title, .footer a {
    color: #7f9db9;
}
.table th {
    background-color: #31312f;
    color: #f06d23;
}
.rule, .product-delivery {
    color: #f06d23;
}
.header-section--search.fixed {
    border-bottom: 1px solid #f06d23;
}
.footer__columns {
    border-color: #7f9db9;
}
.product-tile__box {
    background: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}
.product_creator, .contextual-navigation, .product-creator a, .product-seller__name {
    color: #36c;
}

.header-fluid, .header-section__search, .main-menu__button, .main-menu__content,.main-menu__sub-content  {
    background-color: #31312f
}

.main-menu__sub-button {
    border-top: 1px solid #31312f;
    border-bottom: 1px solid #31312f;
}

.show-more__fade {
    background: linear-gradient(rgba(255,255,255,0),#31312f)
}

.icon-svg--select, .buy-block__delivery-text, .buy-block__highlight {
    color: #d6e0f5;
}

.jumpstation-navigation__header>a, .jumpstation-navigation__header>span, .jumpstation-navigation__links>li>a, .navigation-block__node, .facet-control__filter a, .rollup__compact a, .logo-select{
     color: #7f9db9;
}
}
