css:
@-moz-document domain("imgur.com") {
a, .selection {
    color:#E86B86 !important;
}


body, #fullbleed-bg  {
background-attachment: fixed !important;
background: -moz-linear-gradient(top, #e5fcff 0%, #fff4f8 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e5fcff), color-stop(100%,#fff4f8));
background: -webkit-linear-gradient(top, #e5fcff 0%,#fff4f8 100%);
background: -o-linear-gradient(top, #e5fcff 0%,#fff4f8 100%);
background: -ms-linear-gradient(top, #e5fcff 0%,#fff4f8 100%);
background: linear-gradient(to bottom, #e5fcff 0%,#fff4f8 100%); 
}

/*** HEADER ***/

#topbar {
	position:fixed;  
	height:50px;
    top:0;  
	background:rgba(246,230,252,1)!important;
    box-shadow:none;
}

#header-logo a.logo:hover, .menu-icon:hover,  #topbar li a:hover, #topbar .notification-events button:hover, #topbar .notification-events button.active, #topbar .notification-conversations a.unread-count:hover+.conversations 
{ background:#E7FBFE !important;}


/*** MAIN PAGE ***/

.sentence-sorting, .sentence-sorting.search-sentence {
	color:#000;
    top:50px;
    margin-bottom:60px;
    z-index:99999;
}

.posts.br5, .posts.br5.posts-search {
    background:#fff;
    box-shadow:none !important;
}

.post img {
    outline: 0px;
}

#outside-tagging, #outside-tagging-showhide {
    background:#fff;
    box-shadow:none !important;
}

.textbox {
    background:#fff;
    color:#000 !important;
}


/*** GALLERY ***/

#under-image, .js-post-truncated.font-opensans-semibold, #comments-container.comments-initialized {
    background:rgba(255,255,255,0.5);
}
.post-container, #right-content {
    margin-top:50px;
    background:transparent;
}

.post-header, .post-image-description, .new-panel-header, .content.scroll-content.cf, .layout-options, .sg-item.list {
    background:#fff !important;
    color:#333 !important;
    box-shadow:none;
}

.post-title, h1, h2, h3 {color:#333 !important; text-shadow:none;}

a.layout-button { background:#fff !important;}

a.layout-button.selected { background:#E86B86 !important; color:#fff !important;}


/*** DROPDOWN MENU ***/

/*** images ***/

.nav-wrapper, #account-thumbs, #thumbs-container  {
    background:#fff !important;
}

/*** albums ***/

.info, .metadata {
    background:#fff !important;
}

/*** gallery profile ***/
.panel {
    background:rgba(255,255,255,0.5);
    margin-top:50px;
}

/*** messages ***/

.date-text, .message-wrapper.striped, .thread-bottom {
    background:rgba(255,255,255,0.5) !important;
    color: #444 !important;
}

/*** Settings ***/

#leftside-nav, .settings-content {
    margin-top:50px;
    color:#444;
}
}
