css:
@-moz-document domain("talknote.com") {
.task_list_box .task_list .task_item .icon, .task_list_box .task_list .task_item .username {
    height: 21px;
}
.task_list_box .task_list .task_item .complete a {
    display: block;
    width: 40px;
    height: 16px;
    font-size: 10px;
    line-height: 16px;
    border-radius: 3px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    background: #444;
    color: #fff;
    text-align: center;
    /* margin: 2px 0; */
}
.task_list_box .task_list .task_item .icon img {
 display: none;
}

.task_list_box .task_list .task_item .task_content {
    margin-top: 5px;
    /* word-break: break-all; */
    color: #525860;
}

.task_list_box .task_list .task_item .item_row.header {
 height: 0px;
}
.task_list_box .task_list .task_item .link-task-description {
 display: none;
}

#easy_question_form {
 display: none;
}
#sidebar_footer_nav {
 display: none;
}
.right_nav_cont {
 display: none;
}

#network_container #search_text, #member_container #search_text, #payment_card_show #search_text, #payment_card_edit #search_text, #device_container #search_text, .payment_container #search_text {
    border: 4px solid #ddd;
    width: 320px;
    height: 26px;
    font-size: 12px;
    line-height: 26px;
    padding-left: 5px;
    padding-right: 25px;
    border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    background: url("https://company.talknote.com/versions/20151105/images/mes_search.png?20150521") no-repeat 329px 9px;
    background-size: 15px;
}
.dm_entry_left{display: none;}
.task_item{border:dotted 1px #f0f;margin: 2px;}
.username a{text-indent:-5px;font-size:10px;overflow:visible;padding: 5px;}
.task_list_box .task_list .task_item .icon span, .task_list_box .task_list .task_item .icon a, .task_list_box .task_list .task_item .username span, .task_list_box .task_list .task_item .username a {
    display: inline-block;
    height: 12px;
    line-height: 12px;}
.task_list_box .task_list .task_item .username span.username, .task_list_box .task_list .task_item .username span.status {
   margin-left: 0px;
}
#dm_container #dm_entries .activity .message_text{
    margin: 8px 0 8px 16px;
    border: 1px dotted #F0F;
    padding: 5px;
    font-size: 12px !important;
    color: #f0f !important;
}
.message_close{font-size:18pt;color: #f0f !important;}
}
