css:
@-moz-document url-prefix("https://www.admitad.com/ru/webmaster/") {

.objects_list .line .links a.delete_offer {
background: linear-gradient(rgba(252, 236, 236, 1) 0%, rgba(250, 187, 187, 1) 19%, rgba(250, 138, 138, 1) 48%, rgba(249, 109, 109, 1) 57%, rgba(252, 97, 97, 1) 62%, rgba(250, 79, 79, 1) 67%, rgba(250, 60, 60, 1) 71%, rgba(255, 0, 0, 1) 100%);
color: white;
font-weight: bold;
font-family: courier;
font-size: 12pt;
text-shadow: black 2px -2px 1px;
padding: 0 10px 0 10px;
direction: initial;
letter-spacing: 2px;
}

div.footer-inner nav ul li:nth-child(1) ul li:nth-child(7) {
position: absolute;
left: 70px;
top: -7px;
padding: 1.1rem 1.5rem 1.1rem;
text-shadow: #e5b6ec 0px 1px;
text-transform: uppercase;
z-index: 999
}

div.footer-inner .social ul li:nth-child(1) {
position: absolute;
left: 20px;
top: -20px;
padding: 1.1rem 1.5rem 1.1rem;
z-index: 999
}

.usabilla_live_button_container {
  bottom: 10% !important;
}
}
