css:
@-moz-document url-prefix("https://www.google.co.jp/search"), url-prefix("https://www.google.co.jp/#q="), url-prefix("https://www.google.com/search"), url-prefix("https://www.google.com/#q="), regexp("https://www.google.co.jp/webhp.*q=.*"), regexp("https://www.google.com/webhp.*q=.*") {
#tvcap,
.AUiS2 /* 他の人はこちらも検索 */
{ display: none !important; }

#search .g a > h3 {
  color: #304ffe;
  font-weight: bold;
  -webkit-font-smoothing: antialiased;
}

#search .g a:visited > h3 {
  color: purple;
  font-weight: bold;
}

body,
.g {
  font-feature-settings: "palt";
  font-family: inherit;
}
}
