css:
            /* Bg-color for page and font-color for all text*/
            .b-custom-style
            {
                color: #000000 !important;
                background: #8a8a8a !important;
            }

            /* Font-color for all headers */
            .b-custom-style h1,
            .b-custom-style h2,
            .b-custom-style h3,
            .b-custom-style h4,
            .b-custom-style h5,
            .b-custom-style h6,
            .b-custom-style h1 a,
            .b-custom-style h2 a,
            .b-custom-style h3 a,
            .b-custom-style h4 a,
            .b-custom-style h5 a,
            .b-custom-style h6 a,
            .b-custom-style .b-column,
            .b-custom-style .b-post-date
            {
                color: #9ac81f !important;
            }

            /* Common blocks (begin) */
                /* Username (begin) */
                    .b-custom-style a .b-yauser,
                    .b-custom-style .b-yauser,
                    .b-custom-style .b-yauser *
                    {
                        color: #000000 !important !important;
                    }

                    .b-custom-style .b-yauser b
                    {
                        color: #ff0000 !important;
                    }
                /* Username (end) */

                /* Add to friends (begin) */
                    .b-custom-style  .b-add-to-friends td i
                    {
                        background-color: #8a8a8a !important;
                    }
                /* Add to friends (begin) */

            /* Common blocks (end) */

            /* Page head (begin) */
                .b-custom-style .b-page-head,
                .b-options-main .result .b-custom-style
                {
                    background-color: #a0a0a0 !important;
                }

                .b-custom-style .b-page-head table,
                .b-options-main .result .b-custom-style
                {
                    color: #9ac81f !important;
                }

                .b-custom-style .b-profile-menu .active .holster
                {
                    background-color: #8a8a8a !important;
                }

                 .b-custom-style .b-profile-menu .holster
                {
                    color: #000000 !important;
                    background-color: #a0a0a0 !important;
                }

                .b-custom-style .b-profile-menu .item a,
                .b-custom-style .b-profile-menu .item b
                {
                    color: #000000;
                }

                .b-custom-style .b-profile-menu .active b
                {
                    color: #9ac81f !important;
                    border-bottom-color: #8a8a8a !important;
                }

                .b-custom-style .b-profile-menu .last b span
                {
                    border-bottom-color: #000000 !important;
                }

            /* Page head (end) */

            /* Friends (begin) */
                .b-custom-style .b-column
                {
                    background-color: #a0a0a0 !important;
                }
            /* Friends (end) */
