    text-shadow: 1px 1px 1px rgba(0,0,0,0.4);
    padding: 19px;
}
/*Barre Blabla Episodes*/
.choiceText {
    font-family: Banda Light;
    font-size: 15px;
    background: linear-gradient(to right, rgba(37, 97, 97, 0.9) 0%, rgba(105, 176, 176, 0.8) 60%, rgba(233, 253, 213, 0.6) 100%);

}
/*Objectif et Historique des Actions*/
#episode-toolbox > div {
    width: 385px;
    background: rgba(118, 156, 132, 0.59);
    height: 140px;
    overflow: hidden;
    padding: 5px;
    border-radius: 4px;
}
/*Grand Titre Historique et Objectif*/
#episode-toolbox h2 {
    text-align: center;
    color: white;
    border-bottom: 1px dotted #fff;
}


/*Ici Finit: Episode en cours*/
/*Ici Commence: Armoire*/

/*?*/
.appearance-category::after, .appearance-item::after {
    border-top: none;
    border-bottom: none;
}
/*Catégories Items*/
.appearance-category, .appearance-item {
    float: left;
    cursor: pointer;
    position: relative;
    box-shadow: black 0px 0px 1px;
    background: rgba(188,230,217,0.8);
    margin: 5px;
    border-radius: 3px;
}
/*Barre Inutile en dessous item*/
.appearance-category::after, .appearance-item::after {
    border-top: none;
    border-bottom: none;
}
/*Noms Catégories Départ*/
.appearance-category-name {
    margin: 0 auto;
    font-family: "Banda Bold";
    font-size: 10px;
    line-height: 11px;
    text-transform: uppercase;
    color: #62A3A1;
    text-align: center;
}
/*Catégorie au Survol*/
.appearance-category:hover, .appearance-item:hover {
    background: rgba(178,230,151,0.8)
}
/*Item Séléctionné*/
.appearance-item.selected {
    background: rgb(178,230,151);
}
/*Nom Item*/
.appearance-item-info-name {
    text-align: center;
    font-family: 'Banda Regular';
    font-size: 15px;
    color: rgb(255, 255, 255);
}
/*Info rareté : commun*/
.appearance-item-info-rarity.rarity-common {
    color: white;
}
/*Block Info Rareté*/
.appearance-item-info-rarity {
    text-align: center;
    font-family: 'Banda Light', serif;
    font-size: 13px;
    background-image: radial-gradient(#9AD0C7,#599497);
    display: inline-block;
    padding: 5px 20px;
    margin: 5px;
    border-radius: 6px;
    border-width: 1px;
    border: 2px ridge;
    border-color: rgb(178, 230, 151);
}
/*Block Info Item*/
.appearance-items-info {
    box-shadow: #88C4BF 0px 0px 5px;
    text-align: center;
    background-color: rgba(163,213,209,0.6);
}
/*Déroulant Séléction*/
#selectedCategory {
    border: 2px ridge #AFE6E0;
    background: #AFE6E0;
}
#selectedCategory:hover {
    border-color: #AFE6E0;
}

#selectedCategory.open {
    border-color: #AFE6E0;
    background: #AFE6E0;
}


/*Ici Finit: Armoire*/
/*Ici Débute: Page de Connexion*/

/*Block Réseaux Sociaux*/
#index-social {
    background-image: radial-gradient(#C2F0EB,#61ACB0);
    border: 2px ridge #61ACB0;
    text-align: center;
}
/*Text Stat Joueurs*/
#stats-player span {
    color: #E1F9D7;
    font-size: 15px;
    text-shadow: 1px 1px 1px rgba(0,0,0,0.4);
    font-family: Banda Light;
}
/*Découvre le monde d'Eldarya Titre*/
body h2 {
    color: #2B7984;
    font-size: 15px !important;
    font-family: "Banda Bold" !important;
}
/*Texte D'intro*/
#intro {
    font-family: "Banda light";
    color: #196F7A;
    text-shadow: 1px 1px 1px rgba(0,0,0,0.4);
    font-size: 12px;
    text-align: center;
}
/*Bordure Image Accueil*/
#index-banner {
    height: 408px;
    border: none;
    border-radius: 10px;
    text-align: center;
    position: relative;
}
/*Header de Connexion*/
#header-login-form {
