css:
@-moz-document domain("oldgods.net") {
body {
    background: #000;
    /* Old browsers */

    color: #fff;
}
/* Font */
/* Light */
@font-face {
 font-family: "Roboto Condensed"; font-style: normal; font-weight: 300; src: local('Roboto Condensed Light'), local('RobotoCondensed-Light'), url("https://cdnjs.cloudflare.com/ajax/libs/ink/3.1.10/fonts/Roboto_condensed/robotocondensed-light-webfont.ttf") format('truetype'); }
@font-face {
 font-family: "Roboto Condensed"; font-style: italic; font-weight: 300; src: local('Roboto Condensed Light Italic'), local('RobotoCondensed-LightItalic'), url("https://cdnjs.cloudflare.com/ajax/libs/ink/3.1.10/fonts/Roboto_condensed/robotocondensed-lightitalic-webfont.ttf") format('truetype'); }
/* Normal */
@font-face {
 font-family: "Roboto Condensed"; font-style: normal; font-weight: 400; src: local('Roboto Condensed Regular'), local('RobotoCondensed-Regular'), url("https://cdnjs.cloudflare.com/ajax/libs/ink/3.1.10/fonts/Roboto_condensed/robotocondensed-regular-webfont.ttf") format('truetype'); }
@font-face {
 font-family: "Roboto Condensed"; font-style: italic; font-weight: 400; src: local('Roboto Condensed Italic'), local('RobotoCondensed-Italic'), url("https://cdnjs.cloudflare.com/ajax/libs/ink/3.1.10/fonts/Roboto_condensed/robotocondensed-italic-webfont.ttf") format('truetype'); }
/* Bold */
@font-face {
 font-family: "Roboto Condensed"; font-style: normal; font-weight: 700; src: local('Roboto Condensed Bold'), local('RobotoCondensed-Bold'), url("https://cdnjs.cloudflare.com/ajax/libs/ink/3.1.10/fonts/Roboto_condensed/robotocondensed-bold-webfont.ttf") format('truetype'); }
@font-face {
 font-family: "Roboto Condensed"; font-style: italic; font-weight: 700; src: local('Roboto Condensed Bold Italic'), local('RobotoCondensed-BoldItalic'), url("https://cdnjs.cloudflare.com/ajax/libs/ink/3.1.10/fonts/Roboto_condensed/robotocondensed-bolditalic-webfont.ttf") format('truetype'); }
* {
 box-sizing: border-box; font-family: 'Roboto Condensed', sans-serif; text-shadow: 1px 1px 0px #737373, 2px 3px 0px #000000; font-smooth: never !important; -webkit-font-smoothing: none !important; font-weight: 600 !important; color: #fff !important; }
.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: 'Roboto Condensed', sans-serif;
    font-weight: 400 !important;
    color: #fff !important;
}
.h2, h2 {
    font-size: 26px;
}
/* White Font */

.task-column:not(.rewards) .color-worst:not(.completed) .task-checklist-edit li > input {
    color: #fff !important;
}
text {
    fill: rgb(255, 255, 255);
}
/* Framed */
#dailiesHistorySection table,
#innerBody {
 background: linear-gradient(135deg, /*[[bga]]*/ 0%, /*[[bgb]]*/ 100%); box-shadow: inset 0px 0px 1px 1px #6c6c6c, inset 0px 0px 1px 4px /*[[br]]*/, inset 0px 0px 0px 6px #333333; border-radius: 6px;border: 0}

input#refetch,
#userApiDetailsForm fieldset label,
#userApiDetailsForm fieldset input[type="submit"],
#userApiDetailsForm fieldset label input,
#userApiDetailsForm fieldset {
    border-radius: .382em!important;
    font-size: 20px;
    border: 2px solid /*[[br]]*/ !important;
    padding: 8px;
}
input#showAllEquipment {
    border-radius: .382em!important;
    font-size: 14px;
    border: 2px solid /*[[br]]*/ !important;
    padding: 8px;
}
.task-filter li.active a {
    /* -webkit-box-shadow: 0 0 0 1px #adc0c6!important; */
    box-shadow: 0 0 0 1px /*[[br]]*/ !important;
}
hr.padded, hr {
    border: 1px solid /*[[br]]*/;
}

/** Black Glass **/

#userApiDetailsForm fieldset label input {
    background: #000 none repeat scroll 0 0;
    color: #fff;
}
.select2-container .select2-choice .select2-arrow b, .column-filter-widget select, #habitTrendsSection button {
    border: 1px solid /*[[br]]*/;
    background: #000000;
    background: linear-gradient(to bottom,/*[[bgb]]*/ 85%,/*[[bga]]*/ 99%);
}
.atm-i {
    background-color: #000;
    color: #fff;
}
/** Darker Glass **/
input[type="text"],
td.danger,
.tavern-chat li.highlight {
    background: rgba(0, 0, 0, 0.4) none repeat scroll 0 0 !important;
    color: #fff !important;
}
/** Dark Glass **/

.reward-item {
    background: rgba(0, 0, 0, 0.2) none repeat scroll 0 0;
    border: 1px solid /*[[br]]*/ !important;
}

.task-column:not(.rewards) .color-worst:not(.completed) {
    background: rgba(0, 0, 0, 0.2) none repeat scroll 0 0;
    border-top: 1px solid /*[[br]]*/ !important;
    border-right: 1px solid /*[[br]]*/ !important;
    border-left: 1px solid /*[[br]]*/ !important;
}
/** Mid Glass **/
#equipmentRecommendationsSection table tr.even, #equipmentRecommendationsSection table tr.even .sorting_1, #equipmentRecommendationsSection table tr.even .sorting_2, #equipmentRecommendationsSection table tr.even .sorting_3, #equipmentRecommendationsSection table tr.even .sorting_4, #equipmentRecommendationsSection table tr.even .sorting_5, #equipmentRecommendationsSection table tr.even .sorting_6, #equipmentRecommendationsSection table tr.odd, #equipmentRecommendationsSection table tr.odd  .sorting_1, #equipmentRecommendationsSection table tr.odd  .sorting_2, #equipmentRecommendationsSection table tr.odd  .sorting_3, #equipmentRecommendationsSection table tr.odd  .sorting_4, #equipmentRecommendationsSection table tr.odd  .sorting_5, #equipmentRecommendationsSection table tr.odd  .sorting_6,
td.safe,
table.dataTable tr.odd,
table.dataTable tr.odd td.sorting_1,
#dailiesHistorySection table th,
#taskStatisticsSection table,
input#refetch:active,
#taskOverviewSection table .odd {
    background: rgba(100,100,100,0.4) none repeat scroll 0 0;background-color: rgba(100,100,100,0.4)!important;border-bottom: 2px solid rgba(76,76,76,0.5) !important; border-top: 0px solid rgba(28,28,28,0.5) !important; border-right: 2px solid rgba(76,76,76,0.5) !important; border-left: 2px solid rgba(36,36,36,0.5) !important;color: #fff !important}
/* Light Glass */
td.neutral,
table.dataTable tr.even td.sorting_1,
table.dataTable tr.even,
#taskOverviewSection input,
input#showAllEquipment,
input#refetch,
#userApiDetailsForm fieldset input[type="submit"],
#taskOverviewSection table .sorting_1, #taskOverviewSection table .sorting_2, #taskOverviewSection table .sorting_3, #taskOverviewSection table .even {
 background: rgba(35, 35, 35, 0.4) none repeat scroll 0 0 !important; background-color: rgba(35, 35, 35, 0.4) !important; color: #fff !important}
/** Lighter Glass **/

input#refetch:hover,
#userApiDetailsForm fieldset input[type="submit"]:hover {
    background: rgba(255, 255, 255, 0.4) none repeat scroll 0 0 !important;
    background-color: rgba(255, 255, 255, 0.4) none repeat scroll 0 0 !important;
    color: #fff !important;
}
/** Clear Glass **/

#DASHBOARD li, .table.dataTable tr.odd {
    border: medium none !important;
    outline: none;
    background: rgba(0, 0, 0, 0) none repeat scroll 0 0 !important;
}
/* Colored Glass */

.dropdown-menu>.active>a:hover {
    background-color: /*[[bga]]*/;
}
.alert-info {
    background-color: #000000;
}
.customize-option:active {
    background-color: /*[[bga]]*/ !important;
    border-radius: 50%;
}
.customize-menu .locked {
    background-color: #000000 !important;
    border-radius: 50%;
}
.selectableInventory,
.badge-info {
    background-color: /*[[br]]*/ !important;
}
/** Box Shadow **/

.habits .task-actions a {
    box-shadow: inset -3px 0px 10px 2px rgba(0, 0, 0, 0.75);
}
li.showHideToggle {
    text-decoration: none;
}
/** No Border **/
#dailiesHistorySection table td:first-child,
.closer:hover,
#dailiesHistorySection table th, #dailiesHistorySection table td,
#dailiesHistorySection table th.missingDate, #dailiesHistorySection table td.missingDate {
    border: 0 solid transparent;
}
/** Form Control **/
input[type="text"],
#taskOverviewSection input{
    border: 1px solid #D0D0D0 !important;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.428571429;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}

/** Sockets **/
#DASHBOARD li > div {
    box-shadow: inset 0px 0px 60px 6px rgba(0, 0, 0, 1), 0px 0px 0px 1px rgba(0, 0, 0, 0.4);
    border-radius: 50%;
    height: 100px;
    padding: 13px 0 0;
    width: 100px;
    border: 4px ridge /*[[br]]*/;
}
.coloured {
    display: inline-block;
    width: 4.5em;
    height: 4.5em;
    padding-top: .3em;
    text-shadow: 1px 1px 1px #000000;
    border: 2px ridge #d0d0d0;
    box-shadow: inset 0 0 30px 4px rgba(0,0,0,1), 0px 0px 0px 1px rgba(0, 0, 0, 0.4);
    font-size: 0.8em;
    border-radius: 50%;
}
}
