css:
@-moz-document domain("forums.warframe.com") {
body.ipsApp {
    background: #000 url(http://i.imgur.com/DVYYiuX.png) no-repeat fixed center top;
    background-size: 100% auto;
}
div.site_limiter a img {
    content: url("http://i.imgur.com/2KQpMPi.png");
    width: 341px;
    height: 51px;
    border: 0;
    vertical-align: middle;
    box-sizing: border-box;
    padding-top: 10px;
}
#site_header,
#ipsLayout_contentWrapper,
html .ipsResponsive_showDesktop ipsResponsive_block .ipsNavBar_active ul.ipsNavBar_primary,
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsBox,
#elUserNav,
.ipsWidget.ipsWidget_horizontal .ipsWidget_title,
.ipsLayout_container div.ipsNavBar_primary.ipsClearFix,
.ipsNavBar_primary, .ipsAreaBackground, .ipsDataItem_meta.ipsType_richText,
.ipsDataItem_meta.ipsType_richText span  {
    background: transparent!important;
    color: white;
}
div#top-links {
    background: transparent;
    color: white;
    margin-bottom: 8px;
    padding-bottom: 15px!important;
    height: 121px!important;
    border-color: transparent;
}
#top-links a,
.ipsQuote .ipsQuote_citation a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last a,
.ipsType_pageTitle span, a, .ipsApp .navmenu li a, #elUserNav > li > a:not( #elRegisterButton ) {
    color: red;
}
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last a, .ipsUserPhoto {
    background: transparent;
}
.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a,
.ipsButtonBar .ipsButtonRow li > a,
.ipsButtonBar .ipsButtonRow li > button,
.ipsButtonBar .ipsButtonRow li > span {
    background: rgba(255, 255, 255, 0.1);
}
.ipsNavBar_active,
.ipsNavBar_secondary,
.ipsNavBar_primary > ul > li.ipsNavBar_active > .ipsNavBar_secondary,
.ipsNavBar_primary:not( .ipsNavBar_noSubBars) > ul:before {
    background-color: rgba(51, 51, 51, 0.4)!important;
}
.ipsNavBar_primary > ul > li > a {
    background: rgba(51, 51, 51, 0.04)!important;
}
h1.type-title {
    text-shadow: none;
    color: white;
    padding: 10px;
    margin: 0;
    font-size: 14px;
    font-weight: 400;
    position: relative;
    border-radius: 2px 2px 0px 0px;
}
.ipsDataList.ipsDataList_large.ipsDataList_zebra.ipsAreaBackground_reset {
    background: transparent;
}
.ipsDataItem.ipsDataItem_responsivePhoto.ipsClearfix {
    background: rgba(0, 0, 0, .8);
    color: white;
}

.ipsStreamItem_actionBlock .ipsType_light
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected):not( .ipsModerated):not( .ipsDataItem_new):not( .ipsDataItem_success):not( .ipsDataItem_warning):not( .ipsDataItem_error):not( .ipsDataItem_info):not( .ipsDataItem_status):nth-child(even) {
    background: rgba(255, 255, 255, .1);
    color: white;
}
#social a.social-link,
#social {
    background-color: transparent!important;
}

#tennocon-button > a > img {
    content: url(http://i.imgur.com/GxMsipi.png);
}
a.ipsType_blendLinks,
.ipsType_blendLinks a,
div.cPost_contentWrap.ipsPad p,
.ipsType_medium.ipsType_textBlock.ipsType_richText.ipsContained.is-truncated,
.ipsPageHeader h1, .ipsStreamItem_actionBlock .ipsType_light, #ipsLayout_footer a, #ipsLayout_footer p, #elCopyright a, .ipsType_pageTitle{
    color: white!important;
}

#ipsTabs_elProfileTabs_elProfileTab_field_core_pfield_1_panel, a span, .ipsMenu_item > a, .ipsMenu_item > span, div p a:hover  {
    color: red!important;
}

blockquote.ipsQuote,
.ipsPager,
.ipsComposeArea_dummy,
.ipsComposeArea_editor,
.ipsSpoiler, .ipsStyle_spoiler, .ipsAreaBackground_light,
div blockquote.ipsQuote
{
    background: rgba(31, 31, 31, 0.5)!important;
    background-color: rgba(31, 31, 31, 0.5);
    border-color: rgba(31, 31, 31, 0.8);
    color: white;
}
html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor:before, .ipsMenu_item:not( .ipsMenu_itemClicked ):not( .ipsMenu_itemDisabled ) a:hover, .ipsMenu_item[data-selected] a, .ipsMenu_item[data-selected] span {
    border-color: transparent rgba(31, 31, 31, 0.5) transparent transparent;
}
html[dir="ltr"] .ipsFollow .ipsCommentCount:after,
html[dir="ltr"] .ipsCommentCount:after {
    border-color: transparent rgba(31, 31, 31, 0.8) transparent transparent
}
.ipsQuote_citation,
.ipsButtonBar, div p {
    background-color: transparent!important;
    color: white;
}
.ipsBreadcrumb > ul:last-child > li:last-child {
    color: rgba(255, 255, 255, 0.6);
}
.ipsLikeRep,
.ipsFollow .ipsCommentCount {
    background: rgba(31, 31, 31, 0.8);
}
.ipsFollow .ipsCommentCount,
.ipsCommentCount.ipsPos_right {
    color: rgba(255, 255, 255, 0.6);
}
.ipsCommentCount.ipsPos_right,
.cForumRow_hidden .cForumTitle,
.cForumTitle, .ipsType_sectionTitle, .ipsApp .ipsNotificationCount, .navmenu li:hover {
    background: rgba(255, 255, 255, 0.3)!important;
   	box-shadow: none;
}
.ipsType_neutral,
.ipsReputation_count,
.ipsReputation_count.ipsType_positive,
.ipsApp .ipsButton_light {
    background: rgba(31, 31, 31, 0.5);
	color: white;
}
.cPost.ipsBox.ipsComment.ipsComment_parent.ipsClearfix.ipsClear.ipsColumns.ipsColumns_noSpacing.ipsColumns_collapsePhone {
    border-color: rgba(31, 31, 31, 0.8);
}
.ipsDataItem {
    border-color: rgba(255, 255, 255, 0.3);
}
#elFullNotifications_menu.ipsMenu.ipsMenu_wide.ipsHide.ipsMenu_bottomCenter,
.ipsMenu,
.ipsMenu_headerBar,
.ipsSpoiler_header,
li.ipsDataItem,
.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread):not( .ipsDataItem_selected):not( .ipsModerated),
.ipsMenu_headerBar,
.ipsMenu_footerBar, .ipsApp ul.ipsPad, .ipsApp ol.ipsPad {
    background-color: rgba(31, 31, 31, 0.8)!important;
    border-color: rgba(31, 31, 31, 0.8)!important;
    color: white;
}

p a span {color: red!important;}

li.ipsDataItem, .ipsAreaBackground_reset, p span, .cRatingColumn, div.ipsDataItem_meta.ipsType_break.is-truncated, .ipsComment, .ipsComment.ipsBox, .ipsPad, article section >*, .ipsType_normal.ipsType_richText.ipsContained >* {
	background-color: rgba(31, 31, 31, 0.8);
    background:rgba(31, 31, 31, 0.8);
    color: white!important;
}

.ipsMenu.ipsMenu_bottomCenter:before {
    border-color: transparent transparent rgba(31, 31, 31, 0.8) transparent !important;
}
.ipsMenu_headerBar,
.ipsMenu_footerBar {
    color: rgba(255, 255, 255, 1)!important;
    background-color: rgba(31, 31, 31, 0.8)!important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(31, 31, 31, 0.8)), color-stop(100%, rgba(31, 31, 31, 0.8)));
}

.ipsApp .ipsButton:not( .ipsButton_noHover ):hover, .ipsComment:not( .ipsModerated ) .ipsComment_header {
	color: rgba(255, 255, 255, 1)!important;
    background-color: rgba(51, 51, 51, 0.8)!important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(51, 51, 51, 0.8)), color-stop(100%, rgba(51,51,51, 0.8)));
}

.ipsMenu.ipsMenu_topCenter:before, .ipsMenu.ipsMenu_topCenter:after {
	border-color: #23272A transparent transparent transparent;
}

.ipsComment_content .cPost_contentWrap.ipsPad table,
.ipsComment_content .cPost_contentWrap.ipsPad tbody,
.ipsComment_content .cPost_contentWrap.ipsPad tr,
.ipsComment_content .cPost_contentWrap.ipsPad td,
div.ipsType_richText.ipsType_small table,
div.ipsType_richText.ipsType_small tbody,
div.ipsType_richText.ipsType_small tr,
div.ipsType_richText.ipsType_small td
{
    background-color: #23272A;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #23272A), color-stop(100%, #23272A));
    border: solid #2C2F33 1.0pt!important;
}
.ipsType_richText.ipsType_small,
.ipsTabs_panel,
.ipsTabs_panels.ipsTabs_contained,
.cTopicHovercard .cTopicHovercard_container,
.ipsType_reset.ipsType_normal.ipsTruncate.ipsTruncate_line,
.is-truncated,  .ipsEmbedded_content{
    background-color: rgba(31, 31, 31, 0.8)!important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(31, 31, 31, 0.8)), color-stop(100%, rgba(31, 31, 31, 0.8)));
    color: white;
    border-color: rgba(31, 31, 31, 0.8)!important;
}
.ipsTabs{
    border-color: rgba(31, 31, 31, 0.5)!important;
}
.ipsEmbedded_headerArea, #elSearch #elSearchFilter, #elSearchFilter_menu, .ipsDialog_content, .ipsDialog > div, .ipsFieldRow_section, .ipsApp select:not( [multiple] ) {
    background-color: rgba(31, 31, 31, 0.8);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(31, 31, 31, 0.8)), color-stop(100%, rgba(31, 31, 31, 0.8)));
    color: white;
}
.ipsTabs_activeItem, .ipsTabs_activeItem:hover {
    background-color: rgba(51, 51, 51, 0.8);
    color: white;
}
.ipsHovercard {
    background-color: rgba(51, 51, 51, 0.8);
    border-color: rgba(51, 51, 51, 0.8);
}
.ipsHovercard:before, .ipsHovercard_stemBottom .ipsHovercard_stem {
	border-color: transparent transparent rgba(51, 51, 51, 0.8) transparent;
}

.cPost.ipsComment .ipsType_richText ul li, .cPost.ipsComment .ipsType_richText ul li span, .ipsType_sectionHead, .ipsType_richText {
	color: rgba(255,255,255,0.9)!important;
}

#elSearch input[type="search"] {color: #99AAB5;}

.ipsSpoiler_header.ipsSpoiler_closed [data-action='toggleSpoiler']:before {
    color: red;
}

b span, strong span{
	font-weight: bold!important;
    color: white!important;
}

li.ipsWidget.ipsWidget_vertical.ipsBox:not(:first-child) {
    display: none;
}

.cForumTitle, .ipsType_sectionTitle {
    height: 28px;
    padding-top: 7px;
}

.ipsType_sectionTitle a {
    margin-top: -2px;
    margin-right: -12px;
}

.ipsMenu_title, .ipsDialog_title, .ipsItemStatus.ipsItemStatus_large {
	background: rgba(81,81,81,0.8);
    color:white;
}

/* Editor CSS */
.cke_top {
	background: rgba(255,255,255,0)!important;
}
.cke_wysiwyg_frame, .cke_wysiwyg_div {
    background-color: rgba(51, 51, 51, 0.8);
    border-color: rgba(51, 51, 51, 0.8);
    color: rgba(255,255,255,0.9);
}

.ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"], .ipsApp input[type="tel"], .cke_inner
{
	background-color: transparent;
	color: #99AAB5;
}

a.cke_button_on, a.cke_button_off:hover, a.cke_button_off:focus, a.cke_button_off:active, a.cke_button_disabled:hover, a.cke_button_disabled:focus, a.cke_button_disabled:active {
	background-color: rgba(255,0,0,20);
}

.cke_toolbar .cke_button_icon {
	filter: invert(100%)!important;
    -webkit-filter: invert(100%)!important;
}

.ipsMemberCard {
	background: inherit;
	border: 1px solid #2e2e2e
}

#elSearchWrapper .cSearchSubmit {
	color: #99AAB5;
}

.ipsAutocompleteMenu, #elSearchExpanded, #elSearchExpanded .ipsMenu_title, html[dir="ltr"] .ipsSideMenu_withChecks .ipsSideMenu_item a, html[dir="ltr"] .ipsSideMenu_withChecks a.ipsSideMenu_item, html[dir="ltr"] .ipsSideMenu_withChecks span.ipsSideMenu_item, html[dir="ltr"] .ipsSideMenu_withRadios .ipsSideMenu_item a, html[dir="ltr"] .ipsSideMenu_withRadios a.ipsSideMenu_item, html[dir="ltr"] .ipsSideMenu_withRadios span.ipsSideMenu_item, #elSearchExpanded .ipsSideMenu_list, #elSearch {
	background: #23272A;
	box-shadow: 0px 2px 10px #2c2f33cc;
	border: 1px solid #2c2f33;
}

.ipsAutocompleteMenu_item[data-selected], .ipsSideMenu_itemActive a, a.ipsSideMenu_itemActive, span.ipsSideMenu_itemActive, .ipsSideMenu_withChecks .ipsSideMenu_item > input[type="checkbox"]:checked ~ a, .ipsSideMenu_withChecks .ipsSideMenu_item > input[type="checkbox"]:checked ~ span, .ipsSideMenu_withRadios .ipsSideMenu_item > input[type="radio"]:checked ~ a, .ipsSideMenu_withRadios .ipsSideMenu_item > input[type="radio"]:checked ~ span {
	background: #2C2F33;
}

ul:not( .ipsMenu_keyNav ) .ipsMenu_item:not( .ipsMenu_itemClicked ):not( .ipsMenu_itemDisabled ) a:not( .ipsMenu_itemInline ):hover,
.ipsMenu_item[data-selected] a, .ipsMenu_item[data-selected] span, .ipsMenu_item.ipsMenu_hover {
	background: #2C2F33;
}

.ipsImage_thumbnailed {
	border: none;
}

.cProfileHeader_name {
    background-color: transparent;
    background: transparent;
}
}
