css:
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
/*quote shrink*/
.is_quote .post_title.large, .is_quote .post_title.small, .is_quote .post_title.medium, .is_quote .post_title.extra_large {
    font-size: 14px !important;
    line-height: 21px !important;
    text-indent: 0 !important;
    padding: 0px 20px !important;
    margin-left: 0 !important;
    margin-top: 13px;
}

.is_quote table {
    margin-left: 20px;
    max-width: 450px;
}

.is_quote .post_title .quote {
    font-family: 'Helvetica Neue', HelveticaNeue, Helvetica, Arial, sans-serif;
    font-weight: normal !important;
    text-indent: 0 !important;
}

.is_quote .post_title .quote p {
    margin: 10px 0 !important;
}

.post.is_quote .quote:not(.post_chrome):before, .post.is_quote .quote:not(.post_chrome):after {display:none;}
.post.is_quote .quote:not(.post_chrome) p:first-child, .post.is_quote .quote:not(.post_chrome) p:last-child {display:block}
.post.is_quote .quote:not(.post_chrome) p i, .post.is_quote .quote:not(.post_chrome) p em {font-style:italic;}
.post.is_quote .quote:not(.post_chrome) p b, .post.is_quote .quote:not(.post_chrome) p strong {font-weight:bold;}
}
