css:
@-moz-document domain(teamtreehouse.com) {

body,
header,
#container,
#account-info {
    background-color: #4B545E;
    color: #fff;
}
.secondary-heading h2,
.control-container .nav a:hover,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #fff;
}
#view,
.mixed-box .box-header,
.mixed-box .box-content,
.mixed-box .box-actions,
.mixed-box .box-alert,
.mixed-box .box-footer,
.achievement-steps {
    background-color: #3e474f;
    background: #3e474f;
    border-top: 1px solid #2E353B;
}
.mixed-box .box-header.secondary:not(.alert),
.mixed-box .box-content.secondary:not(.alert),
.mixed-box .box-actions.secondary:not(.alert),
.mixed-box .box-alert.secondary:not(.alert),
.mixed-box .box-footer.secondary:not(.alert) {
    border-top: 1px solid #2E353B;
}
.control-container .nav a.selected {
    color: #5fcf80;
}
.achievement-steps li,
nav {
    background-color: #2E353B;
    border-bottom: 1px solid #2E353B;
}
.achievement-steps li a {
    border-bottom: 1px solid #2E353B;
    color: #fff;
}
.achievement-steps li a:hover strong,
.achievement-steps li a p {
    color: #fff;
}
a {
    transition: inherit;
}
html.dashboard-controller.index-action .content-suggestions .box-header .track-header {
    border-bottom: 2px solid #2E353B;
}
.footer-list {
    border-top: 2px solid #2E353B;
}
.achievement-steps li a:hover {
    background-color: #5fcf80;
    color: #fff;
}
.achievement-steps li a strong,
.card-title strong,
.card-title h3,
.card-title .description {
    color: #fff;
    transition: color 300ms ease;
}
.card-title {
    background-color: #2E353B;
}
a.card-title:hover .description,
a.card-title:hover h3,
a.card-title:hover strong {
    color: #ccc;
}
.button.secondary:not(.has-topic-color),
.button-secondary:not(.has-topic-color),
#treehouse-workspace .template.modal .modal-body p {
    color: #aaa;
}
.button.secondary:hover:not(.has-topic-color),
.button.secondary:focus:not(.has-topic-color),
.uploader.hover .button.secondary:not(.has-topic-color),
.button-secondary:hover:not(.has-topic-color),
.button-secondary:focus:not(.has-topic-color),
.uploader.hover .button-secondary:not(.has-topic-color) {
    color: #5fcf80;
}
.card-action-button,
.card-action-info {
    border: 2px solid #fff;
}
.track .card-title:after,
.track .card-title:before,
.box,
#track-card:after,
#track-card:before {
    background: #2E353B;
}
#no-results p {
    color: #fff;
}
#cc-instructions h1#task p,
.tabs li a:hover,
.tabs li a,
.tabs li.current a,
.tabs li:hover a,
.markdown-zone h3,
.markdown-zone h4,
.markdown-zone h5,
.markdown-zone h6,
.discussion-title a,
.discussion-answer-info,
.discussion-answer-count,
.discussion-question h1,
.markdown-zone p,
.discussion-byline a:hover,
.discussion-answer .discussion-byline a,
.discussion-comment .discussion-byline a,
#container aside #sidebar-nav li.current,
.control-container .nav li.current a:hover,
#status-info div,
#treehouse-workspace .template.modal .modal-header h1,
.disclaimer p {
    color: #fff;
}
.markdown-zone code,
.markdown-zone a,
.videos-controller.show-action #video-meta span#video-authors a {
    color: #5fcf80;
}
.mixed-box .box-actions.alert.info,
.mixed-box .box-actions.alert.icons-only,
.mixed-box .box-header.secondary:not(.alert),
.mixed-box .box-content.secondary:not(.alert),
.mixed-box .box-actions.secondary:not(.alert),
.mixed-box .box-alert.secondary:not(.alert),
.mixed-box .box-footer.secondary:not(.alert) {
    background-color: #2E353B;
}
#cc-editor .editor-files,
#container aside #sidebar-nav li.current,
.control-container .nav,
.control-container.attached .search {
    background: #2E353B;
}
.control-container .nav li.current a {
    color: #7b8b8e;
}
.discussion-byline {
    color: #7b8b8e;
}
.videos-controller.show-action #video-meta span#video-authors a:hover,
.markdown-zone a:hover,
.discussion-commenting a:hover,
.discussion-title a:hover {
    color: #80E89E;
}
.button.secondary:not(.has-topic-color),
.button-secondary:not(.has-topic-color) {
    background-color: #4B545E;
}
.topic-javascript .button {
    border-color: #fff;
}
#forum-post-breadcrumb:after {
    background-image: none;
}
.form-element {
    background: #2E353B;
    border: 3px solid #4B545E;
    border-radius: 0px;
}
html.dashboard-controller.index-action .content-suggestions .box-footer .suggestion-time-estimate strong {
    color: #7b8b8e;
}
#container aside #sidebar-nav li a {
    background-color: inherit;
}
.discussion-tags:after {
    background-image: none;
}
.secondary-heading h1,
.secondary-heading h2,
.secondary-heading h3,
.secondary-heading p {
    color: #7b8b8e;
}
.avatar-white-bg .avatar-mask {
    fill: #2E353B;
}
.button.secondary:not(.has-topic-color),
.button-secondary:not(.has-topic-color) {
    background-color: inherit;
}
.control-container.attached .search .search-form .search-input input {
    border: none;
    background: #4B545E;
    color: #000;
}
.control-container.attached .search .search-form:hover .search-input input {
    border: none;
    background: #80E89E;
    color: #000;
}
.control-container .search-container,
.control-container.search {
    background-color: #2E353B;
}
.control-container .search-input input {
    border: none;
    background: #4B545E;
    color: #000;
    padding-left: 10px;
}
.control-container .search-input input:hover {
    border: none;
    background: #80E89E;
    color: #000;
    padding-left: 10px;
}
.workspace-table .top,
.workspace-table .top,
.workspace-table .bottom {
    border-bottom: 1px solid #2E353B;
    border-top: 1px solid #2E353B;
    background: #2E353B;
    border-radius: 0px;
}
.contained {
    background: #2E353B;
}
.workspace-item {
    border-bottom: 1px solid #2E353B;
    border-top: 1px solid #2E353B;
    border-left: 4px solid #2E353B;
    border-right: 4px solid #2E353B;
    background: #3e474f;
    border-radius: 0px;
}
.workspace-table .pagination .pagination-nav,
.workspace-table .pagination .pagination-nav.disabled {
    color: #4b5658;
}
.workspace-table .pagination .pagination-nav:hover,
.workspace-table .pagination .pagination-nav.disabled:hover {
    color: #576366;
}
.CodeMirror-lines,
.CodeMirror-gutter,
#treehouse-workspace #open-files-container ul li.selected a,
#treehouse-workspace.light .cm-s-light .CodeMirror-scroll,
#treehouse-workspace #editor-holder #not-editor,
#treehouse-workspace #console .toolbar .tab,
#treehouse-workspace #console .console-container,
#cc-editor .editor .CodeMirror .CodeMirror-scroll,
#cc-editor .preview .CodeMirror .CodeMirror-scroll,
#cc-editor .cm-s-solarized.cm-s-dark .CodeMirror-gutters,
#cc-editor .editor-files a.selected {
    background: #000;
    background-color: #000;
}
#treehouse-workspace.light .cm-s-light .CodeMirror-linenumber,
#treehouse-workspace #open-files-container ul li.selected a {
    color: white;
}
#treehouse-workspace #open-files-container ul,
#treehouse-workspace #open-files-container,
#treehouse-workspace #not-open-files-container,
#treehouse-workspace #console .toolbar,
.stage-progress-step-tooltip {
    background: #1b1b1b;
}
.CodeMirror-lines,
.CodeMirror-gutter,
#treehouse-workspace #open-files-container ul li.selected a,
#treehouse-workspace.light .cm-s-light .CodeMirror-scroll,
#treehouse-workspace #editor-holder #not-editor,
#treehouse-workspace #console .toolbar .tab,
#treehouse-workspace #console .console-container {
    border-right: 2px solid #222;
}
#treehouse-workspace #open-files-container ul,
#treehouse-workspace #open-files-container,
#treehouse-workspace #not-open-files-container,
#treehouse-workspace #console .toolbar {
    border-bottom: 2px solid #222;
    box-shadow: none;
}
#treehouse-workspace .main-view .content,
#treehouse-workspace .template.modal,
#treehouse-workspace .template.modal .modal-header,
#treehouse-workspace .template.modal .modal-body,
#treehouse-workspace .template.modal .modal-footer,
.achievement-hero,
.current-syllabus-hero,
.stage-progress-step-tooltip:before {
    background: #333;
}
#treehouse-workspace #open-files-container,
#treehouse-workspace #not-open-files-container {
    box-shadow: none;
}
#treehouse-workspace #status-bar {
    background: #222;
    background-color: #222;
    color: #fff;
    border-top: 1px solid #333;
}
#treehouse-workspace #status-bar #status-indicators #status-language,
#treehouse-workspace #status-bar #status-indicators #status-indent {
    color: #555;
}
#treehouse-workspace #sidebar,
#treehouse-workspace #open-files-container ul li a,
.content-meta {
    background: #222;
}
#treehouse-workspace #open-files-container ul li {
    border-right: 2px solid #222;
}
#treehouse-workspace .button:hover,
#treehouse-workspace .action:hover,
#treehouse-workspace .btn:hover {
    background: #5DA371;
}
#treehouse-workspace .button,
#treehouse-workspace .action,
#treehouse-workspace .btn,
.disclaimer {
    background: #539165;
    border: none;
}
#treehouse-workspace #titlebar .dropdown-menu,
#treehouse-workspace #titlebar .util-menu {
    background: #555;
    border: 1px solid #333;
}
#treehouse-workspace #titlebar .dropdown-menu a,
#treehouse-workspace #titlebar .util-menu a {
    color: #fff;
}
.dashboard-controller .total-points h1 {
    color: #aaa;
}
.content-meta h3 {
    color: #eee;
}
.stage-progress-step-icon {
    fill: #444;
}
#loading-screen {
    background: #333;
}
.CodeMirror-lines,
.CodeMirror-gutter,
#treehouse-workspace #open-files-container ul li.selected a,
#treehouse-workspace.light .cm-s-light .CodeMirror-scroll,
#treehouse-workspace #editor-holder #not-editor,
#treehouse-workspace #console .toolbar .tab,
#treehouse-workspace #console .console-container {
    border: none;
}
#treehouse-workspace #open-files-container ul,
#treehouse-workspace #open-files-container,
#treehouse-workspace #not-open-files-container,
#treehouse-workspace #console .toolbar {
    border-bottom: none;
}
#treehouse-workspace .modal-bar {
    background: #555;
    border: none;
}
.discussion-answer-best {
    background: #2E353B;
    border: none;
}
    
}
