css:
@-moz-document domain("hammockforums.net") {
* {
    box-shadow:none!important;
    border-radius:0!important;
}
html,body,.above_body,.navbar,.navtabs,.navtabs li a.navtab{
    background-color:#333!important;
    background:#333!important;
    color:white!important;
    box-shadow:none!important;
}

.breadcrumb .navbit.lastnavbit {
    color:white;
}

form#quick_reply textarea {
    background:white!important;
    color:#333!important;
}

.body_wrapper {
     background-color:#333!important;
}

a {
    color:orange!important;
}
#MYCOMMENTNOSHADOW,.thread_info h4,.thread_info .blockbody,.footer,.newcontent_textcontrol,.threadlisthead,.threadbit,.forum_info .blockbody,.forum_info .blockhead,#sidebar_container.member_summary{
     box-shadow:none!important;
}
#MYCOMMENTBORDER,.threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored,.threadbit .sticky,.postbit, .postbitlegacy, .eventbit,.postbitlegacy .postbody, .eventbit .eventdetails .eventbody,.navlinks,.toolsmenu,.thread_info .blockbody,.thread_info h4,div#sidebar_container.member_summary,#sidebar_container .blockbody,.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    border-color:#555!important;
}

.postbitlegacy .postdetails, .eventbit .eventdetails,.postbitlegacy .userinfo {
    background:#444!important;
}

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody,.threadbit .sticky,.threadbit .nonsticky,.threadbit .alt,.thread_info .blockbody,li.userprof_content,div#sidebar_container.member_summary,.moduleinactive_bg,#sidebar_container .blockbody,#sidebar_container .userprof_content .blockrow,.bppostbit,.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
	background:#555!important;
    color:white!important;
}

#MYCOMMENTWHITETEXT,.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow,.above_postlist .pagination_top .postpagestats,#pagetitle,.newcontent_textcontrol,.time,.threadlisthead a,.postbitlegacy dl.userinfo_extra dt, .postbitlegacy dl.user_rep dt,.forumhead,.forumbit_nopost .forumhead .forumtitle a {
    color:white!important;
}

#MYCOMMENTTRANSPARENT,.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols,.toolsmenu,.navlinks,.userprof_headers_border {
    background:transparent!important;
}

#MYCOMMENTBORDERREMOVER,.footer,.bppostbit {
	border:none!important;
}
#MYCOMMENTORANGEBG,
.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead,.blockhead,.thread_info h4, .footer,.newcontent_textcontrol,.threadlisthead,.forumhead {
	background:orange;
}

.bbcode_container div.bbcode_quote {
    background:#999;
}
}
