css:
@-moz-document domain("zismo.biz") {
* {
	font-family: tahoma !important;
}

body {
	font: 12px, Tahoma !important;
}

#logo {
    width: 52px !important;
    height: 44px !important;
    padding: 0 !important;
    margin: 17px 20px 0 20px;
}

#logo a {
    width: 52px !important;
    height: 44px !important;
    display: block;
    background: url(http://zismo.biz/Images/zm_logo.png) !important;
}

#logo img {
	display: none !important;
}
 
#user_navigation.logged_in {
    padding-top: 25px !important;
}

#primary_nav {
	background: url(http://zismo.biz/forumvk/forum_panel.png) repeat-x !important;
    border-top: 0 !important;
    height: 32px !important;
    padding-top: 6px;
}

#quickNavLaunch {
	display: none !important;
}

#primary_nav a {
    font-size: 12px;
    color: #006588 !important;
    border-bottom: solid 1px #8daebc;
    text-decoration: none;
    padding: 0 1px 0 1px !important;
    display: inline-block !important;
    margin: 0 10px !important;
    font-weight: normal !important;
}

#primary_nav a span {
	color: inherit !important;
}

#user_navigation li {
	background: url(http://zismo.biz/Images/preal.gif);
    border: 0 !important;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
    position: static !important;
}

#user_navigation.logged_in li.username {
    height: 25px !important;
    margin-right: 2px !important;
}

#user_navigation.logged_in li.username a {
	line-height: 25px !important;
}

#user_link {
	font-size: 12px !important;
    color: #146580 !important;
    padding: 0 18px !important;
}

#user_link img {
	display: none;
}

#inbox_link {
	background: url(http://zismo.biz/Images/profile_icons.png) no-repeat 50% 16px;
} 

#notify_link {
	background: url(http://zismo.biz/Images/profile_icons.png) no-repeat 50% -3px;
}

#logout_link {
	background: url(http://zismo.biz/Images/profile_icons.png) no-repeat 50% -24px;
}

#user_link_menucontent {
	left: 92px !important;
    top: 59px !important;
}

#content {
	background: #fff;
}

#content > div[align="center"],
#content > br,
.ipsAd123,
.nexus123ad,
#GrpEv_message,
.__like.right[data-app="forums"],
.forum_rules {
	display: none !important;
}

#content > .category_block.block_wrap {
	display: none;
}

#content > .ipsType_pagetitle {
	position: absolute;
    top: 205px;
    text-align: center;
    color: #fff;
    font-size: 12px !important;
    font-weight: bold;
    width: 100%;
    z-index: 2;
}

.ipsFilterbar.maintitle .ipsList_inline {
	display: none;
}

.maintitle {
	background: url(http://zismo.biz/forumvk/gtabletop.png);
    height: 38px !important;
    box-sizing: border-box;
	border-radius: 0 !important;
	text-align: center;
	font-size: 12px !important;
	font-weight: bold;
	padding-top: 12px;
}

.ipsFilterbar.maintitle {
	position: relative;
}

.post_wrap .row2 {
	background: #fff !important;
	font-size: 12px !important;
	height: 15px !important;
	padding: 5px 0 !important;
	line-height: normal !important;
}

.post_wrap .row2 .author {
	width: 326px !important;
	font-size: 12px !important;
	color: rgb(81, 82, 83) !important;
	display: block;
    position: absolute;
    top: 0px;
	border-right: 1px solid #ccc;
	height: 25px;
}

.post_wrap .row2 .author a {
	position: relative;
	top: 6px;
}

.online {
	position: absolute;
	left: 283px;
	top: 2px;
}

.row2 .post_id {
	float: none;
	margin-left: 327px;
	font-weight: normal;
}

.row2 .post_id::before {
	content: "Пост ";
}

.row2 .post_id a img {
	display: none;
}

.row2 .right {
	float: none;
}

.row2 .published {
	background: #fff;
	margin-left: -89px;
	padding: 3px 0;
	color: rgb(81, 82, 83) !important;
	font-weight: normal !important;
}

.row2 .published::before {
	content: "написан ";
}

.post_body {
	padding-left: 0 !important;
	margin-right: 0 !important;
}

.post_body .entry-content {
	padding-left: 10px !important;
	font-size: 11px !important;
}

ul.pages,
ul.forward,
ul.back {
    line-height: 29px;
}

.pagejump {
    background: #1FA0DD !important;
    padding: 0 10px !important;
    font-size: 8pt !important;
    height: 29px !important;
    float: left;
}

.pagejump a {
    color: #fff !important;
    font-weight: normal !important;
}

.page,
.next,
.last,
.back {
	background: #f4f4f4 !important;
    min-width: 15px !important;
    font-size: 8pt !important;
    border-radius: 0 !important;
    padding: 0 !important;
    height: 27px !important;
    border: #1FA0DD solid 1px;
    float: left;
    margin-left: -2px !important;
}

.page a,
.next a,
.last a,
.back a {
	min-width: 15px !important;
    padding: 0 2px !important;
    height: 100% !important;
    color: #000 !important;
    font-weight: normal !important;
    display: inline-block !important;
	background: #f4f4f4 !important;
}

.page a:hover,
.next a:hover,
.last a:hover,
.back a:hover {
	border: 0 !important;
}

.page.active {
    font-weight: bold !important;
    color: #666 !important;
    padding: 0 2px !important;
}

.nav_sep {
    display: none;
}

.breadcrumb li::after {
    display: inline;
    content: "»";
}

.breadcrumb li:last-child::after {
	display: none;
}

.breadcrumb li a {
    color: #23699f;
    text-decoration: underline;
    padding-right: 5px;
}

.topic_elements {
	background-image: url(http://zismo.biz/forumvk/gtabletop.png) !important;
	background-color: transparent !important;
	border-radius: 0 !important;
	border: 0 !important;
    height: 38px !important;
    box-sizing: border-box;
	overflow: hidden;
}

.topic_elements_top {
	position: relative;
	top: 100px;
}

.topic_elements_top .ipsUserPhotoLink {
	display: none !important;
}

[itemprop="name headline description"] {
	position: absolute;
	z-index: 1;
	top: -106px;
	width: 100%;
	text-align: center;
    font-size: 12px !important;
    font-weight: bold;
    width: 100%;
    z-index: 2;
	color: #fff !important;
}

.topic_controls {
	
}

.topic_buttons .non_button {
	display: none;
}

.topic_buttons [title="Новая Тема"] {
	background: url(http://zismo.biz/Azam/t_new.gif);
    width: 71px !important;
    min-width: 0 !important;
    padding: 0 !important;
    text-indent: 2000% !important;
    height: 18px !important;
    overflow: hidden;
    margin-top: 8px;
}

.__like  {
    display: none;
}

.ipsBox_container {
	border: 0 !important;
}

.ipb_table td {
	border: 1px solid #ccc !important;
	background: #fff !important;
	border-collapse: collapse;	
}

.ipb_table h4 a {
	color: #3394ca !important;
	font-size: 10pt !important;
}

.ipb_table * {
	font-size: 12px !important;
}

.ipb_table .desc {
	color: #000;
	font-size: 9px !important;
	font-weight: normal !important;
}

.col_c_stats {
	text-align: center;
	color: #aaa !important;
}

.col_c_stats strong {
	text-shadow: none !important;
	color: #aaa !important;
	font-weight: normal !important;
}

td[style="padding: 5px 0px 5px 0 !important; width: 1px !important;"],
.col_f_preview {
	display: none;
}

.author_info {
	border-left: #ccc solid 1px;
}

.basic_info {
	width: 174px !important;
	overflow: hidden;
}

.basic_info li {
	border: 2px solid #d9e0e9;
	position: relative;
	line-height: normal !important;
	padding: 0 !important;
	font-size: 11px !important;
	white-space: nowrap;
	padding: 5px !important;
	background-image: none !important;
}

.basic_info li .right {
	position: absolute;
	right: 0px;
	width: 80px;
	color: #7997b7 !important;
}

.basic_info li::before {
	display: inline-block;
	color: #7997b7;
	font-size: 11px !important;
	width: 70px;
	margin-right: 10000px;
}

.basic_info li::after {
	display: block;
	width: 160px;
	height: 15px;
	background-image: url(http://zismo.biz/mono_iconset.gif) !important;
	background-repeat: no-repeat;
	content: "";
	position: absolute;
	top: 6px;
	right: 5px;
	border-bottom: 2px solid #eff2f5;
}

.post_count::before {
	content: "Сообщений: ";
}

.post_count::after {
	background-position: 100% -193px;	
}

.points_count::before {
	content: "Поинты: ";
}

.points_count::after {
	background-position: 100% -119px;	
}

.warn_count::before {
	content: "Замечания: ";
}

.warn_count::after {
	background-position: 100% -264px;	
}

.online_count::before {
	content: "Онлайн: ";
}

.online_count::after {
	background-position: 100% -207px;	
}

.post_count {
	border-bottom: none !important;
	background-position: 150px 100px;
}

.points_count,
.warn_count {
	border-top: none !important;
	border-bottom: none !important;
}

.online_count {
	border-top: none !important;
	border-bottom: none !important;
}

.friends_count {
	display: none;
}

.rep_container {
	background: transparent !important;
	padding: 5px !important;
	height: auto !important;
	text-align: left !important;
	border: 2px solid #d9e0e9;
	border-top: none;
	position: relative;
}

.rep_container::before {
	display: inline-block;
	content: "Репутация: ";
	width: 70px;
	color: #7997b7;
	font-size: 11px !important;
}

.rep_container::after {
	display: block;
	width: 160px;
	height: 15px;
	background-image: url(http://zismo.biz/mono_iconset.gif) !important;
	background-repeat: no-repeat;
	content: "";
	position: absolute;
	top: 6px;
	right: 5px;
	background-position: 100% -178px;
}

.rep_container .tCard {
	color: #7997b7;
	text-shadow: none !important;
	font-weight: normal !important;
	font-size: 11px !important;
}

.rep_container > div {
	background: transparent !important;
	position: absolute;
	top: 2px;
	right: 0;
	width: 80px !important;
}

.rep_container > div > div:not(.tCard) {
	display: none;
}

.member_title {
	border: 2px solid #d9e0e9 !important;
	border-radius: 0 !important;
	padding: 5px 10px !important;
	box-sizing: border-box;
	line-height: normal !important;
	font-size: 11px !important;
	color: #000 !important;
	position: static !important;
	margin-bottom: 5px !important;
	width: 174px !important;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    box-sizing: border-box;
}

a[title="Прокачать профиль!"] {
	display: none;
}

#footer_utilities {
	background: url(http://zismo.biz/forumvk/fonniz.png) repeat-x;
	padding-top: 20px;
}

#footer_utilities a {
	font-size: 11px !important;
}

.avatar > a::after {
	width: 150px;
    height: 35px;
    display: block;
    content: "";
    position: absolute;
}

.citation {
	border-width: 1px !important;
    border-style: solid !important;
    border-color: rgb(234, 244, 248) !important;
	padding: 0 0 0 5px !important;
	font-size: 7pt !important;
	background: #fff !important;
}

.ipsBlockquote {
	font-size: 11px !important;
    background: rgb(245, 247, 248) !important;
    padding: 15px !important;
    border-left: 8px solid rgb(185, 204, 213) !important;
    border-top: 1px solid rgb(185, 204, 213) !important;
    border-bottom: 1px solid rgb(185, 204, 213) !important;
    border-right: 1px solid rgb(185, 204, 213) !important;
}

.post_controls {
	background: #f8f8f8;
	border-top: 1px solid #d9e0e9;
	padding: 5px;
}

.post_controls > .ipsPad_top_bottom_half {
	position: absolute;
}

.post_controls li,
.ipsLikeBar1 li {
	margin-right: 4px;
    border-width: 1px;
    border-style: solid;
    border-color: rgb(196, 196, 196);
    border-radius: 2px;
}

.post_controls li a:not(.rep_up):not(.rep_down) {
    cursor: pointer !important;
    display: inline-block !important;
    border-top-color: rgb(124, 152, 186) !important;
    border-bottom-color: rgb(94, 128, 169) !important;
    border-left-color: rgb(94, 128, 169) !important;
    border-right-color: rgb(94, 128, 169) !important;
    color: rgb(51, 51, 51) !important;
    text-shadow: rgb(255, 255, 255) 0px 1px 0px !important;
    font-size: 11px !important;
    border-radius: 1px !important;
    background: url("http://zismo.biz/button_vk.png") 0px -54px repeat-x rgb(97, 129, 166) !important;
    background-position: 0px -54px !important;
    padding: 5px 10px !important;
	opacity: 1 !important;
	height: auto !important;
	line-height: normal !important;
}

a[title="Просмотр профиля: nikitas244"]::after {
    background: url(http://zismo.biz/plan_ru/icon/block.png);
}

.likeBar1 li {
	display: block !important;
}

.likeBar1 li:first-child {
	position: absolute;
    left: 270px;
	top: 173px;
	border: none !important;
	float: none !important;
	margin-right: 20px;
}

.likeBar1 li:nth-child(2) {
	display: list-item !important;
	position: absolute;
    left: 285px;
	top: 173px;
	border: none !important;
	float: none !important;
}

.likeBar1 li:first-child::before {
	content: "[";
}

.likeBar1 li:nth-child(2)::after {
	content: "]";
}

.rep_up,
.rep_down {
    width: auto !important;
    height: 10px !important;
    overflow: hidden !important;
    padding-left: 0 !important;
    padding-top: 0 !important;
    padding-right: 0 !important;
    padding-bottom: 0 !important;
    background: #fff !important;
	font-size: 10px !important;
	opacity: 1 !important;
	float: none !important;
	display: inline-block;
}

.rep_up::after {
	content: "+";
}

.rep_down::after {
	content: "/ -";
}

.rep_up img,
.rep_down img {
	display: none;
}
}
