css:
@-moz-document url("https://chat.scrap.tf/chat") {
h2 {
    color: #434343;
}

.chat-question, .chat-welcome {
    color: #434343;
}

.chat-welcome a:not(.btn) {
    color: #4285F4 !important;
}

.chat-tabs {
    background-color: #0398DC;
}

.chat-txt, .chat-entry, .chat-header {
    background-color: #03A9F4;
}

body, .sidebar-hide, .main-hide, .chat-message, .yt-video-box {
    background-color: #F7F9FA;
    color: #686868;
}

.dropdown-menu li, .dropdown-menu li a, a span {
    color: #686868 !important;
}

.chat-message {
    font-weight: 100;
    font-size: 125%;
}

.chat-message a {
    font-weight: 500 !important;
}

li a, .dropdown a {
    color: #F9F9F9 !important;
    font-weight: 400;
}

.chat-message a, .sidebar-name span {
    /*	text-shadow: 0 0 3px #ddd; /* */
    
    font-weight: 100;
    color: #686868 !important;
}

.sidebar-category, .user-list-items .dropdown-header {
    font-weight: 400;
    font-size: 125%;
}

.chat-tabs .fa, .chat-txt {
    color: #F9F9F9 !important;
}

input {
    -webkit-text-fill-color: #F9F9F9;
    font-weight: 100;
}
}
