css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.yourtv.com.au/guide/),
               url-prefix(http://www.yourtv.com.au/personal_guide/),
               url-prefix(http://hww.yourtv.com.au/guide/)
{
  #hd_1,
  #TVFIX_HEADER,
  #guide-regions-menu,
  #grid > H1,
  #pay-region-add,
  #nav-footer,
  .grid-ad,
  .FIXBOTTOM_1,
  .FIXBOTTOM_2,
  .FIXBOTTOM,
  #nw_ft_wrap,
  .region-end,
  .venue-company-divider,
  .venue[rowspan='2'],
  .venue-right,
  SPAN.btn,
  #hd_main,
  TD.banner-td,
  #ninemsn-global-header,
  #ninemsn-global-footer
    { display: none !important; }


  #grid td
    { padding: 1px !important; }

  #wrapper1col,
  .wrapper
    { width: 100% !important; }

  #guide > #grid > table {
    margin: 0 !important;
    width: 100% !important;
    }

  .ptime {
    display:   inline;
    margin:    0 !important;
    font-size: 7.5px !important;
    }

  .pname,
  .pinfo,
  .ptime + DIV
    { display: inline !important; }

  #grid TD.venue,
  #grid TD.venue-name,
  #grid TD.region-menu
    { padding: 0 !important; }

  .highlight {
    width: 50px !important;
    display: inline-block !important;
    }


  #grid tbody
    { display: none !important; }
  #grid tbody.FTA,
  #grid tbody#FTA-header
    { display: table-row-group !important; }

  #main > BR
    { display: none !important; }

  html body,
  .wrapper
    { padding: 0 !important; }

  .time-header > TD
    { line-height: 12px !important; }

  /* make grid take up the whole width & height */
  #main {
    width: 100% !important;
    max-width: none !important;
    }
  
  #guide {
    height: auto !important;
    overflow-x: auto !important;
    overflow-y: auto !important;
  }

  /* force colours so highlighting doesn't dim events */
  TD.event-dim .pname A
    { color: #287300 !important; }
  .ptime
    { color: #666666 !important; }
  .pinfo
    { color: #616161 !important; }

  #grid td .catch-up-link
    { display: none !important; }


  /* Make Time headers stand out more */
  TR.time-header TH
    { font-weight: bold !important;
      font-size: 12px !important; }
  TR.time-header TH.time2
    { background: grey !important; }

  /* Highlight movies */
  #grid DIV.movie DIV
    { background-color: rgb(255, 249, 189);
      background-image: none; }
   
  /* Highlight interesting shows */
  #grid DIV.interesting
    { background-color: rgb(255, 228, 175);
      background-image: none; }
   
   
  /* Hide new header & background crap */
  .newHeaderWrapper
    { display: none !important; }
  .inner-page,
  BODY
    { background: none !important; }
   
  
  /* Change some colours back */
  #grid td.event-highlight
    { background-color: rgb(211, 249, 188); }
  
  
  /* Hide Setup/Highlight bar */
  .region-menu
    { display: none !important; }
   
  /* Reduce height of tabs/table header */
  #grid .day-nav li, #grid .btn-nav li
    { padding: 1px 5px !important; }
  #grid .day-nav .tab-mid a, #grid .btn-nav .tab-mid a
    { font-size: 13px !important; }
  #grid tr.time-header th
    { padding: 1px 6px !important; }
  
  
  /* Add channel labels */
  /*  trying images again
  .venue-name::after
    { font-size: 12px;
      margin-top: 0 ! important; }

  TR[data-channel="ABC"]         .venue-name::after { content: "7HD"; }
  TR[data-channel="ABC2 / KIDS"] .venue-name::after { content: "ABC2"; }
  TR[data-channel="7HD"]         .venue-name::after { content: "7"; }
  TR[data-channel="7TWO"]        .venue-name::after { content: "7two"; }
  TR[data-channel="7mate"]       .venue-name::after { content: "7mate"; }
  TR[data-channel="7flix"]       .venue-name::after { content: "7flix"; }
  TR[data-channel="9HD"]         .venue-name::after { content: "9"; }
  TR[data-channel="9Gem"]        .venue-name::after { content: "9Gem"; }
  TR[data-channel="9Go!"]        .venue-name::after { content: "9Go!"; }
  TR[data-channel="9Life"]       .venue-name::after { content: "9Life"; }
  TR[data-channel="TEN HD"]      .venue-name::after { content: "TEN"; }
  TR[data-channel="ELEVEN"]      .venue-name::after { content: "11"; }
  TR[data-channel="ONE"]         .venue-name::after { content: "ONE"; }
  TR[data-channel="SBS HD"]      .venue-name::after { content: "SBS"; }
  TR[data-channel="SBS 2"]       .venue-name::after { content: "SBS2"; }
  TR[data-channel="Channel 31"]  .venue-name::after { content: "C31"; }
  */
  
  /* Shrink Channel text size & reduce column width */
  #grid TR.time-header TH.region
    { font-size: 8px !important; }
  #grid TD.venue
    { width: 1px; }
  
  /* reduce size of Channel images */
  #grid TD.venue .venue-name IMG
    { max-width: 40px; }


}
