css:
@-moz-document domain(youtube.com) {
  
/* Hiệu ứng trong suốt */  
  :not(.ytp-play-progress) {background-color:transparent!important}
  
/* Nền */
  html {background:url(http://i.imgur.com/Flan7Yr.jpg) fixed!important; background-size:100% 100%!important}
  
/* Hạ độ sáng nền */  
   html:after {content:"";position:fixed;top:0;bottom:0;left:0;right:0;background:#000;z-index:-2;opacity:.38}
  
/* Fix những chỗ trong suốt khó đọc text */  
#appbar-guide-menu, .gssb_e, #masthead-positioner , .yt-uix-clickcard-card-border, .yt-ui-menu-content, .ytp-popup {background: rgba(0,0,0,0.8) !important}  
  
/* Khung trong suốt */
 #watch-header,  .watch-sidebar  , [class*="yt-uix-load-more comment-section"]  {background: rgba(255,255,255,0.05) !important}
  
.ytp-play-progress{background: #00BBFF!important}
.ytp-progress-list {box-shadow: 0px 0px 3px rgba(255,255,255,0.5) !important}

  
/* Màu chữ */
a , :not(FONT) {color:rgba(255,255,255,0.8) !important}   
.yt-uix-tile-link {color : white !Important}

/* Autohide */
#masthead-positioner:not(:hover) {opacity: 0 !important; transition: 0.5s !important}

/* Màu nút */  

.like-button-renderer-like-button:before {
	background: no-repeat url(http://i.imgur.com/RzNYc5b.png);
	background-size: auto;
	width: 20px;
	height: 14px;
}
.like-button-renderer-like-button.yt-uix-button:active:before,
.like-button-renderer-like-button.yt-uix-button.yt-uix-button-toggled:before {
	background: no-repeat url(http://i.imgur.com/GqJ6Rh8.png);
	background-size: auto;
	width: 20px;
	height: 15px;
}
.like-button-renderer-like-button:active .yt-uix-button-content,
.like-button-renderer-like-button.yt-uix-button-toggled .yt-uix-button-content {
	color: #8653D3;
}
.like-button-renderer-dislike-button:before {
	background: no-repeat url(http://i.imgur.com/3HNu6pa.png);
	background-size: auto;
	width: 20px;
	height: 12px;
	padding-bottom: 1px;
}
#comment-section-renderer .sprite-like:before {
	background: no-repeat url(http://i.imgur.com/RzNYc5b.png);
	background-size: auto;
	width: 14px;
	height: 14px;
}
#comment-section-renderer .sprite-like-dislike:before {
	opacity: 3.2;
}
#comment-section-renderer .sprite-dislike:before {
	background: no-repeat url(http://i.imgur.com/3HNu6pa.png);
	background-size: auto;
	width: 14px;
	height: 14px;
}
#comment-section-renderer .comment-renderer:hover .sprite-like-dislike:before {
	opacity: 106.4;
}
#comment-section-renderer .sprite-like[data-action-on]:before {
	background: no-repeat url(http://i.imgur.com/GqJ6Rh8.png);
	background-size: auto;
	width: 14px;
	height: 14px;
}
#comment-section-renderer .sprite-dislike[data-action-on]:before {
	background: no-repeat url(http://i.imgur.com/mqJYfxz.png);
	background-size: auto;
	width: 14px;
	height: 14px;
}

   
}
