css:
@-moz-document domain("dagbladet.no") {
body {
    font: 12px arial, sans-serif;
    *font-size: small;
    *font: x-small;
    background-color: #2f2f2f !important;
    color: #f3f3f3;
}

main {
    background-color: #2f2f2f !important;
}
article.preview .headline.black, article.preview .headline .black {
    color: #F3F3F3;
}
#doc {
    background: #2f2f2f !important;
}

.ref h2 a:link,
.ref h2 a:visited {
    color: #e3e3e3 !important;
    text-decoration: none;
}

.related_items div a {
    color: #f3f3f3 !important;
    text-decoration: none;
}

#webTitleSpan {
    text-transform: uppercase;
}

.back.gray {
    background-color: #474747 !important;
}

a:link {
    color: #808080;
}

a:visited {
    color: #808080;
}

.back.white {
    background-color: #5E5E5E;
}

.ref {
    border: none;
}

.plussbox_container {
    display: none;
}

#dzBottom {
    display: none;
}

p.image-caption b,
p.image-caption strong {
    color: #fff;
}

p.image-caption {
    color: #B8B8B8;
}

#webTitleSpan {
    color: #fff;
}

.article-body #editableBodyText {
    color: #B8B8B8;
}

.article-body h2 {
    color: #fff;
}

.xtop,
.xbottom {
    background: transparent;
}

.l-gray .xb1,
.l-gray .xb2,
.l-gray .xb3,
.l-gray .xb4 {
    background: transparent;
}

.back.yellow {
    background-color: #474747;
}

.back.pink {
    background-color: #474747;
}


/* Version 0.1 */


/* Fikset Dagbladet Meninger */

#page > .row {
    background-color: #464646 !important;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #fff !important;
}

.article-byline .article-byline-name {
    color: #fff;
}

.main-article .main-article-content {
    color: #B8B8B8;
}

.main-article .main-article-content strong {
    color: #fff;
}

.section-title h4 {
    background-color: #464646;
}

#disqus_thread {
    background-color: #EAEAEA;
    padding: 15px;
}

a.mainUrl {
    display: none;
}

#menuwrapper {
    display: none;
}

#pluss-banner-container {
    display: none;
}

/* Version 0.2 */
.article-body .tool-box .article-tools {
    background-color: #676767;
    border-top: none;
}
/* Version 0.2.1 */
.has-fullscreen>.row {
    background-color: #2f2f2f;
}
/* Version 0.2.2 */
.has-fullscreen article {
    background: #2f2f2f;
    z-index: 100;
}
/* Version 0.2.3 */
.native-advertisement{
    display:none;
}
/* Version 0.2.4 */
.horizontal-scroller{
    background-color:#2f2f2f;
}
.horizontal-scroller .inner ul.list li a .title, .horizontal-scroller .inner ul.list li a .text {
    color: #bfbfbf;
}
}
