css:
@-moz-document domain("tv2.no") {
body {
    background: #2f2f2f;
}
.tv2-content {
    background-color: #2f2f2f;
}
.tv2-story a {
    color: #fff;
}
.bg-grey-light, .bg-grey-light .header-with-border span {
    background-color: rgba(255, 255, 255, 0.20);
}
.header-with-border span {
    color: #fff;
    background-color: rgba(255, 255, 255, 0.2);
}
.tv2-videoList-contentbox .contentbox-container .videolist .videobox {
    background-color: #4d4d4d;
}
.tv2-videoList-contentbox .contentbox-container .videolist .videobox article.media .video-metadata h3 {
    color: #FFF;
}
.tv2-videoList-contentbox .contentbox-container .videolist .videobox article.media .video-metadata .views {
    color: #ECECEC;
}
.tv2-videoList-contentbox .contentbox-container a.seeMore {
    background-color: #4d4d4d;
    color: #fff;
}
#tv2-footer .colophon {
    background: #5D5D5D;
    color: #fff;
}
#header .header-stripe {
    background-color: #4d4d4d;
}
#header .search-box {
    background-color: #4d4d4d;
}
#header #top-logo ul li:first-child a {
    background: #4d4d4d;
}
div.program-banner {
    background-color: #4d4d4d;
}
.tv2-ad.broom_articletop_ad_wrapper, .tv2-ad.broom_forside_top_ad, .tv2-ad.tv2-ad-topbanner, .tv2-ad.tv2-ad-topbanner-a, .tv2-ad.tv2-ad-topbanner-f {
    display: none;
}
div.program-banner {
    background-color: #4D4D4D;
}
.txt-red {
    text-shadow: 1px 1px 1px #000;
}
#header #top-logo ul li a {
    color: #EBEBEB;
}
#header #header-menu ul li.header-nav-item a:hover, #header #toggle ul li.header-nav-item a:hover {
    color: #ebebeb;
}
#header #header-menu ul li a, #header #toggle ul li a {
    color: #A0A0A0;
}
/* artikkel */
.bodytext {
 	color: #B8B8B8;
    font-family: 'Helvetica Neue',Arial,freesans,sans-serif;
    font-size:16px;
}
.byline {
    background-color: #4d4d4d;
    color: #fff;
}
.dateline-container {
    display:none;
}
.tv2-article .article-head p.subtitle {
    color: #fff;
}
.path ol li a {
    color: #fff;
}
.path ol li:before {
    color: rgba(255, 255, 255, 0.4);
}
.tv2-article .article-head h1.title {
    color: #fff;
}
/* Version 0.2 - 21 September 2016 */
.row {
    background-color: #2f2f2f;
}
/* Version 0.2.1 - 22 October 2016 */
.tv2-content>.row:not([class*=bg-]) {
    background-color: #2f2f2f;
}
.tv2-content {
    background-color: #2f2f2f !important;
}
.tv2-ad-fullscreen-f {
    display: none;
}
}
