@-moz-document domain("vg.no") {
body {
    background-color: #2F2F2F;
    color: #fff;
}
div.reg-grid-container > *:not(div):not(a):not(header):not(footer) {
    display: none !important;
}
.reg-grid-container {
    background-color: #2F2F2F;
}
.desktop #main-nav-bar,
.tablet #main-nav-bar {
    margin-top: 0px;
    background-color: transparent;
}
.drfront h3,
.drfront h3 a,
.drfront .insert-title,
.drfront .insert-title a {
    color: #fff;
}
.desktop.section-forsiden .main-navbar-content,
.tablet.section-forsiden .main-navbar-content {
    box-shadow: none;
}
.desktop .main-navbar-content,
.tablet .main-navbar-content {
    box-shadow: none;
}
.desktop.section-nyheter .main-navbar-content,
.tablet.section-nyheter .main-navbar-content {
    box-shadow: none;
}
.navbar-content,
.main-navbar-content {
    background-color: #4D4D4D;
}
.wid-weather {
    background: #4D4D4D;
}
.desktop .main-navbar-content .second,
.tablet .main-navbar-content .second {
    margin-top: 6px;
}
.desktop .section-nav-top-stripe,
