    margin-top: 6px;
}
.desktop .section-nav-top-stripe .main,
.tablet .section-nav-top-stripe .main {
    margin-top: 6px;
}
.desktop .main-navbar-content .button-container,
.tablet .main-navbar-content .button-container {
    margin-top: 6px;
}
.desktop .main-navbar-content .button-container li>a,
.tablet .main-navbar-content .button-container li>a {
    border-left: none;
}
.reg-article .reg-tools {
    display: none;
}
.reg-article #byline ul.has-byline {
    display: none;
}
.reg-grid-main {
    color: #B8B8B8;
}
.desktop .reg-article .article-main-lead,
.tablet .reg-article .article-main-lead {
    background-color: #2F2F2F;
}
.desktop .reg-article .article-main-lead figcaption,
.desktop .reg-article .article-main-lead .rsGCaption,
.tablet .reg-article .article-main-lead figcaption,
.tablet .reg-article .article-main-lead .rsGCaption {
    color: #B8B8B8;
}
aside .widget,
.wid-special .widget {
    background-color: #676767;
}
aside .widget a,
.wid-special .widget a {
    color: #FFFFFF;
}
.vgpluss-full {
    display: none;
}
#page-footer-desktop .footer-bar .section-nav-topbar a {
    color: #fff;
}
.front #vg247-iframe {
    display: none;
}
#vg247-iframe-placeholder {
    min-height: 0px !important;
}
#wid-right-column .article-timestamp ul li a {
    font-size: 12px;
    color: #002D90;
}
.desktop #article-bottom-elements .related article a,
.tablet #article-bottom-elements .related article a {
    color: #989898;
}
.widget h2 {
    color: #FFFFFF;
}
.desktop #article-body-text .widget,
.desktop #article-body-text .tip-box,
.tablet #article-body-text .widget,
.tablet #article-body-text .tip-box {
    background: #575757;
    border: none;
}
.reg-article #article-body-text figure figcaption,
.reg-article #article-body-text div.figure figcaption {
    background-color: #575757;
    color: #B8B8B8;
    border-bottom: none;
}
aside .widget li,
.wid-special .widget li {
    border-bottom: none;
}
.kickback-widget li:nth-child(even) {
    background-color: #8B8B8B;
}
#wid-right-column .article-timestamp ul li a {
    font-size: 12px;
    color: #B5B5B5;
}
#wid-right-column .article-timestamp ul li time {
    color: #FFF;
}
#wid-right-column .article-timestamp ul {
    height: 370px;
}
.front-bottom-widgets {
    display: none;
}
.main-navbar-content {
    box-shadow: none !important;
}
a {
    color: #FFF !important;
    text-decoration: none;
    outline: none;
}
#wid-right-column .article-timestamp {
    border: 0px;
    margin-top: 3px;
}
#wid-right-column .article-timestamp h3 {
    background-color: #4A4A4A;
    padding: 7px;
}
