}
.drfront .df-container-skin-sporten {
    background-color: #2B2B2B;
}
.drfront .df-container.df-container-skin-cm-norsktipping {
    background-color: #4a4a4a;
    display: none;
}
#ad-bilag2 {
    display: none;
}
#vg247-iframe-placeholder {
    display: none;
}
.desktop .article-body-text .widget,
.desktop .article-body-text .tip-box,
.tablet .article-body-text .widget,
.tablet .article-body-text .tip-box {
    background: #676767;
}
.reg-article .article-body-text figure figcaption,
.reg-article .article-body-text div.figure figcaption {
    background-color: #676767;
    color: #fff;
}
.section-sport {
    background-color: #2F2F2F;
}
.section-sport.desktop .article-main-lead,
.section-sport.desktop .article-body-text .widget,
.section-sport.tablet .article-main-lead,
.section-sport.tablet .article-body-text .widget {
    background-color: #676767;
}
.pag-article {
    background-color: #2F2F2F;
}
.well {
    background: #676767;
}
.module.verbatim .items>div.item-sticky {
    background-color: #676767;
}
.module.verbatim .items>div h4 {
    color: #fff;
}
.module.verbatim .items>div p {
    font-size: 14px;
    color: #B8B8B8;
}
.module.verbatim .footer {
    background-color: #676767;
}
.section-sport.desktop aside .widget,
.section-sport.tablet aside .widget {
    background-color: #676767;
}
.fb_iframe_widget {
    display: inline-block;
    position: relative;
    background-color: #696969;
}
.container .row {
    background: #2F2F2F;
}
.module.net-jockey>div {
    background-color: #676767;
}
