    background: #676767;
    color: #B8B8B8;
}
.module.article .well .text h1 {
    color: #CCCCCC;
}
.module.article h2,
.module.article ul {
    color: #cccccc;
}
.module.question .answers {
    background: #272727;
}
.container .module.question .question-input {
    background: #272727;
    overflow: hidden;
}
.module.verbatim .items>div.highlight-permalink {
    background-color: #272727;
}
.container .module .continue {
    background: #272727;
}
.module.vgtv .well {
    background: #676767;
    color: #F5F5F5;
}
.module.net-jockey>div .info .studiovert-name {
    font-size: 13px;
    color: #B3B3B3;
}
/* version 0.2 */
.desktop #section-nav-bar .nav-header li a, .tablet #section-nav-bar .nav-header li a {
    background: #4D4D4D;
}
.desktop #section-nav-bar, .tablet #section-nav-bar {
    border: 1px solid #4D4D4D;
    background: #4D4D4D;
}
/* version 0.2.1 */
.reg-grid-main-content {
    background-color: #2F2F2F;
}
