css:
@-moz-document domain("userstyles.org") {
/*
Name : userstyles.org Custom - Dark Anime
Author : Dark_Lord9
Published : 10/05/2016
Version : 1.4
*/
/* Animations */
@keyframes picAnim {
    0%{transform:translate(337px);opacity:1;}
    10%{transform:translate(0);}
    90%{opacity:1;}
}
@keyframes txtAnim {
    30%{opacity:1;}
    95%{transform:translateY(0);opacity:1}
    100%{transform:translateY(101px);}
}
/* Background */
body {
    background-color: black;
    background-image: url(http://konachan.com/sample/d0c4000ff5e9e98cb5be709ff86fecab/Konachan.com%20-%20207964%20sample.jpg);
    background-position: left top;
    background-size: 1500px 790px;
    background-repeat: no-repeat;
    background-attachment: fixed;
}
@media screen and (min-width: 1500px) {
    body {
       background-size: cover;
    }
}
/* Content Zone */
#main-header {
    background-color: rgba(52, 53, 59, .5)!important;
    color: rgb(8, 12, 93)!important;
    margin-top: 15px;
}
#main-article {
    background-color: rgba(52, 53, 59, .5)!important;
    border-radius: 5px;
    padding: 5px 10px 5px 10px;
}
nav {
    background-color: rgba(52, 53, 59, .5)!important;
}
#related {
    background-color: rgba(52, 53, 59, .5)!important;
}
.edit-setting{background-color:transparent;}
/* Links */
a:link,a:visited {
    color: white !important;
    text-decoration: underline;
}
a:hover {
    -webkit-transition: ease-in-out all .2s;
    -moz-transition: ease-in-out all .2s;
    -o-transition: ease-in-out all .2s;
    color: #8A0707!important;
    text-decoration: underline;
}
#login-options a.google_oauth2-login.btn-auth.btn-google,
#login-options a.btn-auth.btn-yahoo,
#login-options a.google_oauth2-login.btn-auth.btn-google:hover,
#login-options a.btn-auth.btn-yahoo:hover{
    color: white !important;
    text-decoration: underline;
}
#login-options a.btn-auth.btn-openid,
#login-options a.btn-auth.btn-password,
#login-options a.btn-auth.btn-openid:hover,
#login-options a.btn-auth.btn-password:hover{
    color: grey !important;
    text-decoration: underline;
}
/* Texts */
h1 {
    color: white !important;
    text-decoration: none;
}
h2 {
    color: #101018;
}
h3 {
    color: #101018;
}
h4 {
    color: white;
}
p {
    color: white;
}
p.notice {
    background-color: #8A0707;
}
ul {
    list-style-type: circle;
}
li {
    color: white;
}
li.more{border-top: 1px solid #181818;}
dt {
    font-weight: bold;
    font-size: 16px;
    font-style: italic;
    color: #101018;
}
.style-brief-text + header{
    background-color: transparent !important;
}
.Message {
    color: white;
}
/* Table */
th {
    color:#e9e9e9;
}
th,
td {
    padding: 5px;
}
/* Ad Blocker */
.ad,
[href="http://vanillaforums.org"] {
     /*[[ad_blocking]]*/;
}
/* Dropdown Options */
select {
    background-color: rgba(100, 100, 100, .3);
    border: 1px solid white;
    border-radius: 2px;
    color: white;
}
select option {
    background-color: rgba(50, 50, 50, .7);
}
/* Text Area */
textarea {
    background-color: rgba(80, 80, 80, .7);
    box-shadow: 0 0 5px black;
    border: 1px solid #555;
    color: #eee;
}
/* Search, URL And Text Bars */
#search-terms {
    background-color: rgba(100, 100, 100, .3);
    color: white;
    border: 1px solid #AAA;
    border-radius: 2px;
    padding-left: 2px;
    width: 80%;
}
#widget-title,
input[type="text"],
input[type="url"],
input[name^="screenshot_description"],
input[name^="new_screenshot_description"],
input[type="password"],
#obsoleting-style-id{
    background-color: rgba(100, 100, 100, .3);
    color: white;
    border: 1px solid #AAA;
    border-radius: 2px;
    padding-left: 2px;
}
input[title="Label displayed to the user"],
input[title="Install key - matches the placeholder comment in your code"],
input[title="Install key - used to keep track of selections when the user updates"] {
    background-color: rgb(50, 50, 50);
    border: 1px solid white;
    box-shadow: 0 0 5px black;
    color: #eee;
    padding: 3px;
}
/* Butttons */
.install-status.install,
.install-status.install-info {
    border-top: 1px solid #949494;
    background: #4b4c4d !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#5c5c5c), to(#4b4c4d));
    background: -webkit-linear-gradient(top, #5c5c5c, #4b4c4d);
    background: -moz-linear-gradient(top, #5c5c5c, #4b4c4d);
    background: -ms-linear-gradient(top, #5c5c5c, #4b4c4d);
    background: -o-linear-gradient(top, #5c5c5c, #4b4c4d);
    padding: 7px 14px;
    border-radius: 6px;
    box-shadow: rgba(0, 0, 0, .5) 0 1px;
    text-shadow: rgba(0, 0, 0, 0.4) 0 1px;
    color: #d4d4d4;
    font-size: 14px;
    font-family: Georgia, serif;
    text-decoration: none;
    vertical-align: middle;
}
.install-status.install:hover,
.install-status.install-info:hover{
    -webkit-transition: ease-in-out all .3s;
    -moz-transition: ease-in-out all .3s;
    -o-transition: ease-in-out all .3s;
    background: #383838 !important;
    color: #ffffff !important;
}
.install-status.install:active,
.install-status.install-info:active{background: #2b2b2b !important;}
#search-submit,
.remove-option,
.move-option-up,
.move-option-down,
.edit-setting-buttons input[type="button"],
input[type="submit"] {
    border: 1px solid #949494;
    background: #5c5c5c;
    padding: 4px 4px;
    border-radius: 3px;
    box-shadow: rgba(0, 0, 0, 1) 0 1px;
    text-shadow: rgba(0, 0, 0, .4) 0 1px;
    color: #d4d4d4;
    font-size: 10px;
    vertical-align: middle;
    cursor: pointer;
}
#search-submit:hover,
.remove-option:hover,
.move-option-up:hover,
.move-option-down:hover,
.edit-setting-buttons input[type="button"]:hover,
input[type="submit"]:hover {
    -webkit-transition: ease-in-out all 0.3s;
    -moz-transition: ease-in-out all 0.3s;
    -o-transition: ease-in-out all 0.3s;
    background: #414141;
    color: #ffffff;
}
#search-submit:active,
.remove-option:active,
.move-option-up:active,
.move-option-down:active,
.edit-setting-buttons input[type="button"]:active,input[type="submit"]:active{background: #2b2b2b;}
.form-controls tbody tr td [type="submit"],
#new-setting input[type="button"] {
    border: 1px solid #949494;
    background: #4b4c4d !important;
    padding: 7px 14px;
    border-radius: 5px;
    text-shadow: rgba(0, 0, 0, .4) 0 1px;
    color: #d4d4d4;
    font-size: 14px;
    font-family: Georgia, serif;
    vertical-align: middle;
}
.form-controls tbody tr td [type="submit"]:hover {
    background: #383838 !important;
    color: #ffffff !important;
}
.form-controls tbody tr td [type="submit"]:active {background: #2b2b2b !important;}
.new-option input[type="button"] {
    border: 1px solid #888;
    background: #4b4c4d !important;
    padding: 7px 14px;
    border-radius: 6px;
    box-shadow: rgba(0, 0, 0, 1) 0 1px;
    text-shadow: rgba(0, 0, 0, .4) 0 1px;
    color: #d4d4d4;
    font-size: 14px;
    font-family: Georgia, serif;
    text-decoration: none;
    vertical-align: middle;
    margin-bottom: 2px;
}
.new-option input[type="button"]:hover,
#new-setting input[type="button"]:hover{
    background: #383838 !important;
    color: #ffffff !important;
}
.new-option input[type="button"]:active, #new-setting input[type="button"]:active{background: #2b2b2b !important;}
/* Pagination */
.pagination {
    float: right;
}
.pagination span,
.pagination a {
    text-decoration: none;
}
.pagination em {
    color: #777;
    text-shadow: 0 0 5px black;
}
.pagination .gap {
    color: white;
}
/* Scroll Bar */
::-webkit-scrollbar {width: 10px;height: 10px;}
::-webkit-scrollbar-thumb{background-color: #333333;}
::-webkit-scrollbar-track-piece{background: #171717;}
::-webkit-scrollbar-thumb:hover{background-color: #404040;}
::-webkit-scrollbar-thumb:active{background-color: #535353;}
::-webkit-scrollbar-corner{background: #171717;}
/* Selection */
::selection{background-color:#999;color:white;}
::-moz-selection{background-color:#999;color:white}
/* Cursor (Thanks again to those who made them) */
html{cursor: /*[[cursor]]*/;}
}

@-moz-document url-prefix("https://forum.userstyles.org/") {
/* Header */
#Head {
    background-color: #1a1a1a;
    width: 100%;
    height: 50px;
    position: fixed;
    z-index: 9999;
}
#Head .Row .SiteTitle,
#Head .Row .SiteMenu{
    vertical-align: central !important;
    text-transform: capitalize;
    font-family: "Century Gothic", CenturyGothic, AppleGothic, sans-serif !important;
}
/* Content Zone */
.PanelColumn {
    background-color: rgba(52, 53, 59, 0.5);
    border-radius: 5px;
    padding: 5px;
    position: fixed;
    margin-top: 80px;
}
.ContentColumn{margin-top: 0;padding-top: 80px;}
.CategoryFilter{color: white;}
.DataList .ItemDiscussion,
.Item.Category-style-development.Item-Category-style-development.Depth1.Depth-1.Read,
.Item.Alt.Category-style-requests.Item-Category-style-requests.Depth1.Depth-1.Read{background-color: transparent;}
ul{list-style: none;}
.ItemDiscussion{z-index:5;}
pre,
code {
    background-color: white;
    color: black;
    border: 1px dashed;
}
.Message > blockquote {
    background-color: rgba(0, 0, 0, 0.3);
    border-color: rgba(190, 190, 190, 0.7);
    margin-left: 20px;
}
.P.PageDescription {
    color: #E0E0E0;
    font-size: 15px;
    font-weight: bold;
}
.Item.ItemComment {
    border-bottom: 1px solid #333;
}
.PopList li{
    color: #777;
}
.Item.Title a {
    color: black !important;
}
.DiscussionAboutShowDiscussion,
.RadioLabel {
    color: #777;
}
.ProfileOptions a{
    color: #555 !important;
    text-decoration: none;
}
.About.P dl dd{
    color:#bbb;
    text-decoration: underline;
    text-shadow: 0 0 10px black;
}
.PanelInfo.PanelCategories li:nth-child(1) {border-top:1px solid #222;border-bottom:0;}
.PanelInfo.PanelCategories li:nth-child(5) {border:none;}
.DataListWrap h2{color:#333;font-style:italic;}
.Pager.NumberedPager a{text-decoration: none;}
.Flyout.FlyoutMenu{z-index:999;}
/* Text Area */
textarea:focus {
    background-color: rgba(80, 80, 80, 0.7);
    box-shadow: 0 0 5px black;
    border: 1px solid #555;
    color: #eee;
}

/* Search And Text Bars */
#Form_Search {
    background-color: #101010;
    border: 1px solid #999 !important;
    box-shadow: 0 0 30px #101010;
    color: white;
}
.InputBox.BigInput,
.InputBox.BigInput:focus {
    background-color: rgba(100, 100, 100, 0.3);
    color: white;
    border: 1px solid #AAA;
    border-radius: 2px;
    padding-left: 2px;
}
/* Button */
.Button:not(#Form_Go):not(#Form_Search1) {
    border-top: 1px solid #949494;
    background: #4b4c4d !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#5c5c5c), to(#4b4c4d));
    background: -webkit-linear-gradient(top, #5c5c5c, #4b4c4d);
    background: -moz-linear-gradient(top, #5c5c5c, #4b4c4d);
    background: -ms-linear-gradient(top, #5c5c5c, #4b4c4d);
    background: -o-linear-gradient(top, #5c5c5c, #4b4c4d);
    padding: 7px 14px;
    border-radius: 6px;
    box-shadow: rgba(0, 0, 0, 1) 0 1px;
    text-shadow: rgba(0, 0, 0, .4) 0 1px;
    color: #d4d4d4;
    font-size: 14px;
    font-family: Georgia, serif;
    text-decoration: none;
    vertical-align: middle;
}
.Button:hover:not(#Form_Go):not(#Form_Search1) {
    background: #383838 !important;
    color: #ffffff !important;
}
.Button:active:not(#Form_Go):not(#Form_Search1){background: #2b2b2b !important;}
}

@-moz-document url("https://userstyles.org/") {
/* Animated Message */
#content-wrapper:after {
    content: "";
    display: block;
    position: fixed;
    right: 0;
    bottom: 0;
    width: 230px;
    height: 337px;
    background-image: url(https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-35134.png);
    background-position:top center;
    background-size: cover;
    opacity: 0;
    animation: picAnim 23s;
    animation-delay: 2s;
}
#content-wrapper:before {
    content:"Wel.. Welcome dear sir. I.. I have been sent by my master Dark_Lord9 to inform you that he is thanking you for using his work and he is asking you to rate it and to report any problems. Anyway, I shall take my leave now. THA.. THANK YOU AGAIN.";
    color: white;
    line-height: 1.3;
    display: block;
    width: 300px;
    height: 100px;
    padding: 3px;
    border: 1px solid white;
    position: fixed;
    bottom: 5px;
    right: 240px;
    box-shadow: 0 0 15px #efefef;
    background-color: rgba(0, 0, 0, 0.5);
    opacity: 0;
    animation: txtAnim 22s;
    animation-delay: 2s;
}
}
