.linkContainer {
    /*background: #353535;*/
    
    background: #1c1c1c;
background: url(data:image/svg+xml;base64,);
background: -moz-linear-gradient(-45deg, #1c1c1c 0%, #383838 52%, #3d3d3d 63%, #383838 74%, #1c1c1c 100%);
background: -webkit-gradient(linear, left top, right bottom, color-stop(0%,#1c1c1c), color-stop(52%,#383838), color-stop(63%,#3d3d3d), color-stop(74%,#383838), color-stop(100%,#1c1c1c));
background: -webkit-linear-gradient(-45deg, #1c1c1c 0%,#383838 52%,#3d3d3d 63%,#383838 74%,#1c1c1c 100%);
background: -o-linear-gradient(-45deg, #1c1c1c 0%,#383838 52%,#3d3d3d 63%,#383838 74%,#1c1c1c 100%);
background: -ms-linear-gradient(-45deg, #1c1c1c 0%,#383838 52%,#3d3d3d 63%,#383838 74%,#1c1c1c 100%);
background: linear-gradient(135deg, #1c1c1c 0%,#383838 52%,#3d3d3d 63%,#383838 74%,#1c1c1c 100%);
    
    border-color: rgb(39, 39, 39);
    box-shadow: 0 5px 15px -5px rgba(0, 0, 0, 0.8), inset -3px 6px 42px 0px rgba(0,0,0,0.6);
    position: relative;
    overflow:hidden;
    
    /*box-shadow: 0 12px 25px -10px rgba(120, 181, 0, 0.8), inset -3px 6px 42px 0px rgba(255,255,255,0.6);*/
}
.linkContainer:hover {
    /*box-shadow: 0 12px 25px -10px rgba(120, 181, 0, 0.8), inset 0px 33px 45px -25px rgba(255,255,255,0.4);*/
    box-shadow: 0 12px 25px -10px rgba(120, 181, 0, 0.8), inset 0px 0 50px 20px rgba(255,255,255,0.05);
    border-bottom-color: #78B500 !important;
    /*background: #424242 !important;*/
}
.linkContainer > div > label {
    color: white !important;
}
.linkContainer > label {
    color: rgb(90, 90, 90);
}
.linkContainer:hover > label {
    color: rgb(255, 255, 255);
    text-shadow: 0 0 10px rgba(255, 255, 255, 1);
}
.linkContainer:after {
    /*background: #BEFF00 none repeat scroll 0 0;*/
    
    background: #99006b;
    background: url(data:image/svg+xml;base64,);
    background: -moz-linear-gradient(left, #99006b 0%, #ff7ce0 65%, #ff2bd4 100%);
    background: -webkit-gradient(linear, left top, right top, color-stop(0%,#99006b), color-stop(65%,#ff7ce0), color-stop(100%,#ff2bd4));
    background: -webkit-linear-gradient(left, #99006b 0%,#ff7ce0 65%,#ff2bd4 100%);
    background: -o-linear-gradient(left, #99006b 0%,#ff7ce0 65%,#ff2bd4 100%);
    background: -ms-linear-gradient(left, #99006b 0%,#ff7ce0 65%,#ff2bd4 100%);
    background: linear-gradient(to right, #99006b 0%,#ff7ce0 65%,#ff2bd4 100%);
	
    border-bottom: 2px solid #FF94DF;
    
    content: "";
    display: block;
    height: 40px;
    width: 40px;
    position: absolute;
    right: -20px;
    top: -22px;
    -webkit-transform: rotate(45deg);
    -moz-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    -o-transform: rotate(45deg);
    transform: rotate(45deg);
    /**/
    -webkit-box-shadow: 0 0 50px 0px #FF42D0;
    -ms-box-shadow: 0 0 50px 0px #FF42D0;
    box-shadow: 0 0 50px 0px #FF42D0;
}
