css:
@-moz-document url-prefix("https://www.eldarya.fr") {
body {
    background: url("http://www.full-wallpaper.com/paysage/feerique/dreamsky007.jpg") fixed top center;
    background-size: cover;
    overflow-y: scroll;
}
body.night {
    background: url("http://lamistitine.l.a.pic.centerblog.net/v3dozits.jpg") fixed top center;
    background-size: cover;
    overflow-y: scroll;
}
#header #header-top {
    position: absolute;
    top: 0;
    background-color: #9370DB;
    background-image: url("http://1.bp.blogspot.com/-FYF19mHKlYs/Tv7L5Cycv-I/AAAAAAAACh0/nha-O6FKPaQ/s1600/argyle_pattern_cp.png");
    height: 39px;
    width: 100%;
}
.pun .spoil {
    margin: 5px 15px 15px 15px;
    padding: 8px;
    background-color: #9370DB;
    border-radius: 10px;
    border: 1px solid #9370DB;
}
.pun .spoil > div > h4 {
    margin: 0px 0px 10px 0px;
    text-decoration: underline;
    font-weight: bold;
    cursor: pointer;
    color: #D4C3E0;
}
#footer {
    font-family: Banda Bold, sans-serif;
    position: absolute;
    height: 42px;
    width: 100%;
    bottom: 0;
    left: 0;
    background-color: #9370DB;
    background-image: url("http://1.bp.blogspot.com/-FYF19mHKlYs/Tv7L5Cycv-I/AAAAAAAACh0/nha-O6FKPaQ/s1600/argyle_pattern_cp.png");
}
#container .page-main-container {
    background-color: rgba(223, 236, 255, 0.33);
    border-radius: 10px;
    padding: 20px;
    min-height: 400px;
}
#news-list .news {
    background: rgb(209, 241, 255) no-repeat 20px;
    position: relative;
    border-top: 2px solid #4F3F8C;
    height: 50px;
}
#index-news .date {
    float: left;
    background-color: rgb(177, 119, 226);
    width: 55px;
    height: 25px;
    color: #fff;
    border-radius: 10px;
    font-size: 16px;
    text-align: center;
    line-height: 25px;
    margin: 12px 5px;
}
#index-news {
    display: inline-block;
    vertical-align: top;
    height: 346px;
    width: 249px;
    margin: 20px 0 0;
    background-color:#B177E2;
    border-radius: 10px;
    border: 2px solid #20357D;
    overflow: hidden;
}
#index-ranking {
    display: inline-block;
    height: 346px;
    width: 249px;
    background-color: #B177E2;
    border-radius: 10px;
    border: 2px solid #20357D;
    overflow: hidden;
    margin: 20px 20px 0 0;
}
#ranking-list {background: linear-gradient(0deg, #20357D 15%, #0000CD 50%, #D1F1FF 96%);
    border-top: 1px solid #d077e6 !important;}
#ranking-list > div {
    text-shadow: 1px 1px 1px black;
    box-shadow: inset 0px 0px 2px #D1F1FF, 0px 0px 5px #D1F1FF !important ;
    position: relative;
    background-repeat: no-repeat;
    background-position: 175px;}
    #ranking-list .guard-4 {
    background-position: 157px;}
#main-menu .main-menu-episode {
    background-image: url("http://zupimages.net/up/16/23/l1sf.png");
}
#main-menu .main-menu-episode:hover, #main-menu .main-menu-episode.selected {
    background-image: url("https://zupimages.net/up/19/07/87zl.png");
}
/*_____*/
 #main-menu .main-menu-minigames {
    background-image: url("http://zupimages.net/up/16/23/5dk5.png");
}
#main-menu .main-menu-minigames:hover, #main-menu .main-menu-minigames.selected {
    background-image: url("https://zupimages.net/up/19/07/yfrq.png");
}
/*_____*/
 #main-menu .main-menu-mall {
    background-image: url("http://zupimages.net/up/16/23/v0z0.png");
}
#main-menu .main-menu-mall:hover, #main-menu .main-menu-mall.selected {
    background-image: url("https://zupimages.net/up/19/07/7ntk.png");
}
/*_____*/
 #main-menu .main-menu-marketplace {
    background-image: url("http://zupimages.net/up/16/23/hdtz.png");
}
#main-menu .main-menu-marketplace:hover, #main-menu .main-menu-marketplace.selected {
    background-image: url("https://zupimages.net/up/19/07/dw75.png");
}
/*_____*/
 #main-menu .main-menu-ranking {
    background-image: url("http://zupimages.net/up/16/23/ouqw.png");
}
#main-menu .main-menu-ranking:hover, #main-menu .main-menu-ranking.selected {
    background-image: url("https://zupimages.net/up/19/07/nhtl.png");
}
/*_____*/
 #main-menu .main-menu-library {
    background-image: url("http://zupimages.net/up/16/23/ahea.png");
}
#main-menu .main-menu-library:hover, #main-menu .main-menu-library.selected {
    background-image: url("https://zupimages.net/up/19/07/hoen.png");
}
/*_____*/
 #main-menu .main-menu-messaging {
    background-image: url("http://zupimages.net/up/16/23/m2ic.png");
}
#main-menu .main-menu-messaging:hover, #main-menu .main-menu-messaging.selected {
    background-image: url("https://zupimages.net/up/19/07/2uvm.png");
}
/*_____*/
 #main-menu .main-menu-bank {
    background-image: url("http://zupimages.net/up/16/23/quwc.png");
}
#main-menu .main-menu-bank:hover, #main-menu .main-menu-bank.selected {
    background-image: url("https://zupimages.net/up/19/07/ecje.png");
}
.index-carousel {
    position: relative;
    height: 231px;
    width: 522px;
    border-radius: 10px;
    border: 2px solid rgb(27, 28, 64);
    background-color: rgba(168, 207, 239, 0.77);
    overflow: hidden;
}
.index-main-container .index-carousel-description {
    color: rgb(0, 0, 0);
}
#player-info-level {
    font-family: Magnel, serif;
    color: #38256F;
    font-size: 23px;
    font-weight: 400;
    text-align: center;
    line-height: 29px;
    border-radius: 50%;
    background-color: #ffffff;
    box-shadow: 0 0 9px 0 rgb(19, 0, 39);
    width: 33px;
    height: 33px;
    position: absolute;
    top: -8px;
    right: -8px;
    cursor: default;
    z-index: 2;
}
#player-info-dots .player-info-dot.lit {
    background-color: #1123BF;
}
#pet-headerPortrait {
    top: 35px;
    height: 75px;
    width: 75px;
    left: 12px;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    border-radius: 50%;
    border: solid 4px #29338D;
    box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.3);
    background: rgb(174, 182, 218);
}
#player-headerPortrait {
    width: 106px;
    height: 106px;
    border-radius: 50%;
    border: solid 4px #29338D;
    box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.3);
    left: 70px;
    top: 10px;
    background-color: #ADB2D8;
}
#player-infos .player-name {
    color: #fff;
    font-weight: 600;
    font-family:"Banda Regular", sans-serif;
    position: relative;
    height: 26px;
    line-height: 26px;
    display: inline-block;
    border: solid 2px #CFE0FF;
    background-color: #343B9C;
    -webkit-transform: skew(-12deg);
    -moz-transform: skew(-12deg);
    -o-transform: skew(-12deg);
    -webkit-backface-visibility: hidden;
}
#player-infos .player-currencies {
    background-color: #343B9C;
    border-radius: 20px;
    height: 25px;
    line-height: 25px;
    font-family: Banda Semi Bold, sans-serif;
    font-weight: 600;
    padding: 0 10px;
    margin-top: 5px;
}
#index-player {
    position: absolute;
    margin: 0 auto;
    width: 420px;
    height: 600px;
    right: 20px;
    top: 20px;
    border: solid 2px rgba(73, 0, 175, 0.45);
    border-radius: 10px;
    overflow: hidden;
    background-color: #fff;
}
#pet-info-luck-level {
    display: inline-block;
    width: 30px;
    min-height: 30px;
    padding-left: 35px;
    margin-left: 6px;
    background: url("http://www.eldarya.fr/static/img/pet/luck_full.png") 0% 50% no-repeat;
}
#pet-info-food, #pet-info-levels {
    color: rgb(52, 56, 111);
    font-weight: bold;
    background: rgba(225, 228, 241, 0.76);
    border-radius: 10px;
    margin: 15px 0px;
    padding: 15px;
    overflow: hidden;
}
#pet-info-food img {
    border-width: 1px;
    border-style: solid;
    border-color: rgb(100, 57, 144);
    background: rgb(230, 222, 254);
}
.episode-container .text {
    z-index: 100;
    text-align: justify;
    position: absolute;
    font-family: Suplexmentary;
    line-height: 1em;
    font-size: 20px;
    overflow: hidden;
    background: linear-gradient(rgba(126, 115, 181, 0.85098) 0%, rgba(34, 29, 109, 0.85098) 89%, rgba(50, 25, 82, 0.298039) 100%);
}
#right-profile-tab {
    float: left;
    width: 369px;
    background-color: rgba(221, 232, 249, 0.72);
    position: relative;
    min-height: 111px;
    padding: 15px 15px 30px;
    margin: 10px 0px 0px 10px;
    border-radius: 10px;
    overflow: auto;
}
#library-container li {
    background-color: rgba(225, 236, 255, 0.76);
}
.mall-category, .mall-product, .mall-variation {
    width: 106px;
    height: 124px;
    float: left;
    cursor: pointer;
    position: relative;
    box-shadow: black 0px 0px 5px;
    background: rgba(233, 241, 255, 0.91);
    margin: 5px;
    padding: 7px;
    border-radius: 3px;
}
#mall-productDetail-info {
    width: 626px;
    clear: both;
    height: 85px;
    position: relative;
    box-shadow: black 0px 0px 5px;
    text-align: center;
    background: rgba(225, 236, 253, 0.68);
    margin: 7px 5px;
    padding: 7px;
    border-radius: 3px;
}
.marketplace-itemsForSale-item {
    height: 105px;
    float: left;
    cursor: pointer;
    position: relative;
    box-shadow: black 0px 0px 5px;
    background: #E4E4F3;
    margin: 7px;
    padding: 7px;
    border-radius: 3px;
}
img.abstract-icon {
    background-color: #E4E4F3;
}
.inventory-item {
    width: 80px;
    height: 80px;
    background: #EBEBFF;
    margin: 5px;
    padding: 2px;
    float: left;
    position: relative;
    border-radius: 3px;
    box-shadow: 0px 0px 5px black;
}
.appearance-category, .appearance-item {
    background: rgba(229, 229, 255, 0.5);
    margin: 5px;
    float: left;
    cursor: pointer;
    position: relative;
    border-radius: 3px;
    box-shadow: 0px 0px 5px black;
}
#selectedCategory {
    border: 1px solid rgba(0, 0, 255, 0.61);
    border-bottom-width: 2px;
    background: #EAEAFF;
    position: absolute;
    border-radius: 4px;
    cursor: pointer;
    width: 100%;
}
#main-bonus-progressbar > div.open:not(.transition_bonus) {
	border: 2px solid #9370D8;
	background: linear-gradient(to top, #0000CD 0%, #9370D8 40%, rgba(0, 156, 191, 0.5) 100%);
	box-shadow: 0px 0px 3px #6A5ACD, 0px 0px 3px white;}
	#main-bonus-progressbar > div.current:not(.transition_bonus) {
    border: 2px solid #191970;
    background: linear-gradient(to top, #0000CD 0%, #9370D8 40%, rgba(0, 156, 191, 0.5) 100%);
    box-shadow: 0px 0px 15px white, 0px 0px 15px white, 0px 0px 20px white;}
	#main-bonus-progressbar > div:not(.transition_bonus) {
    border: 2px solid #AFEEEE;
    background: linear-gradient(to top, #0000CD 0%, #9370D8 40%, rgba(0, 156, 191, 0.5) 100%);
	box-shadow: 0px 0px 3px WHITE, 0px 0px 3px WHITE, 0px 0px 3px white;}
    #main-bonus-progressbar {background: linear-gradient(0deg, LIGHTBLUE 0%, DARKBLUE 76%, #9370D8 96%);}
	#main-bonus-progressbar > div.current > .label, #main-bonus-progressbar > div.transition_bonus.current {
	background-color: #6A5ACD;
    color: WHITE;
    font-weight: bold;
    border: 1px solid WHITE;}
#main-bonus-progressbar > div.open > .label, #main-bonus-progressbar > div.transition_bonus.open {
	background-color: #9370D8;
	color: WHITE;
    font-weight: bold;
	border: 1px solid WHITE;}
#main-bonus-progressbar > div > .label, #main-bonus-progressbar > div.transition_bonus {
	background-color: #0000CD;
    color: WHITE;
    font-weight: bold;
	border: 1px solid WHITE;}
}
