css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.epicpants.com") {
  
/* !important needed for Chrome */

/* Logo */
body.site-title .logo a {
  color: #CCCCCC !important;
  }

/* Logo Subtitle */
.logo-subtitle {
  margin-left: 3px !important;
  }

/* Background */
body {
  background-color: #000000 !important; 
  color: #F5F5F5 !important;
  }
  
/* Text Color */
body a {
  color: #F5F5F5 !important;
  }
a:link, body a, body a:visited {
  color: #999999 !important;
  }
body a, body a:hover {
  color: #FFFFFF !important;
  }

/* Nav Bar Text Color */
.site-title #topNav nav li a:link, .site-title #topNav nav li a:visited, #topNav nav .folder-collection > a {
  color: #999999 !important;
  }
.site-title #topNav nav li a:hover {
  color: #FFFFFF !important;
  }
  
/* Nav Bar Spacing */
#topNav {
  margin-right: 7px !important; 
  margin-bottom: -5px !important; 
  margin-top: 15px !important;
  }
#topNav nav li {
  margin-right: 31px !important;
  padding-right: 20px !important;
  }
.site-title #topNav nav li a {
  padding-left: 5px !important;
}

/* Sizing Charts */
.sqs-block-image .intrinsic .image-block-wrapper img {
  background-color: #EBEBEB !important;
}

/* Drop Down Menus */
#topNav nav .folder-collection ul li a {
  background: #222222 !important;
  }
#topNav nav .folder-collection ul {
  background: #222222 !important; 
  border: 1px solid #D0D0D0 !important;
  }

/* Product Title */
#productDetails .product-title {
  color: #F5F5F5 !important;
}
  
/* Add to Cart Button */
.sqs-add-to-cart-button {
  background-color: #5C5C5C !important;
  }
}
