@namespace url(http://www.w3.org/1999/xhtml);
/*
Website Colour Scheme
#121414 Basic Background
#101010 Border Darkening
#202020 Bodrer Highlight
#1d1d1d Secondary Background; Information Border Highlight
#242424 Secondary Highlight
#121212 Thirdary Background
#3399ff Hyperlink _1
#66bbff Hyperlink _2
#080808 Information Primary
#050505 Information Border Darkening
#646464 Basic Font Colour for Site
#545454 Header Font Colour
#182020 MISC Colour?
*/
/*
Other Information that could be important
http://www.mylionking.com/fan/art/resources/images/report.png
http://www.mylionking.com/fan/art/resources/images/photos.png
http://www.mylionking.com/fan/art/resources/images/book.png
http://www.mylionking.com/fan/art/resources/images/world.png
http://www.mylionking.com/fan/art/resources/images/favorite.png
http://www.mylionking.com/fan/art/resources/images/chart_bar.png

Any place with the URL ".http://needstobeinvalid.com/." is a placeholder an image I don't want displayed since it looked bad.
That dead, bogus link is intentional, and based by it's name, shows it is very delibrate.
So please. Don't fix that.

*/
@-moz-document url-prefix("http://www.mylionking.com/fan/art/") {
  /* Gives the FAA portion of the web-site a darker appearance with higher contrast text for less strain on the eye */
body{
  max-width:1920px;
  min-width:950px;
  margin:0 auto;
  font:100% "Trebuchet MS",Verdana,Arial,sans-serif;
  color:#646464!important;
  }
  /*  This is the core layout size for the web-site;
  Default Values:
  width=950px
  margin=0 auto
  padding: 0 10px 10px 10px
  font:100.01% "Trebuchet MS",Verdana,Arial,Sans-serif
  color:#121212 //standard font colour
  !important
  */
a, a:link, a:visited {
  text-decoration:none;
  color:#3399ff!important;
  }
  /*   Hyperlink Unhighlighted
  Default Values:
  text-decoration:none
  color:#7b3c1b
  !important
  */
a:active, a:hover, a:visited:hover {
  text-decoration:none;
  color:#66bbff!important;
  }
  /*   Hyperlink Highlighted
  Default Values:
  text-decoration:underline
  color:#b3642e
  !important
  */
p {
  margin:0;
  padding:0;
  }
  /* Paragraph Wrapping */
span.small {
  font-size:75%;
  }
  /* sub-script font on page */
span.supasmall {
  font-size:60%;
  }
  /* supersub-script font on page */
img {
  border:0;
  }
  /*
  border around an image. Unsure what image(s) though
  could not locate what image(s)
  */
.fanart_content {
  margin:0;
  background:#121414;
  text-align:left;
  border-top:0px solid #202020; /* Turns off the Border across the top of the page and across the Main Section */
  border-bottom:4px solid #101010;
  border-left:2px solid #202020;
  border-right:4px solid #101010;
  -webkit-border-bottom-left-radius:6px;
  -webkit-border-bottom-right-radius:6px;
  }
  /* Primary Page Background
  Default Values:
  margin:0;
  background:#f0e6d1;
  text-align:left;
  border-bottom:1px solid #f0e6d1;
  border-left:1px solid #f0e6d1;
  border-right:1px solid #f0e6d1;
  -moz-border-radius-bottomleft:6px;
  -moz-border-radius-bottomright:6px;
  -webkit-border-bottom-left-radius:6px;
  -webkit-border-bottom-right-radius:6px;
  */
#fanart_header {
    background-position:top center!important;
}
.fanart_content_left {
  margin:0;
  background:#080808;
  border-left:2px solid #1d1d1d;
  border-top:2px solid #1d1d1d;
  border-bottom:4px solid #050505;
  border-right:0px solid #050505;
  -webkit-border-bottom-left-radius:6px;
  -webkit-border-bottom-right-radius:6px;
  float:left;
  width:30%; /* If page size is too narrow, this can cause some wrap issues with Artists' Gallery Page */
  text-align:left;
  }
  /* Selection Menus in the Gallery Tab of Profiles
  Default Values:
  margin:0;
  background:#d2bba1;
  border-left:1px solid #d2bba1;
  border-bottom:1px solid #d2bba1;
  -moz-border-radius-bottomleft:6px;
  -webkit-border-bottom-left-radius:6px;
  -moz-border-radius-bottomright:6px;
  -webkit-border-bottom-right-radius:6px;
  float:left;
  width:247px;
  text-align:left;
  */
.fanart_content_right {
  margin:0;
  background: #121414;
  border-left:0 solid #202020;
  border-top:0px solid #202020; /* Turns off border across to of Artist's Page */
  border-right:4px solid #101010;
  border-bottom:4px solid #101010;
  -webkit-border-bottom-left-radius:6px;
  -webkit-border-bottom-right-radius:6px;
  float:right;
  width:69.51%;
  text-align:left;
  }
  /* Artwork Tab within Gallery Tab of Profile
  Default Values:
  margin:0;
  background: #f0e6d1;
  border-left:1px solid #f0e6d1;
  border-right:1px solid #f0e6d1;
  border-bottom:1px solid #f0e6d1;
  -moz-border-radius-bottomleft:6px;
  -webkit-border-bottom-left-radius:6px;
  -moz-border-radius-bottomright:6px;
  -webkit-border-bottom-right-radius:6px;
  float:right;
  width:700px;
  text-align:left;
  */
.fanart_content div.text {
  margin:0 15px;
  padding:5px 0 5px 0;
  clear:both;
  }
  /* Top of site selections Wrap
  Default Values:
  margin:0 15px;
  padding:5px 0 4px 0;
  clear:both;
  */
.fanart_content_left div.text {
  margin:-2px -4px 0 3px;
  padding:0 0 9px 0;
  }
  /*
  Default Values:
  margin:-2px -4px 0 3px;
  padding:0 0 9px 0;
  */
.fanart_content_right div.text {
  margin:0 15px;
  padding:5px 0 4px 0;
  }
  /*
  Deafult Values:
  margin:0 15px;
  padding:5px 0 4px 0;
  */
.fanart_content div.left {
  float:left;
  width:50%;
  }
  /* Left Column of Profile Page
  Default Values:
  float:left;
  width:56%;
  */
.fanart_content div.right {
  float:right;
  width:46%;
  margin:11px 0;
  }
  /* Right Coloumn of Profile Page
  Default Values:
  float:right;
  width:40%;
  margin:11px 0;
  */
.fanart_content div.boxfilled {
  background:#1d1d1d;
  padding:8px;
  margin:6px;
  }
  /*
  Default Values:
  background:#edc17d;
  padding:8px;
  margin:6px;
  */
.fanart_content div.boxfilledclean {
  background:#1d1d1d;
  padding:4px;
  }
  /*
  Default Values:
  background:#edc17d;
  padding:4px;
  */
.fanart_content div.boxbordered {
  border:3px solid #1d1d1d;
  padding:8px;
  margin:6px;
  }
  /*
  Default Values:
  border:3px solid #edc17d;
  padding:8px;
  margin:6px;
  */
.cboxElement > IMG {
  min-width:auto!important;
  min-height:auto!important;
  max-width:100%!important;
  max-height:1080px!important;
  width:inherit!important;
  height:inherit!important;
  }
h1,h2,h3,h4,h5,div#text p{
  font:100% "Trebuchet MS","Verdana","Arial","sans-serif";
  margin:0.3em;
  padding:0;
  color:#545454;
  }
  /* Header Basic Information
  Default Values:
  font:100.01% "Trebuchet MS",Verdana,Arial,sans-serif;
  margin:0.3em; //Affects some wrap?
  padding:0;
  color:#121212;
  */
h1{
  font-size:200%; /* Default */
  }
h2{
  font-size:150%;
  text-shadow:0 0 0 rgba(0, 0, 0, 0);
  }
  /* Header 2 Information
  Default Values:
  font-size:150%;
  text-shadow:0 1px 0 rgba(255, 255, 255, 0.6);
  */
h3{
  font-size:150%;
  width:100%;
  margin-bottom:0.5em;
  padding-bottom:2px; /* Affects Journal Wrap */
  border-bottom:0 solid #121414;
  color:#545454; /* Header 3 Font Colour */
  text-shadow:0 0 0 rgba(0, 0, 0, 0);
  }
  /* Header 3 Information
  Default Values:
  font-size:150%;
  width:100%;
  margin-bottom:0.5em;
  padding-bottom:2px;
  border-bottom:1px solid #d3bfa7;
  color:#291f14;
  text-shadow:0 1px 0 rgba(255, 255, 255, 0.6);
  */
h4{
  font-size:120%;
  }
h5{
  font-size:100%;
  }
hr {
  margin:8px 0;
  height:1px;
  border:1px #1d1d1d solid!important;
  }
.fanart_content p {
  padding-bottom:1em;
  margin-left:0.5em;
  }
ol,ul{
  margin:0.3em 0.3em 0.3em 1.3em;
  }
#fanart_header {
  height:140px;
  display:block;
  margin:-10px 5px 0 5px;
  padding:0 0 0 5px;
  text-align:left;
  }
#fanart_header h1 {
  background:url("http://puu.sh/oNKWo/bf418ce79f.png") no-repeat left top; /* Custom Link to show who made Theme */
  display:block;
  width:100%; /* was 244px */
  height:92px; /* Cannot change; breaks entire layout*/
  text-indent:-9000em;
  opacity: 0.0 !important;
  -moz-transition: .5s ease-in;
  -moz-transition: .5s ease-out;
  }
  #fanart_header h1:hover {
  background:url("http://puu.sh/oNKWo/bf418ce79f.png") no-repeat left top; /* Custom Link to show who made Theme */
  display:block;
  width:100%; /* was 244px */
  height:92px; /* Cannot change; breaks entire layout*/
  text-indent:-9000em;
  opacity: 1.0 !important;
  -moz-transition: .5s ease-in;
  -moz-transition: .5s ease-out;
  }
  
ul#fanart-menu{
  list-style-type:none;
  height:27px;
  font-size:13px;
  font-weight:bold;
  margin:0;
  padding:12px 0 0 0;
  text-shadow:0 0 0 rgba(0, 0, 0, 0);
  }
ul#fanart-menu li.left{
  margin:0;
  }
ul#fanart-menu li{
  display:block;
  float:left;
  margin:0 0 0 5px;
  height:27px;
  }
ul#fanart-menu li a{
  display:block;
  float:left;
  color:#121414;
  background:#121414;
  line-height:27px;
  text-decoration:none;
  padding:0 17px 0 18px;
  height:27px;
  }
  /* FFA Menu Related
  Default Values:
  display:block;
  float:left;
  color:#7b3c1b;
  background:#f0e6d1;
  line-height:27px;
  text-decoration:none;
  padding:0 17px 0 18px;
  height:27px;
  */
ul#fanart-menu li a.current{
  background:#1d1d1d; /* default #d2bba1 */
  }
.fanart_content_header {
  background:#1d1d1d;
  text-align:left;
  border-top:2px solid #202020;
  border-left:2px solid #202020;
  border-right:4px solid #101010;
  -webkit-border-top-left-radius:6px;
  -webkit-border-top-right-radius:6px;
  }
  /* Information Header-Trim
  Default Values:
  background:#d2bba1;
  text-align:left;
  border-top:1px solid #d2bba1;
  border-left:1px solid #d2bba1;
  border-right:1px solid #d2bba1;
  -moz-border-radius-topleft:6px;
  -moz-border-radius-topright:6px;
  -webkit-border-top-left-radius:6px;
  -webkit-border-top-right-radius:6px;
  */
.fanart_content_header div#topper {
  background:#1d1d1d; /* Default #d2bba1 */
  min-height:70px;
  max-height:70px;
  margin:0 0 0 0;
  }
.fanart_content_header div#fanart-title-container {
  float:left;
  display:block;
  width:42%;
  }
.fanart_content_header div#fanart-title {
  
  }
.fanart_content_header div#fanart-title h2 {
  margin:8px 0 0 8px;
  }
.fanart_content_header div#fanart-title p {
  margin:0 0 0 8px;
  }
ul#fanart-tabs{
  clear:none;
  list-style-type:none;
  height:27px;
  font-size:13px;
  font-weight:bold;
  padding:0;
  float:right;
  margin:42px 5px 0 0;
  text-shadow:0 0 0 rgba(0, 0, 0, 0); /* Default: 0 1px 0 rgba(255, 255, 255, 0.6 */
  }
ul#fanart-tabs li{
  display:block;
  float:right;
  margin:0 0 0 5px;
  height:27px;
  }
ul#fanart-tabs li.left{
  margin:0;
  }
ul#fanart-tabs li a{
  display:block;
  float:left;
  color:#121414;
  line-height:27px;
  text-decoration:none;
  padding:0 12px 0 13px;
  height:27px;
  border-top:2px solid #202020;
  border-left:2px solid #202020;
  border-right:4px solid #101010;
  }
  /* Secondary Tabs in FAA Album
  Default Values:
  display:block;
  float:left;
  color:#7b3c1b;
  line-height:27px;
  text-decoration:none;
  padding:0 12px 0 13px;
  height:27px;
  border-top:1px solid #d2bba1;
  border-left:1px solid #d2bba1;
  border-right:1px solid #d2bba1;
  */
ul#fanart-tabs li a:hover, li a.active{
  
  }
ul#fanart-tabs li a.current{
  background:#121414;
  border-top:2px solid #202020;
  border-left:2px solid #202020;
  border-right:4px solid #101010;
  -webkit-border-top-left-radius:6px;
  -webkit-border-top-right-radius:6px;
  }
  /*
  Default Values:
  background:#f0e6d1;
  border-top:1px solid #f0e6d1;
  border-left:1px solid #f0e6d1;
  border-right:1px solid #f0e6d1;
  -moz-border-radius-topleft:6px;
  -moz-border-radius-topright:6px; 
  -webkit-border-top-left-radius:6px;
  -webkit-border-top-right-radius:6px;
  */
ul#fanart-album-tabs{
  clear:none;
  list-style-type:none;
  height:27px;
  font-size:13px;
  font-weight:bold;
  padding:0;
  float:right;
  margin:42px 5px 0 0;
  text-shadow:0 0 0 rgba(0, 0, 0, 0); /* Default: 0 1px 0 rgba(255, 255, 255, 0.6 */
  }
ul#fanart-album-tabs li{
  display:block;
  float:right;
  margin:0 0 0 5px;
  height:27px;
  }
ul#fanart-album-tabs li.left{
  margin:0;
  }
ul#fanart-album-tabs li a{
  display:block;
  float:left;
  color:#121414;
  line-height:27px;
  text-decoration:none;
  padding:0 12px 0 29px;
  height:27px;
  border-top:2px solid #202020;
  border-left:2px solid #202020;
  border-right:4px solid #101010;
  }
  /* Third Tab Set FAA
  Default Values:
  display:block;
  float:left;
  color:#7b3c1b;
  line-height:27px;
  text-decoration:none;
  padding:0 12px 0 29px;
  height:27px;
  border-top:1px solid #d2bba1;
  border-left:1px solid #d2bba1;
  border-right:1px solid #d2bba1;
  */
ul#fanart-album-tabs li a:hover, li a.active{
  
  }
ul#fanart-album-tabs li a.current{
  border-top:2px solid #202020;
  border-left:2px solid #202020;
  border-right:4px solid #101010;
  -webkit-border-top-left-radius:6px;
  -webkit-border-top-right-radius:6px;
  }
