  }
ul#fanart-album-tabs li.characters a.current {
  background:#121414 url("http://www.mylionking.com/fan/art/resources/images/world.png") no-repeat 7px 6px;
  }
ul#fanart-album-tabs li.favorites a, ul#fanart-tabs li.favorites a:link, ul#fanart-tabs li.favorites a:visited {
  background:transparent url("http://www.mylionking.com/fan/art/resources/images/favorite.png") no-repeat 7px 6px;
  }
ul#fanart-album-tabs li.favorites a.current {
  background:#121414 url("http://www.mylionking.com/fan/art/resources/images/favorite.png") no-repeat 7px 6px;
  }
ul#fanart-album-tabs li.stats a, ul#fanart-tabs li.stats a:link, ul#fanart-tabs li.stats a:visited {
  background:transparent url("http://www.mylionking.com/fan/art/resources/images/chart_bar.png") no-repeat 7px 6px;
