css:
@-moz-document domain("new.x.sankuai.com"), domain("x.sankuai.com") {
.text-input textarea {
    background-color: #363d4a;
}
.wrapper-right-chat .wrapper-right-outer .wrapper-right-background {
    background-color: #363d4a;
}
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-left {
    background: #363d4a;
    color: #c7c7c7;
}
.wrapper-group-members .group-members-title {
    color: rgba(255, 255, 255, 0.5);
}
.wrapper-group-notice .head {
    color: rgba(255, 255, 255, 0.38);
}
.wrapper-group-notice .no-content {
    color: rgb(105, 105, 105);
}
.comp-tab.wrapper-groupInfo-tab .tab-header .tab-list .item-on {
    color: rgba(255, 255, 255, 0.87);
}
.comp-tab .tab-header.underline .tab-list {
    color: rgba(255, 255, 255, 0.54);
}
.wrapper-setting-panel .wrapper-setting-info>.section>.section-title {
    color: rgba(255, 255, 255, 0.38);
}
.comp-dialog.wrapper-forward-start-chat .wrapper-selected .title,
.comp-fullscreen-dialog.start-chat-dialog .wrapper-selected .title {
    background-color: #363d4a;
}
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-right .wrapper-right-outer {
    background-color: #363d4a;
}
.wrapper-blankpage .comp-blank-page .desc {
    color: rgba(255, 255, 255, 0.24);
}
.comp-suggest .ctn-ipt>input {
    background-color: hsla(0, 2%, 18%, 0.5);
}
.comp-switch, .moderator, .group-name {
    color: #ccc;
}
/* Switch */
.comp-switch .switch-round {
    height: 18px;
}
.comp-switch .switch-round:before {
    height: 16px;
    width: 16px;
}
.comp-switch input:checked+.switch-round:before {
    left: -1px;
}

.titlebar {
    background-color: #363d4a;
    border-bottom-color: #333;
}
.wrapper-msglist {
	background-color: #363d4a;
    color: #c7c7c7;
}
.medium-size .wrapper-msglist {
    color: #333;
}
.wrapper-msglist .msglist .wrapper-ipt {
    background-color: #363d4a;
}
.wrapper-msglist .msglist .wrapper-msglist-inner {
    border-bottom-color: #333;
}
.wrapper-msglist-inner {
    background-color: #363d4a;
}
.wrapper-main .wrapper-navbar {
    border-right: 1px solid #333333;
}
.wrapper-msglist .slidebar {
    background-color: #363d4a;
}
.wrapper-right-chat .wrapper-right-outer .wrapper-right-background .wrapper-right-inner.wrapper-chat-inner.showslidebar .slidebar {
    border-left-color: #333;
}
.wrapper-group-notice .wrapper-notice-show .notice-show {
    color: rgba(255, 255, 255, 0.87);
}
.wrapper-group-notice .wrapper-edit-info {
    color: rgba(255, 255, 255, 0.47);
}
.titlebar .ctn-oper .item .dxicon.arrow-top:hover {
    color: auto;
}
.comp-checkbox .dxicon.dxicon-uncheck, .comp-checkbox.existed .dxicon {
    color: rgba(255, 255, 255, 0.24);
}
.message-text .quoted-text，
.message-text .quoted-msg-gray {
    color: rgba(255, 255, 255, 0.34);
}
.message-event .message-event-content {
    color: rgba(255, 255, 255, 0.38);
}
.bubble-personinfo .bubble-personInfo-static,
.bubble-personinfo .bubble-personinfo-info .bubble-personinfo-passport,
.bubble-personinfo .bubble-personinfo-info .bubble-personinfo-org {
    color: rgba(255, 255, 255, 0.38);
}
.message-text {
    background-color: #363d4a;
}
.wrapper-msglist-inner .bubble_list_message {
    padding-bottom: 10px;
}
.bubble-group .bubble-group-panel {
    border-bottom-color: #303744;
}
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-right .wrapper-right-outer {
    color: #dedede;
}
.titlebar .ctn-oper .item.item-group .arrow-top {
    color: #363d4a;
}
.wrapper-left-chat .wrapper-suggest {
    border-bottom-color: #333;
}
.merge-forward-panel .panel-status .count {
    color: rgba(255, 255, 255, 0.54);
}
#searchInput::-webkit-input-placeholder
{
    color: #ccc;
}
#textTextarea::-webkit-input-placeholder {
    color: #656565;
}
.extra-info hr {
    border-color: #333;
}
.group-members-list .member .name {
    color: #c7c7c7;
}
.bubble-group .bubble-group-panel .bubble-group-panel-title {
    color: #ccc;
}
.titlebar .title .title-menu-panel .title-menu:before {
    background-color: #363d4a; 
}
.titlebar .title .title-menu-panel .title-menu {
    background-color: #363d4a;
    border-color: #333;
}
.comp-session.selected::after {
	content: "";
	background: #118bfa;
	display: block;
	width: 3px;
	height: 70px;
	position: absolute;
	left: 0;
	top: 0;
}
.comp-session.top::after {
	border-color: rgba(17, 139, 250, 0.46);
    border-bottom-color: transparent;
    border-left-color: transparent;
}
.comp-session.top.selected::before {
	content: "";
	background: #118bfa;
	display: block;
	width: 3px;
	height: 70px;
	position: absolute;
	left: 0;
	top: 0;
}
.comp-session.top.selected::after {
    content: ' ';
    position: absolute;
    top: 0;
    right: 0;
    left: auto;
    width: 0;
    height: 0;
    border: 8px solid #118bfa;
    border-bottom-color: transparent;
    border-left-color: transparent;
    background-color: transparent;
}
.bubble-groupinfo .bubble-groupinfo-name {
    color: #868686;
}
.bubble-item.you .talker-name {
    color: #71A3D0;
}

/* Message bubble *
.medium-size .message-text {
    background-color: #6a716b;
    color: #fff;
}
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.left.lfc {
    color: #6a716b;
}
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.right.rfc {
    color: #118bfa;
}
.medium-size .me .message-text {
    background-color: #118bfa;
}
.message-text .quoted-text {
    color: #c5c5c5;
}
*/

/* Shadow */
.wrapper-ipt,
.titlebar,
.slidebar,
.cust-navbar,
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-left {
    box-shadow: 0px 0px 7px 1px rgba(0, 0, 0, 0.13);
}
.wrapper-left-chat .wrapper-suggest {
    box-shadow: -10px 0px 7px 1px rgba(0, 0, 0, 0.13);
}
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-left {
    z-index: 2;
}
.titlebar {
    z-index: 1;
}

.medium-size .text-input textarea {
	color: #c1c1c1;
}
.search-dialog .wrapper-msglist-inner {
    background: initial;
}
.bubble-groupinfo .bubble-groupinfo-managers .bubble-groupinfo-manager span {
    color: #999;
}
.bubble-group .bubble-group-panel.is-last-tab.show {
    border-bottom-width: 1px;
}

/* Search result */
.comp-suggest .ctn-ipt>input:focus {
    background-color: inherit;
}
.wrapper-left-chat .wrapper-suggest .suggest-session .tab-header {
    background-color: #363d4a;
}
.comp-suggest .ctn-result {
    color: #bdbdbd;
    background-color: #363d4a;
    border: none;
}

/* Old version entry */
.wrapper-main .oldversion-entry {
    top: -16px;
}
.wrapper-main .oldversion-entry img {
    width: 63px;
    height: auto;
}

/* Start dialog */
.comp-fullscreen-dialog {
    background: #363d4a;
}
.start-chat-dialog .content {
    color: #999;
}
.comp-fullscreen-dialog.start-chat-dialog .content .wrapper-content {
    background: #363d4a;
    border: 1px solid #333;
    box-shadow: 0 0 5px rgba(0,0,0,.15);
}
.comp-fullscreen-dialog>.header {
    background: #363d4a;
    border-bottom: 1px solid #333;
    box-shadow: 0 0 5px rgba(0,0,0,.15);
    color: #999;
}
.comp-fullscreen-dialog.start-chat-dialog .content .wrapper-content .comp-suggest .ctn-ipt input {
    border: none;
    border-radius: 4px;
    background-color: #242931;
}
.comp-fullscreen-dialog.start-chat-dialog .content .wrapper-content .start-chat-org-contact .tab-header {
    border-top-width: 0;
}
.comp-fullscreen-dialog.start-chat-dialog .content .wrapper-content .comp-suggest {
    background-color: #363d4a !important;
}

/* Segmented control */
.comp-tab .tab-header .tab-list {
    color: #929292;
}

/* Button */
.comp-btn {
	background-color: #485363;
    border: none;
    color: #999;
}
.comp-btn-primary {
    color: #fff;
    background-color: #118bfa;
}

/* Notify */
.cust-app-item .badget {
    padding: 0 4px;
}

/* Scroll bar */
#main {
    overflow-x: hidden;
}

/* Image Preview */
.comp-dialog.mask {
    background: transparent;
}
.comp-dialog.mask .comp-dialog-inner.show-image-dialog .content .wrapper-image img {
    vertical-align: middle;
}
.comp-dialog .comp-dialog-inner {
    min-height: auto;
}
.comp-dialog.mask .comp-dialog-inner.show-image-dialog .close {
    display: none !important;
}

/* Body */
body {
    background-color: #363d4a;
}

/* Sidebar */
.wrapper-right-chat .wrapper-right-outer .wrapper-right-background .wrapper-right-inner.wrapper-chat-inner.showslidebar .slidebar {
    z-index: 1;
}

/* TextView */
.wrapper-msglist .msglist .wrapper-ipt {
    height: 160px;
}
.text-input {
    height: 80px;
}

/* File Panel */
.wrapper-plugin.shown {
	filter: invert(87%);
}

/* Setting */
.wrapper-setting {
    filter: invert(87%);
}

/* Color override */
.wrapper-left-chat .wrapper-suggest .message,
.titlebar .title .title-menu-panel .title-menu-icon,
.titlebar .ctn-oper .item .dxicon,
.comp-session .time,
.comp-session .notice,
.bubble-group .bubble-group-panel .bubble-group-panel-title .bubble-group-panel-icon,
.titlebar .title .title-menu-panel .title-menu .title-menu-item .title-menu-item-icon{
    color: inherit;
}
.group-members-list .member-add .group-add-member-icon,
.message-send .dxicon,
.text-input .ctn-send-msg-btn>span,
.comp-session .desc,
.medium-size .wrapper-msglist-inner .time-line .time,
.message-menu,
.bubble-item .message-item .right-content .person .bubble-item-time{
    color: #7d7d7d;
}

/* Funny */
.cust-app-item .activebar,
.comp-session.top.selected::before,
.comp-session.selected::after {
    background-image: linear-gradient(-30deg,#fd27bb,#118bfa);
}
.comp-btn-primary {
    color: #fff;
    background-color: #118bfa;
    background-image: linear-gradient(-46deg,#fd27bb,#118bfa);
}
    
}
