css:
@-moz-document domain("workflowy.com") {


/* WORKFLOWY BACKGROUND IMAGES */ 
body {
  	transition: background-image 1s ease-in-out;
    background-image: url("https://www.dropbox.com/s/lo7sl5tvmu4pitp/Hot%20air%20baloon.jpg?dl=1");
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-size: cover;
}

/* DARK, OPAQUE PAGE CONTAINER */ 
div.page {
        background-color: rgba(0, 0, 0, 0.6);
    	border: none;
        border-radius: 3px;
    }

/* GLOBAL TEXT COLOR */ 
.content {
    color: #D8D8D8 !important;
}

/* LINK COLOR */
.content .contentLink {
    color: #0489B1;
}


}

style_setting_options.value:
/* USER OPTION - DEFAULT PAGE WIDTH */
.page {
        margin-bottom: 800px !important;
}

/* USER OPTION - WIDER PAGE */
.page {
        max-width: 100% !important;
        margin-top: 20px;                                            
        margin-right: 80px !important;
    	margin-left: 80px !important;
        padding: 20px 60px; 
        transform: none !important;
        width: auto !important;
        margin-bottom: 800px !important;
}
