css:
@-moz-document domain("apps.runescape.com") {
body {
    background: #F5F5F5;
    background: url(https://www.sanwebe.com/assets/public/images/background-patterns/bg63.gif);
}
a {
    color: white;
}
a:focus,
a:hover {
    color: #ffffff;
}
.title {
    border-bottom: 1px solid #4c4c4c;
}
.main-navigation__item {
    background-color: #38464F;
}
.main-navigation__item-link {
    border: none;
}
.main-navigation__item-link:hover {
    background-color: #47555f;
    border: none;
}
.main-navigation__item--active a:hover {
    background: #272e31;
}
.main-navigation__item {
    background-color: rgba(0, 0, 0, 0.5);
}
.main-navigation__item:first-child a:not(:hover) {
    border: none;
}
.content__wrapper,
.searched-user-bar {
    background-color: rgba(0, 0, 0, 0.5);
}
.skills-block {
    background-color: rgba(0, 0, 0, 0.5);
}
.activity-block {
    background-color: rgba(0, 0, 0, 0.5);
}
.clan-block {
    background-color: rgba(0, 0, 0, 0.5);
}
.main-navigation__item--active a {
    color: #ffffff;
    background-color: rgba(0, 0, 0, 0.75);
    border: none;
}
.main-navigation__item--active a:hover {
    border: none;
    background-color: rgba(0, 0, 0, 0.75);
}
.profile-block {
    background-color: rgba(0, 0, 0, 0.5);
}
.quests-block {
    background-color: rgba(0, 0, 0, 0.5);
}
.header-bar {
    background-color: rgba(0, 0, 0, 0.5);
}
input:not([type]),
input[type=password],
input[type=date],
input[type=datetime],
input[type=datetime-local],
input[type=month],
input[type=week],
input[type=email],
input[type=tel],
input[type=time],
input[type=url],
input[type=color],
input[type=number],
input[type=search],
input[type=text],
textarea {
    background-color: rgba(0, 0, 0, 0.5);
    border-color: #8c8c8c;
}
input:not([type]):focus,
input[type=password]:focus,
input[type=date]:focus,
input[type=datetime]:focus,
input[type=datetime-local]:focus,
input[type=month]:focus,
input[type=week]:focus,
input[type=email]:focus,
input[type=tel]:focus,
input[type=time]:focus,
input[type=url]:focus,
input[type=color]:focus,
input[type=number]:focus,
input[type=search]:focus,
input[type=text]:focus,
textarea:focus {
    border-color: #ababab;
    background-color: rgba(0, 0, 0, 0.5);
}
.search-small .search-small__submit {
    background: rgb(255, 255, 255);
}
.user-menu-toggle:hover {
    background: rgba(0, 0, 0, 0.35);
}
.button.disabled:focus,
.button.disabled:hover,
.button[disabled]:focus,
.button[disabled]:hover,
button.disabled:focus,
button.disabled:hover,
button[disabled]:focus,
button[disabled]:hover {
    color: #000;
    background-color: #8e8e8e;
}
.main-navigation__item--active a:after {
    border-left: 8px solid #ffffff;
}
.skills-block__title {
    text-align: center;
    border-bottom: 1px solid #8a8a8a;
}
.activities-block__title {
    border-bottom: 1px solid #8a8a8a;
}
.quests-block__title {
    text-align: center;
    border-bottom: 1px solid #8a8a8a;
}
.activity-block__title {
    text-align: center;
    border-bottom: 1px solid #8a8a8a;
}
.activity-block__list-item {
    border-bottom: 1px solid #8a8a8a;
}
.skills-block__stat-title {
    border-bottom: 1px solid #8a8a8a;
}
.skills-block__skills-container {
    border-bottom: 1px solid #8a8a8a;
}
.profile-block__name {
    border-bottom: 1px solid #8a8a8a;
}
.profile-block {
    border: 1px solid #8a8a8a;
}
.clan-block {
    border: 1px solid #8a8a8a;
}
.skills-block {
    border: 1px solid #8a8a8a;
}
.quests-block {
    border: 1px solid #8a8a8a;
}
.activity-block {
    border: 1px solid #8a8a8a;
}
.bar,
.bar--non-click {
    fill: white;
}
.levels-summary__block {
    background-color: rgba(0, 0, 0, 0.5);
    border: none;
}
.xp-levels-table {
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #131e24;
}
.base-tbl {
    border: 1px solid #000000;
}
table tr.alt,
table tr.even,
table tr:nth-of-type(even) {
    background: rgba(0, 0, 0, 0.75);
}
.xp-levels-table__bar-percentage {
    background: rgb(21, 24, 25);
    border: solid #122937 1px;
}
.xp-levels-table th {
    background: #000000;
}
.ng-table th.sortable.sort-asc,
.ng-table th.sortable.sort-desc {
    background: #151819;
}
.base-tbl th {
    background: #000000;
}
.activities-block {
    border: 1px solid #8a8a8a;
}
.ng-table th.sortable.sort-asc,
.ng-table th.sortable.sort-desc {
    background: #151819;
}
.base-tbl {
    background: rgba(0, 0, 0, 0.5);
}
.ng-table-pager .active a,
.ng-table-pager a:hover {
    background: #888888!important;
    border: 1px solid #888888;
}
.activities-block {
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #8a8a8a;
}
.page-summary-block {
    background-color: rgba(0, 0, 0, 0.5);
    border: none;
}
.xp-gains-summary-table th {
    background: #000000;
}
#a-xp-gain-graph-summary__row-overall {
    background-color: rgb(21, 24, 25);
}
.chosen-container .chosen-choices li.search-field input[type=text] {
    color: white;
}
.chosen-container .search-field {
    background: rgba(0, 0, 0, 0);
}
.chosen-container-active .chosen-choices {
    border: 1px solid #081013;
}
.chosen-container .chosen-drop {
    border: 1px solid #081013;
}
.chosen-container .chosen-results li.highlighted {
    background-color: rgba(0, 0, 0, 0.75);
    color: #fff;
}
.d3-tip__time {
    background: #000000;
    color: white;
}
/*section bellow removes the p2v stuff*/

#a-subscribe-banner {
    display: none;
}
.footer {
    display: none;
}
.main-navigation__item--xp-gain {
    display: none;
}
.main-navigation__item--kill-log {
    display: none;
}
.main-navigation__item--wealth {
    display: none;
}
.ng-table-pager {
    width: 300px;
    margin: 0 auto;
}
.main-navigation__list--upgrade {
    display: none;
}
}
