css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Match either a subdomain or a directory that contains the word owncloud */
@-moz-document regexp("(http|https):\/\/.*owncloud(\/|\.).*") {
    
/* Have a login page with a light gray gradient */
#body-login {
    background: rgba(0, 0, 0, 0) -moz-linear-gradient(center top , rgba(240, 240, 240, 0.9) 0px, #9c9c9c 1050px) no-repeat scroll 0 0;
}

/* Have a footer in orange-black style */    
#body-login footer {
    background: #ffc130 none repeat scroll 0 0;
    color: #171717;
}
#body-login footer p.info {
    color: #171717;
}
#body-login p.info a{
    color: #171717;
}
#body-login p.info a:hover, 
#body-login p.info a:focus {
    color: #fff;
}

/* Have a back menu-bar header*/
#body-user #header, 
#body-settings #header, 
#body-public #header {
    background: black;
}

 /* Have a light gray navigation menu */    
#app-navigation {
    background: rgba(0, 0, 0, 0) -moz-linear-gradient(center top , rgba(240, 240, 240, 0.9) 0px, #9c9c9c 1050px) no-repeat scroll 0 0;
}
.nav-trashbin {
    background: none;
}
.settings-button {
               background-color: rgba(0,0,0,0);
}
.settings-button,
.settings-button:hover{
    background-color: rgba(0,0,0,0);
    opacity: 1;
}
    

}
