css:
@-moz-document domain("youtube.com") {
.ytp-load-progress {
    background-color: rgba(200, 50, 50, 0.5);
}
.ytp-swatch-background-color {
    background-color: rgba(40, 150, 255, 1);
}
.ytp-time-current,
.ytp-time-separator,
.ytp-time-duration {
    color: rgb(255, 255, 255);
    text-shadow: 0px 0px 2px rgb(55, 150, 255);
    background-color:rgba(255,255,255,0);
    padding:0px;
    border-bottom:1px solid white;
}
#page{
    background-color:rgba(40,200,100,1);
    
}
#watch7-sidebar-ads{
    display:none;
    width:0px;
}
#yt-masthead-container{
    background-color:rgb(200,0,0);
}
#guide-container{
    background-image:linear-gradient(to left,rgba(200,0,0,1),rgba(150,0,0,1));
    color:white;
}
#guide-container span{
    color:white;
}
}
